import React, { Component } from "react";

export class ChildMessageRenderer extends Component {
  constructor(props) {
    super(props);
    this.state = {
      showMinusIco: false,
      showPlusIco: true,
      showModal: false,
    };
    this.invokeParentMethod = this.invokeParentMethod.bind(this);
  }
  invokeParentMethod() {}

  showMinusIco = () => {
    this.setState({
      showMinusIco: false,
      showPlusIco: true,
    });
  };
  showPlusIco = () => {
    this.setState({
      showMinusIco: true,
      showPlusIco: false,
    });
  };
  showGraphModal = () => {
    this.setState({
      showModal: true,
    });
  };
  showGraphdata = () => {
    this.props.data.getfun();
  };
  render() {
    return (
      <div>
        <p>
          {this.state.showPlusIco ? (
            <i
              className="fa fa-plus-circle action-icons"
              aria-hidden="true"
              onClick={this.showPlusIco}
            ></i>
          ) : null}

          {this.state.showMinusIco ? (
            <i
              class="fa fa-minus-circle action-icons"
              aria-hidden="true"
              onClick={this.showMinusIco}
            ></i>
          ) : null}
          <i
            class="fa fa-info-circle action-icons"
            aria-hidden="true"
            onClick={this.showGraphdata}
          ></i>
        </p>
      </div>
    );
  }
}

export default ChildMessageRenderer;
