import React, { Component } from 'react';
import Filters from '../../../services/filters';
import { InputGroup, FormControl } from 'react-bootstrap';
import '../StoreConfigContainers/StoreConfig';
import DataTable from '../../../services/reactTable';
import colDefs from '../StoreConfigContainers/StoreConfigColDefs';
import '../../Configuration/StoreConfigContainers/StoreConfig.css';
import StoreConfigRowData from '../StoreConfigContainers/StoreConfigRowData';
// import TableComponent from '../../../components/TableComponent/TableComponent';
// import TableContextProvider from '../../../components/TableComponent/context';
// import CreateStore from '../../../utils/columnDefs/CreateStore';
import { cloneDeep, differenceBy } from 'lodash';
import excelIcon from '../../../images/leftPanel/status.svg';

export class CreateStoreGroup extends Component {
  state = {
    storeGroupName: '',
    year: '',
    month: '',
    allStores: StoreConfigRowData,
    currentStores: [],
    selectedAllStores: [],
    selectedCurrentStores: [],
    showEDLP: false,
    showGeo: true,
  };

  onStoreGroupNameChangeHandler = (e) => {
    this.setState({
      storeGroupName: e.target.value,
    });
  };

  header = [
    {
      Header: 'Store Number',
      accessor: 'store_number',
    },
    {
      Header: 'Store Name',
      accessor: 'store_name',
    },
    {
      Header: 'Chain Description',
      accessor: 'chain_description',
    },
    {
      Header: 'Region Description',
      accessor: 'region_description',
    },
  ];
  onAllStoresSelectionChangeHandler = (rowData) => {
    console.log('adsflasdf', rowData);
    this.setState({
      selectedAllStores: rowData,
    });
  };

  onCurrentStoresSelectionChangeHandler = (rowData) => {
    this.setState({
      selectedCurrentStores: rowData,
    });
  };

  onAddClickHandler = () => {
    const allStores = cloneDeep(this.state.allStores);
    const currentStores = cloneDeep(this.state.currentStores);
    currentStores.push(...this.state.selectedAllStores);
    const newAllStores = differenceBy(allStores, this.state.selectedAllStores, 'store_number');
    console.log('asfasdf', this.state.selectedAllStores);
    console.log('adfadf', newAllStores);
    this.setState({
      allStores: newAllStores,
      currentStores,
      selectedAllStores: [],
    });
  };
  onRowClick = (rowInfo) => {
    console.log(rowInfo, 'rowinfooo');
    console.log(rowInfo.original, 'original');
  };
  onRemoveClickHandler = () => {
    const currentStores = cloneDeep(this.state.currentStores);
    const allStores = cloneDeep(this.state.allStores);
    const newCurrentStStores = differenceBy(
      currentStores,
      this.state.selectedCurrentStores,
      'store_number'
    );
    allStores.push(...this.state.selectedCurrentStores);
    this.setState({
      allStores,
      currentStores: newCurrentStStores,
      selectedCurrentStores: [],
    });
  };

  render() {
    let { currentStores, allStores } = this.state;
    return (
      <div className="card p-2">
        <div style={{ display: 'flex', justifyContent: 'space-between' }}>
          <h4 className="font-size-14  pad-10 markdown-heading">
            Store Configuration/
            <span className="font-bold color-blue">Create Store Group</span>
          </h4>
          <div>
            <button className="btn btn-primary" onClick={this.props.onCancelButtonHandler}>
              Back
            </button>
          </div>
        </div>
        <hr
          className="mb-3"
          style={{
            marginTop: '-4px',
          }}
        />
        <div className="row justify-content-between">
          <div className="col-md-6">
            <label className="mr-1">
              <span style={{ color: 'red' }}>*</span>Store group name:
            </label>
            <input
              type="text"
              value={this.state.storeGroupName}
              onChange={this.onStoreGroupNameChangeHandler}
            />
          </div>
          <div className="col-md-2">
            <button className="col-md-5 btn btn-primary" onClick={() => {}}>
              Upload &nbsp;&nbsp;
              <span>
                <img className="img-icons-create-promo" src={excelIcon} alt="" />
              </span>
            </button>
          </div>
        </div>
        <hr />
        <div className="row" style={{ justifyContent: 'space-around' }}>
          <div className="col-md-5">
            <div>All Stores</div>
            <div style={{ height: '370px' }}>
              <DataTable
                visibleColumns="onlyRowSelection"
                getRowProps={(rowInfo) => ({
                  onClick: () => this.onRowClick(rowInfo),
                })}
                columns={this.header}
                data={allStores}
              />
            </div>
            <div>
              <button className="btnStyle btn btn-primary" onClick={this.onAddClickHandler}>
                Add
              </button>
            </div>
          </div>
          <div className="col-md-5">
            <div>
              <span className="mr-2">Current Stores</span>
              {/* <span className="mr-2">Stores: 0</span> */}
            </div>
            <div style={{ height: '370px' }}>
              <DataTable
                visibleColumns="onlyRowSelection"
                getRowProps={(rowInfo) => ({
                  onClick: () => this.onRowClick(rowInfo),
                })}
                columns={this.header}
                data={currentStores}
              />
            </div>
            <div>
              <button className="btnStyle btn btn-primary" onClick={this.onRemoveClickHandler}>
                Remove
              </button>
            </div>
          </div>
        </div>
        <div className="mb-3 mt-4" style={{ textAlign: 'center' }}>
          <button
            style={{ width: '7%' }}
            className=" btn btn-primary"
            onClick={() => this.props.onCreateStoreGroup(this.state)}>
            Create
          </button>
          <button
            className=" btn ml-1 cancel-button"
            style={{ width: '7%' }}
            onClick={this.props.onCancelButtonHandler}>
            Cancel
          </button>
        </div>
      </div>
    );
  }
}

export default CreateStoreGroup;
