import React, { Component } from 'react';
import Filters from '../../../services/filters';
import { connect } from 'react-redux';
import { cloneDeep, differenceBy } from 'lodash';

import '../../Configuration/StoreConfigContainers/StoreConfig.css';
import StyleConfigFilter from '../StyleConfigFilter/StyleConfigFilter';
import excelIcon from '../../../images/leftPanel/status.svg';

export class CreateStyleGroup extends Component {
  state = {
    groupName: '',
    // allStyles: CreateStyleFamilySchema.data,
    currentStyles: [],
    selectedAllStyles: [],
    selectedCurrentStyles: [],
    showEDLP: true,
    showGeo: false,
  };

  onGroupNameChange = (e) => {
    this.setState({
      groupName: e.target.value,
    });
  };
  onFilter = () => {
    let req = {
      filters: {
        hierarchy1: this.props.selectedHierarchy1,
        hierarchy2: this.props.selectedHierarchy2,
        hierarchy3: this.props.selectedHierarchy3,
        hierarchy4: this.props.selectedHierarchy4,
      },
    };
    console.log(req, 'request');
  };
  onAddClickHandler = () => {
    const allStyles = cloneDeep(this.state.allStyles);
    const currentStyles = cloneDeep(this.state.currentStyles);
    currentStyles.push(...this.state.selectedAllStyles);
    const newAllStyles = differenceBy(allStyles, this.state.selectedAllStyles, 'style_id');
    console.log('asdflkasmdfa', newAllStyles, this.state.allStyles, this.state.selectedAllStyles);

    this.setState({
      allStyles: newAllStyles,
      currentStyles,
      selectedAllStyles: [],
    });
  };

  onRemoveClickHandler = () => {
    const currentStyles = cloneDeep(this.state.currentStyles);
    const allStyles = cloneDeep(this.state.allStyles);
    const newCurrentStyles = differenceBy(
      currentStyles,
      this.state.selectedCurrentStyles,
      'style_id'
    );
    allStyles.push(...this.state.selectedCurrentStyles);
    this.setState({
      allStyles,
      currentStyles: newCurrentStyles,
      selectedCurrentStyles: [],
    });
  };

  onAllStyleSelectionChangeHandler = (rowData) => {
    console.log('asdfmasdf', rowData);
    this.setState({
      selectedAllStyles: rowData,
    });
  };

  onCurrentStylesSelectionChangeHandler = (rowData) => {
    console.log('adfsasdf', rowData);
    this.setState({
      selectedCurrentStyles: rowData,
    });
  };

  render() {
    return (
      <div className="card p-2">
        <div style={{ display: 'flex', justifyContent: 'space-between' }}>
          <h4 className="font-size-14  pad-10 markdown-heading">
            Store Configuration/
            <span className="font-bold color-blue">
              {this.props.isEditStyle ? 'Edit Style' : 'Create Style'} Group{' '}
            </span>
          </h4>

          <div>
            <button className="btn btn-primary" onClick={this.props.onCancelButtonHandler}>
              Back
            </button>
          </div>
        </div>
        <hr
          className="mb-2"
          style={{
            marginTop: '-4px',
          }}
        />

        <div className="row justify-content-between">
          <div className="col-md-6">
            <label className="mr-1">
              <span style={{ color: 'red' }}>*</span>Style group name:
            </label>
            <input
              className="inputText"
              type="text"
              value={this.state.familyName}
              onChange={this.onFamilyNameChange}
            />
          </div>
          <div className="col-md-2 text-right">
            {/* <button type="button" className="btn btn-primary" onClick={() => {}}>
              Upload &nbsp;&nbsp;
              <span>
                <img className="img-icons-create-promo" src={excelIcon} alt="" />
              </span>
            </button> */}
          </div>
        </div>
        <hr />
        <Filters
          hideStoreClusterFilter
          hierarchy={['hierarchy1', 'hierarchy2', 'hierarchy3', 'hierarchy4']}
          onFilter={this.onFilter}
        />
        <hr />
        <div className="row mx-2 create-store-grp-table">
          <div className="col-md-6">
            All styles
            <div style={{ height: '439px' }}>{/* <DataTable /> */}</div>
            <div>
              <button className="btnStyle btn btn-primary" onClick={this.onAddClickHandler}>
                Add
              </button>
            </div>
          </div>
          <div className="col-md-6">
            <div>
              <span className="mr-2">Current</span>
              <span>No of styles: 23</span>
            </div>
            <div style={{ height: '414px' }}>{/* <DataTable /> */}</div>
            <div className="mt-4">
              <button className="btnStyle btn btn-primary" onClick={this.onRemoveClickHandler}>
                Remove
              </button>
            </div>
          </div>
        </div>
        <div className="mb-3 mt-4" style={{ textAlign: 'center' }}>
          <button
            style={{ width: '7%', fontSize: '15px' }}
            className="btn btn-primary"
            onClick={() => this.props.onCreateStyleHandler(this.state)}>
            Create
          </button>
          <button
            style={{ width: '7%', fontSize: '15px' }}
            className="btn ml-1 cancel-button"
            onClick={this.props.onCancelButtonHandler}>
            Cancel
          </button>
        </div>
      </div>
    );
  }
}

const mapStateToProps = (store) => {
  return {
    selectedHierarchy1: store.filtersReducer.selectedHierarchy1,
    selectedHierarchy2: store.filtersReducer.selectedHierarchy2,
    selectedHierarchy3: store.filtersReducer.selectedHierarchy3,
    selectedHierarchy4: store.filtersReducer.selectedHierarchy4,
  };
};
const mapActionsToProps = {};
export default connect(mapStateToProps, mapActionsToProps)(CreateStyleGroup);
