import React, { Component } from 'react';
import Filters from '../../services/filters';
import Breadcrumbs from '../../services/breadcrumbs';
import SkuCockpit from '../../components/SKUCockpit/SKUCockpit';
import LineChart from '../support/Charts/LineChart';
import BarChart from '../support/Charts/Barchart';
import { connect } from 'react-redux';
import * as chartjs from 'chart.js';
import _ from 'lodash';
import './index.css';
import reportingActions from '../../actions/reportingActions';
import LoadingOverlay from '../../utils/Loader';
import moment from 'moment';
import {
  decimalFormatter,
  toUnit,
  formatThousands,
  toDollar,
  toPercentage,
} from '../../helper/formatter';
import { toast } from 'react-toastify';
import filterActions from '../../actions/filtersAction';
import ColumnSelection from '../../components/columnSelection';
import styled from 'styled-components';
chartjs.pluginService.register({
  beforeDraw: function (chart) {
    if (chart.config.options && chart.config.options.backgroundColor) {
      var ctx = chart.chart.ctx;
      var chartArea = chart.chartArea;
      ctx.save();
      ctx.fillStyle = chart.config.options.backgroundColor;
      ctx.fillRect(
        chartArea.left,
        chartArea.top,
        chartArea.right - chartArea.left,
        chartArea.bottom - chartArea.top
      );
      ctx.restore();
    }
  },
});
class MarkdownReporting extends Component {
  constructor() {
    super();
    this.state = {
      columnOptions: [],
      selLabels: [],
      selectedItems: [],
    };
  }
  sellThroughOptions = {
    backgroundColor: '#fff',
    tooltips: {
      callbacks: {
        label: function (tooltipItem) {
          return decimalFormatter({ value: tooltipItem.value });
        },
      },
    },
    scales: {
      xAxes: [
        {
          scaleLabel: {
            display: true,
            labelString: 'Period',
          },
        },
      ],
      yAxes: [
        {
          scaleLabel: {
            display: true,
            labelString: 'Sell through %',
          },
          ticks: {
            suggestedMin: 0, // minimum will be 0, unless there is a lower value.
          },
        },
      ],
    },
  };
  inventoryOptions = {
    backgroundColor: '#fff',
    tooltips: {
      callbacks: {
        label: function (tooltipItem) {
          return toUnit({ value: tooltipItem.value });
        },
      },
    },
    scales: {
      xAxes: [
        {
          scaleLabel: {
            display: true,
            labelString: 'Period',
          },
        },
      ],
      yAxes: [
        {
          scaleLabel: {
            display: true,
            labelString: 'Units',
          },
          ticks: {
            suggestedMin: 0, // minimum will be 0, unless there is a lower value.
            callback: (value) => {
              return formatThousands(value);
            },
          },
        },
      ],
    },
  };
  marginOptions = {
    backgroundColor: '#fff',
    tooltips: {
      callbacks: {
        label: function (tooltipItem) {
          return decimalFormatter({ value: tooltipItem.value });
        },
      },
    },
    scales: {
      xAxes: [
        {
          scaleLabel: {
            display: true,
            labelString: 'Period',
          },
        },
      ],
      yAxes: [
        {
          scaleLabel: {
            display: true,
            labelString: 'Margin %',
          },
          ticks: {
            suggestedMin: 0, // minimum will be 0, unless there is a lower value.
          },
        },
      ],
    },
  };
  salesComparisonOptions = {
    legend: { display: false },
    backgroundColor: '#fff',
    tooltips: {
      callbacks: {
        label: function (tooltipItem) {
          return toUnit({ value: tooltipItem.value });
        },
      },
    },
    scales: {
      xAxes: [
        {
          scaleLabel: {
            display: true,
            labelString: 'Period',
          },
        },
      ],
      yAxes: [
        {
          scaleLabel: {
            display: true,
            labelString: 'Units',
          },
          ticks: {
            suggestedMin: 0, // minimum will be 0, unless there is a lower value.
            beginAtZero: true, // minimum value will be 0.
            callback: (value) => {
              return formatThousands(value);
            },
          },
        },
      ],
    },
  };
  header = [
    {
      Header: 'Style-Color',
      columns: [
        {
          Header: '',
          downloadHeader: 'Style-Color',
          accessor: 'style_number',
          groupByID: 'style_number',
          disableGroupBy: true,
          align: 'left',
          showFilter: true,
        },
      ],
    },
    {
      Header: 'Sell through %',
      columns: [
        {
          Header: 'TY',
          downloadHeader: 'Sell through TY %',
          downloadFormatter: toPercentage,
          accessor: 'sell_through_ty',
          aggregate: 'sum',
          disableGroupBy: true,
          align: 'left',
          Cell: (props) => <div> {toPercentage({ value: props.value })} </div>,
        },
        {
          Header: 'LY',
          downloadHeader: 'Sell through LY %',
          downloadFormatter: toPercentage,
          accessor: 'sell_through_ly',
          disableGroupBy: true,
          align: 'left',
          Cell: (props) => <div> {toPercentage({ value: props.value })} </div>,
        },
      ],
    },
    {
      Header: 'Margin %',
      columns: [
        {
          Header: 'TY',
          downloadHeader: 'Margin TY %',
          downloadFormatter: toPercentage,
          accessor: 'margin_ty',
          disableGroupBy: true,
          aggregate: this.roundedMedian,
          Aggregated: ({ value }) => `${value}`,
          align: 'left',
          Cell: (props) => <div> {toPercentage({ value: props.value })} </div>,
        },
        {
          Header: 'LY',
          downloadHeader: 'Margin LY %',
          downloadFormatter: toPercentage,
          accessor: 'margin_ly',
          disableGroupBy: true,
          align: 'left',
          Cell: (props) => <div> {toPercentage({ value: props.value })} </div>,
        },
      ],
    },
    {
      Header: 'Margin ',
      columns: [
        {
          Header: 'Margin ($)',
          accessor: 'margin$',
          disableGroupBy: true,
          Aggregated: ({ value }) => `${value}`,
          align: 'centre',
          Cell: (props) => <div> {toDollar(props)} </div>,
          downloadFormatter: toDollar,
        },

        {
          Header: 'AUM $',
          accessor: 'aum',
          disableGroupBy: true,
          Aggregated: ({ value }) => `${value}`,
          align: 'centre',
          Cell: (props) => <div> {toDollar(props)} </div>,
          downloadFormatter: toDollar,
        },
      ],
    },
    {
      Header: 'Sales',
      columns: [
        {
          Header: 'Actual sales (units)',
          accessor: 'units_sold',
          aggregate: 'average',
          disableGroupBy: true,
          Aggregated: ({ value }) => `${value}`,
          align: 'centre',
          Cell: (props) => <div> {toUnit(props)} </div>,
          downloadFormatter: toUnit,
        },
        {
          Header: 'Revenue ($)',
          accessor: 'revenue$',
          disableGroupBy: true,
          Aggregated: ({ value }) => `${value}`,
          align: 'centre',
          Cell: (props) => <div> {toDollar(props)} </div>,
          downloadFormatter: toDollar,
        },
        {
          Header: 'AUR $',
          accessor: 'aur',
          disableGroupBy: true,
          Aggregated: ({ value }) => `${value}`,
          align: 'centre',
          Cell: (props) => <div> {toDollar(props)} </div>,
          downloadFormatter: toDollar,
        },
        {
          Header: "Last week's sales",
          accessor: 'last_week_sales',
          disableGroupBy: true,
          Aggregated: ({ value }) => `${value}`,
          align: 'centre',
        },
      ],
    },

    {
      Header: 'Current inventory',
      columns: [
        {
          Header: 'On hand',
          accessor: 'inventory.inventory_on_hand',
          aggregate: 'average',
          disableGroupBy: true,
          align: 'left',
          Cell: (props) => <div> {toUnit(props)} </div>,
          downloadFormatter: toUnit,
        },
        {
          Header: 'In transit',
          accessor: 'inventory.inventory_in_transit',
          aggregate: 'average',
          disableGroupBy: true,
          align: 'left',
          Cell: (props) => <div> {toUnit(props)} </div>,
          downloadFormatter: toUnit,
        },
        {
          Header: 'On order',
          accessor: 'inventory.inventory_on_order',
          aggregate: 'average',
          disableGroupBy: true,
          align: 'left',
          Cell: (props) => <div> {toUnit(props)} </div>,
          downloadFormatter: toUnit,
        },
        {
          Header: 'In DC',
          accessor: 'inventory.inventory_in_dc',
          aggregate: 'average',
          disableGroupBy: true,
          align: 'left',
          Cell: (props) => <div> {toUnit(props)} </div>,
          downloadFormatter: toUnit,
        },
        {
          Header: 'Weeks on hand',
          accessor: 'wos',
          disableGroupBy: true,
          Aggregated: ({ value }) => `${value}`,
          align: 'centre',
          Cell: (props) => <div> {toUnit(props)} </div>,
          downloadFormatter: toUnit,
        },
      ],
    },
    {
      Header: 'Markdown status',
      columns: [
        {
          Header: '',
          downloadHeader: 'Markdown status',
          accessor: 'mdStatus',
          disableGroupBy: true,
          align: 'centre',
          showFilter: true,
          Cell: (props) => {
            switch (props.row.original.mdStatus) {
              case 'High':
                return <div className="high-status">High</div>;
              case 'Medium':
                return <div className="medium-status">Medium</div>;
              case 'Low':
                return <div className="low-status">Low</div>;
              default:
                return <div></div>;
            }
          },
        },
      ],
    },
  ];

  mandatoryFiltersVal = () => {
    return (
      this.props.selectedHierarchy1 &&
      this.props.selectedHierarchy2.length &&
      this.props.selectedHierarchy3.length &&
      this.props.selectedHierarchy4.length &&
      this.props.selectedHierarchy5.length &&
      this.props.selectedStores.length &&
      this.props.startDate &&
      this.props.endDate
    );
  };
  onFilter = () => {
    if (!this.mandatoryFiltersVal()) {
      toast.error(`Please select mandatory filters`, {
        autoClose: 15000,
        hideProgressBar: true,
      });
      return;
    }
    this.getReportingData('week_level_aggregated_data');
  };

  onCreateMarkdown = async (items) => {
    await this.props.setRetainFilters(true);
    this.props.history.push({
      pathname: '/vis/Markdown/create-markdown',
      state: { styles: items },
    });
  };
  getReportingData = (reportingType) => {
    let req = {
      filters: {
        hierarchy1: this.props.selectedHierarchy1,
        hierarchy2: this.props.selectedHierarchy2,
        hierarchy3: this.props.selectedHierarchy3,
        hierarchy4: this.props.selectedHierarchy4,
        hierarchy5: this.props.selectedHierarchy5,
        hierarchy6: this.props.selectedHierarchy6,
        hierarchy7: this.props.selectedHierarchy7,
        hierarchy8: this.props.selectedHierarchy8,
        tiers: this.props.selectedStores,
      },
      startDate: moment(this.props.startDate).format('YYYY-MM-DD'),
      endDate: moment(this.props.endDate).format('YYYY-MM-DD'),
      week: this.props.weeks,
      reportingType,
    };
    this.props.getReportingData(req).then((resp) => {
      if (resp.columnHeaders && Object.keys(resp.columnHeaders).length) {
        let defaultHeader = Object.keys(resp.columnHeaders.default);

        let setDefaultColDef = this.header;
        let setDefaultLabel = [];
        let setDefaultColumnOptions = [];
        let setDefaultColumnOptions1 = [];
        let setDefaultItems = [];
        let colHeaders = _.cloneDeep(resp.columnHeaders);
        let labels = Object.keys(colHeaders);
        labels.splice(labels.indexOf('fixed'), 1);
        labels.splice(labels.indexOf('default'), 1);
        for (const label of labels) {
          let labelArray = Object.keys(resp.columnHeaders[label]);
          function diff(A = labelArray, B = defaultHeader) {
            return A.filter(function (a) {
              return B.indexOf(a) !== -1;
            });
          }

          if (diff().length) {
            setDefaultColumnOptions.push(...Object.keys(resp.columnHeaders[label]));
            setDefaultColumnOptions1.push(resp.columnHeaders[label]);
            setDefaultItems.push(...diff());
            setDefaultLabel.push(label);
          }
        }
        let options = setDefaultColumnOptions.map((label) => {
          let find = setDefaultColumnOptions1 && setDefaultColumnOptions1[0][label];
          return {
            label: label,
            value: find,
          };
        });
        let selectedItems = setDefaultItems.map((label) => {
          let find = setDefaultColumnOptions1 && setDefaultColumnOptions1[0][label];
          return {
            label: label,
            value: find,
          };
        });
        this.setState({
          columns: setDefaultColDef,
          selLabels: setDefaultLabel,
          selectedItems: selectedItems,
          columnOptions: options,
        });
      }
    });
  };
  componentWillUnmount() {
    this.props.resetReportingData();
  }
  onHandleCheckBox = (e, label, index) => {
    //adding
    if (e.target.checked) {
      if (this.state.selLabels.indexOf(label) === -1) {
        let labels = [...this.state.selLabels, label];
        let tempArr = [];
        labels.forEach((label) => {
          let columns = this.props.columnHeaders[label];
          let labelColumns = Object.keys(columns);
          let options = labelColumns.map((label) => {
            return {
              label: label,
              value: columns[label],
            };
          });
          tempArr.push(...options);
        });
        this.setState({
          selLabels: labels,
          columnOptions: tempArr,
        });
      }
    } else {
      //removing
      let index = this.state.selLabels.indexOf(label);
      if (index !== -1) {
        let selectedLabel = _.cloneDeep(this.state.selLabels);
        selectedLabel.splice(index, 1);
        let tempArr = [];
        selectedLabel.forEach((label) => {
          let columns = this.props.columnHeaders[label];
          let labelColumns = Object.keys(columns);
          let options = labelColumns.map((label) => {
            return {
              label: label,
              value: columns[label],
            };
          });
          tempArr.push(...options);
        });

        let selectedItemsClone = _.cloneDeep(this.state.selectedItems);
        let newSelectedItems = [];
        let labelArr = Object.keys(this.props.columnHeaders[label]);
        selectedItemsClone.forEach((item) => {
          if (!labelArr.includes(item.label)) {
            newSelectedItems.push(item);
          }
        });

        this.onHandleDropdownSelect(newSelectedItems);
        this.setState({
          selLabels: selectedLabel,
          columnOptions: tempArr,
          selectedItems: newSelectedItems,
        });
      }
    }
  };
  onHandleDropdownSelect = (options) => {};
  render() {
    console.log(this.props.columnHeaders, 'cococococo');
    return (
      <React.Fragment>
        <LoadingOverlay loader={this.props.overlayLoaderState} spinner>
          <div className="card">
            <div>
              <h4 className="font-size-14  pad-10 markdown-heading">
                Markdown Optimization /
                <span className="font-bold color-blue">Reporting Section</span>
              </h4>
            </div>
            <hr
              style={{
                marginTop: '-4px',
              }}
            />
            <Filters
              drop
              stores
              dateRange
              hierarchy={[
                {
                  type: 'hierarchy1',
                  isMandatory: true,
                  isMultiple: false,
                },
                {
                  type: 'hierarchy2',
                  isMandatory: true,
                  isMultiple: true,
                },
                {
                  type: 'hierarchy3',
                  isMandatory: true,
                  isMultiple: true,
                },
                {
                  type: 'hierarchy4',
                  isMandatory: true,
                  isMultiple: true,
                },
                {
                  type: 'hierarchy5',
                  isMandatory: true,
                  isMultiple: true,
                },
                {
                  type: 'hierarchy6',
                  isMandatory: false,
                  isMultiple: true,
                },
                {
                  type: 'hierarchy7',
                  isMandatory: false,
                  isMultiple: true,
                },
                {
                  type: 'hierarchy8',
                  isMandatory: false,
                  isMultiple: true,
                },
              ]}
              onFilter={this.onFilter}
            />
            <hr className="hr-style mt-4 mb-4" />
            {this.props.reportingData && this.props.reportingData.message ? (
              <div className="no-data">{this.props.reportingData.message}</div>
            ) : (
              <React.Fragment>
                {!!Object.keys(this.props.reportingData).length ? (
                  <div className="row justify-content-center">
                    <div className="card col-md-5 ml-4 mb-4 mr-4 reporting-chart-container">
                      Sell through
                      <div>
                        <LineChart
                          data={this.props.reportingData.sellThroughData}
                          options={this.sellThroughOptions}></LineChart>
                      </div>
                    </div>
                    <div className="card col-md-5 ml-4 mb-4 reporting-chart-container">
                      Inventory
                      <div>
                        <LineChart
                          data={this.props.reportingData.inventoryData}
                          options={this.inventoryOptions}></LineChart>
                      </div>
                    </div>
                    <div className="card col-md-5 ml-4 mb-4 mr-4 reporting-chart-container">
                      Margin
                      <div>
                        <LineChart
                          data={this.props.reportingData.marginData}
                          options={this.marginOptions}></LineChart>
                      </div>
                    </div>
                    <div className="card col-md-5 ml-4 mb-4 reporting-chart-container">
                      Sales comparison
                      <div>
                        <BarChart
                          data={this.props.reportingData.salesData}
                          options={this.salesComparisonOptions}></BarChart>
                      </div>
                    </div>
                  </div>
                ) : (
                  <div className="no-data">Please select filters</div>
                )}
              </React.Fragment>
            )}
          </div>
          {!!Object.keys(this.props.reportingData).length && !this.props.reportingData.message && (
            <div className="show-detail-button">
              <button
                className="btn btn-primary mt-3"
                onClick={() => this.getReportingData('style_level_detailed_data')}>
                Show Style-Color level data
              </button>
            </div>
          )}

          {!!this.props.cockpitData.length && (
            <div className="card mt-4">
              <ColSelBtn>
                <ColumnSelection
                  onHandleCheckBox={this.onHandleCheckBox}
                  columnHeaders={this.props.columnHeaders}
                  onHandleSelect={this.onHandleDropdownSelect}
                  columnOptions={this.state.columnOptions}
                  selectedItems={this.state.selectedItems}
                  selLabels={this.state.selLabels}
                />
              </ColSelBtn>
              <SkuCockpit
                header={this.props.header}
                cockpitData={this.props.cockpitData}
                onCreateMarkdown={this.onCreateMarkdown}
              />
            </div>
          )}
        </LoadingOverlay>
      </React.Fragment>
    );
  }
}
const mapStateToProps = (store) => {
  return {
    overlayLoaderState: store.loaderReducer.overlayLoaderState,
    reportingData: store.reportingReducer.reportingData,
    cockpitData: store.reportingReducer.cockpitData,
    columnHeaders: store.reportingReducer.columnHeaders,
