import React, { Component } from "react";
import { connect } from "react-redux";
import { withRouter } from "react-router-dom";
import Select from "react-select";
import { DateRangePicker } from "react-dates";
import "react-datepicker/dist/react-datepicker.css";
import AlertDanger from "../../utils/AlertMessage/alertDanger";
import moment from "moment";
import {
  getPricingFilter,
  getPricingFactorData,
  getCompetitorgData,
  getGenerateRecommendationData,
  getRegionData,
  getStoreData
} from "../Actions/PricingAction";
export class RadioGenerateRecommendation extends Component {
  constructor(props) {
    super(props);
    this.state = {
      selectedRadio: "overall",
      overallFlag: true,
      regionFlag: false,
      storeFlag: false,
      selectedStore: { label: "Group1", value: "1" },
      selectedRegionLabel: "East",
      selectedRegion: { label: "west", value: "4" },
      startDate: null,
      endDate: null,
      focusedInput: null,
      overAllValue: null,
      selectedRegionValue: null,
      selectedStoreValue: null,
      startDateModified: null,
      endDateModified: null,
      showAlertDanger: false,
      showPriceValues: false
    };
  }
  componentDidMount() {
    this.props.getRegionData();
    this.props.getStoreData();
  }

  onDatesChange = (startDate, endDate) => {
    this.setState(startDate, endDate);
    let startDateFormat = moment(this.state.startDate).format("MM-DD-YYYY"); //JSON.stringify(this.state.startDate);
    let resultStartDate = startDateFormat.slice(1, 11);
    let endDateFormat = moment(startDate.endDate).format("MM-DD-YYYY"); //JSON.stringify(startDate.endDate);
    let resultEndDate = endDateFormat.slice(1, 11);
    this.setState({
      startDateModified: startDateFormat,
      endDateModified: endDateFormat
    });
    this.props.CallbackForDate(startDateFormat, endDateFormat);
  };
  setSelectedValue = val => {
    switch (val) {
      case "overall":
        this.setState({
          selectedRadio: "overall",
          overallFlag: true,
          regionFlag: false,
          storeFlag: false
        });
        // this.props.getGenerateRecommendationData({});
        break;
      case "region":
        this.setState({
          selectedRadio: "region",
          overallFlag: false,
          regionFlag: true,
          storeFlag: false
          //selectedRegion: 3
        });
        // this.props.getGenerateRecommendationData({});
        break;
      case "store":
        this.setState({
          selectedRadio: "store",
          overallFlag: false,
          regionFlag: false,
          storeFlag: true
          //selectedStore: 2
        });
        //this.props.getGenerateRecommendationData({});

        break;
      default:
        this.setState({
          overallFlag: false,
          regionFlag: false,
          storeFlag: false
        });
    }
  };
  onDropdownchange = (tag, value) => {
    if (tag === "overall") {
      this.setState({
        overAllValue: value
      });
    }
    if (tag === "region") {
      console.log("selected region vals", value);
      this.setState({
        selectedRegion: value,
        selectedRegionValue: value.value
        //valueSelected:
      });
      this.props.CheckDateRange(
        this.state.startDateModified,
        this.state.endDateModified
      );
    }
    if (tag === "store") {
      this.setState({
        selectedStore: value,
        selectedStoreValue: value.value
      });
    }
  };
  onsubmitClick = () => {
    this.setState({
      showPriceValues: true
    });
    this.props.showGraphsCallback(true);

    if (this.state.selectedRadio === "region") {
      this.props.getGenerateRecommendationData({
        start_date: this.state.startDateModified,
        end_date: this.state.endDateModified,
        category_id: this.props.CategoryId,
        subcategory_id: this.props.SubCategoryId,
        class_id: this.props.ClassId,
        subclass_id: this.props.SubClassId,
        sku_id: this.props.SkuId,
        brand_id: this.props.BrandId,
        region_id: this.state.selectedRegionValue,
        store_group_id: null
      });
    } else {
      this.props.getGenerateRecommendationData({
        start_date: this.state.startDateModified,
        end_date: this.state.endDateModified,
        category_id: this.props.CategoryId,
        subcategory_id: this.props.SubCategoryId,
        class_id: this.props.ClassId,
        subclass_id: this.props.SubClassId,
        sku_id: this.props.SkuId,
        brand_id: this.props.BrandId,
        region_id: null,
        store_group_id: this.state.selectedStoreValue
      });
    }
  };
  render() {

    var storeDataArray = [],
      regionDataArray = [];
    let recomendedPrice = (
      <div className="col-md-4">
        <p className="font-size-13">
          Recommended price change:{" "}
          {this.state.showPriceValues ? (
            <span className="font-bold">
              {this.props.totalUnits[0]}%
              {this.props.totalUnits[0] > 0 ? (
                <i class="fa fa-caret-up upIconC" aria-hidden="true"></i>
              ) : (
                <i class="fa fa-caret-down caretRed" aria-hidden="true"></i>
              )}
            </span>
          ) : null}
        </p>
      </div>
    );
    let currentPrice = (
      <div className="col-md-4">
        <p className="font-size-13">
          Current price/Current average price:{" "}
          {this.state.showPriceValues ? (
            <span className="font-bold">${this.props.totalUnits[1]}</span>
          ) : null}
        </p>
      </div>
    );

    if (this.props.storeData.data && this.props.regionData.data) {
      this.props.storeData.data.map(items => {
        storeDataArray.push({
          label: items.store_group_desc,
          value: items.store_group_id
        });
      });
      this.props.regionData.data.map(items => {
        regionDataArray.push({
          label: items.region_desc,
          value: items.region_id
        });
      });
    }
    console.log("getStoreData", storeDataArray, regionDataArray);

    return (
      <div className="mt-10 mb cntnt-padding">
        <div className="alertDivs">
          {this.state.showAlertDanger ? (
            <AlertDanger
              message="Please Select Date Range!"
              show={this.state.showAlertDanger}
            />
          ) : null}
        </div>
        <div className="row">
          <div className="col-md-5">
            <div className="inputDivs">
              <input
                type="radio"
                className="inverse-radio mr-2 ml-4"
                name="o1"
                onChange={() => this.setSelectedValue("overall")}
                checked={this.state.selectedRadio === "overall"}
              />
              <span className="color-blue"> overall</span>
              <input
                type="radio"
                className="inverse-radio mr-2 ml-4"
                name="r1"
                onChange={() => this.setSelectedValue("region")}
                checked={this.state.selectedRadio === "region"}
              />
              <span className="color-blue">Region</span>
              <input
                type="radio"
                className="inverse-radio mr-2 ml-4"
                name="s1"
                onChange={() => this.setSelectedValue("store")}
                checked={this.state.selectedRadio === "store"}
              />
              <span className="color-blue"> Store Level/Store Group Level</span>
            </div>
          </div>
          <div className="col-md-4 row">
            <div className="col-md-4">
              {" "}
              <p className="color-blue font-size-13">
                Date Range{" "}
                <span className="font-size-14" style={{ color: "red" }}>
                  *
                </span>
              </p>
            </div>
            <div className="col-md-7">
              {" "}
              <DateRangePicker
                startDate={this.state.startDate}
                startDateId="price_period_start_date"
                endDate={this.state.endDate}
                endDateId="price_period_end_date"
                onDatesChange={({ startDate, endDate }) =>
                  this.onDatesChange({ startDate, endDate })
                }
                focusedInput={this.state.focusedInput}
                onFocusChange={focusedInput => this.setState({ focusedInput })}
              />
            </div>
          </div>
          <div className="col-md-2">
            <button
              className="btn btn-primary"
              onClick={this.onsubmitClick}
              disabled={this.state.startDateModified ? false : true}
            >
              Submit
            </button>
          </div>
        </div>
        <div className="price-rev-div">
          <div className="select-generation-filter">
            {this.state.overallFlag ? (
              <div className="row">
                <div className="col-md-3">
                  <Select
                    id="o1"
                    placeholder="Overall"
                    isDisabled={true}
                    id="Overall"
                  />
                </div>
                {recomendedPrice}
                {currentPrice}
              </div>
            ) : null}
            {this.state.regionFlag ? (
              <div className="row">
                <div className="col-md-3">
                  <Select
                    id="region"
                    defaultValue={this.state.selectedRegion}
                    // placeholder={this.state.selectedRegionLabel}
                    options={regionDataArray}
                    value={this.state.selectedRegion}
                    onChange={e => this.onDropdownchange("region", e)}
                  />
                </div>
                {recomendedPrice}
                {currentPrice}
              </div>
            ) : null}
            {this.state.storeFlag ? (
              <div className="row">
                <div className="col-md-3">
                  <Select
                    id="store"
                    placeholder="Select Store"
                    options={storeDataArray}
                    value={this.state.selectedStore}
                    onChange={e => this.onDropdownchange("store", e)}
                  />
                </div>
                {recomendedPrice}
                {currentPrice}
              </div>
            ) : null}
          </div>
        </div>
      </div>
    );
  }
}

const mapStateToProps = store => {
  return {
    getAllFilterProp: store.markDownFilter.getAllFilter,
    priceFilterData: store.priceReducer.priceFilterData,
    factorData: store.priceReducer.factorData,
    copetitorRevisionData: store.priceReducer.copetitorRevisionData,
    genarateRecommendationData: store.priceReducer.genarateRecommendationData,
    storeData: store.priceReducer.getStoreData,
    regionData: store.priceReducer.getRegionData
  };
};

const mapDispatchToProps = dispatch => {
  return {
    getPricingFilter: payload => dispatch(getPricingFilter(payload)),
    getPricingFactorData: payload => dispatch(getPricingFactorData(payload)),
    getCompetitorgData: payload => dispatch(getCompetitorgData(payload)),
    getGenerateRecommendationData: payload =>
      dispatch(getGenerateRecommendationData(payload)),
    getRegionData: payload => dispatch(getRegionData(payload)),
    getStoreData: payload => dispatch(getStoreData(payload))
  };
};

export default connect(
  mapStateToProps,
  mapDispatchToProps
  // mapActionToProps
)(withRouter(RadioGenerateRecommendation));
