import React, { Component } from 'react';
import { InputGroup, FormControl } from 'react-bootstrap';
import '../StoreConfigContainers/StoreConfig';
import DataTable from '../../../services/reactTable';
import '../../Configuration/StoreConfigContainers/StoreConfig.css';
import StoreConfigRowData from '../StoreConfigContainers/StoreConfigRowData';
import { cloneDeep, differenceBy } from 'lodash';
import excelIcon from '../../../images/leftPanel/status.svg';
import _ from 'lodash';

export class CreateStoreGroup extends Component {
  state = {
    storeGroupName: '',
    allStores: [],
    editAllstoreData: [],
    currentStores: [],
    selectedAllStores: [],
    selectedCurrentStores: [],
    storeTableEditData: [],
  };
  componentDidMount() {
    this.props.getStoreGroupTableData();
    // let { id } = this.props.match.params;
    // this.props.fetchStoreConfigStoreTableData(); allstoresdata api
    // this.props.fetchStoreConfigStoreTableEditData({ Stores_groups_Id: [id] }); edit storedata api
  }
  componentDidUpdate(prevProps, prevState) {
    // if (prevProps.storeTableData !== this.props.storeTableData) {
    //   this.setState(
    //     {
    //       allStores: this.props.storeTableData,
    //     },
    //     () => this.setTableData()
    //   );
    // }
    // if (prevProps.storeName !== this.props.storeName) {
    //   this.setGroupName();
    // }
  }

  setTableData = () => {
    // let editAllstoreData = cloneDeep(this.state.editAllstoreData);
    // let storeTableEditData = this.props.storeTableEditData;
    // const newAllStores = differenceBy(editAllstoreData, storeTableEditData, 'store_code');
    // this.setState({
    //   storeTableEditData: storeTableEditData,
    //   editAllstoreData: newAllStores,
    // });
  };
  setGroupName = () => {
    let { id } = this.props.match.params;
    // let storeName =
    //   this.props && this.props.storeName && this.props.storeName.filter((el) => el.value == id);
    // let name = storeName[0].label;
    this.setState({
      // storeGroupName: name,
    });
  };
  onStoreGroupNameChangeHandler = (e) => {
    this.setState({
      storeGroupName: e.target.value,
    });
  };

  header = [
    {
      Header: 'Store Number',
      accessor: 'store_code',
    },
    {
      Header: 'Store Name',
      accessor: 'store_name',
    },
    {
      Header: 'Chain Description',
      accessor: 'chain_description',
    },
    {
      Header: 'Region Description',
      accessor: 'region_description',
    },
  ];

  onCurrentStoresSelectionChangeHandler = (rowInfo) => {
    let { selectedCurrentStores } = this.state;
    let foundEle = _.find(selectedCurrentStores, {
      store_code: rowInfo.original.store_code,
    });
    console.log(rowInfo, 'rowinfooo');
    if (foundEle) {
      _.remove(selectedCurrentStores, (item) => item.store_code === rowInfo.original.store_code);
    } else {
      selectedCurrentStores.push(rowInfo.original);
    }
    this.setState({
      selectedCurrentStores,
    });
  };

  onAddClickHandler = () => {
    const allStores = cloneDeep(this.state.allStores);
    const currentStores = cloneDeep(this.state.currentStores);
    // currentStores.push(...this.state.selectedAllStores);
    let newcurrentStores = _.unionWith(currentStores, this.state.selectedAllStores, _.isEqual);
    const newAllStores = differenceBy(allStores, this.state.selectedAllStores, 'store_code');
    console.log('asfasdf', this.state.selectedAllStores);
    console.log('newAllStores', newAllStores);
    this.setState({
      allStores: newAllStores,
      currentStores: newcurrentStores,
      selectedAllStores: [],
    });
  };
  onAllStoresSelectionChangeHandler = (rowInfo) => {
    let { selectedAllStores } = this.state;
    let foundEle = _.find(selectedAllStores, { store_code: rowInfo.original.store_code });
    console.log(rowInfo, 'rowinfooo', foundEle);
    if (foundEle) {
      _.remove(selectedAllStores, (item) => item.store_code === rowInfo.original.store_code);
    } else {
      selectedAllStores.push(rowInfo.original);
    }

    this.setState({
      selectedAllStores,
    });
  };
  onCreateStoreGroup = () => {
    if (this.props.isEditStoreGroup) {
      // let { id } = this.props.match.params;
      // let storeCodes = this.state.storeTableEditData.map((ele) => ele.store_code);
      // let { storeGroupName } = this.state;
      // this.props.fetchStoreConfigEditStoreData({
      //   Stores_groups_NAME: storeGroupName,
      //   Stores_Codes: storeCodes,
      //   Stores_groups_Id: id,
      // });
    } else {
      let storeCodes = this.state.currentStores.map((ele) => ele.store_code);
      let { storeGroupName } = this.state;
      this.props.createStoreGroup({
        Stores_groups_NAME: storeGroupName,
        Stores_Codes: storeCodes,
      });
    }
  };
  onCancelButtonHandler = () => {
    this.setState({ allStores: [], editAllstoreData: [] });
    this.props.history.goBack();
  };
  onRemoveClickHandler = () => {
    const currentStores = cloneDeep(this.state.currentStores);
    const allStores = cloneDeep(this.state.allStores);
    const newCurrentStStores = differenceBy(
      currentStores,
      this.state.selectedCurrentStores,
      'store_code'
    );
    // allStores.push(...this.state.selectedCurrentStores);
    let newallStores = _.unionWith(allStores, this.state.selectedCurrentStores, _.isEqual);

    this.setState({
      allStores: newallStores,
      currentStores: newCurrentStStores,
      selectedCurrentStores: [],
    });
  };

  render() {
    let {
      storeGroupName,
      currentStores,
      allStores,
      storeTableEditData,
      editAllstoreData,
    } = this.state;
    console.log(this.state.currentStores, 'curr');
    return (
      <div className="card p-2">
        <div style={{ display: 'flex', justifyContent: 'space-between' }}>
          <h4 className="font-size-14  pad-10 markdown-heading">
            Store Configuration/
            <span className="font-bold color-blue">Create Store Group</span>
          </h4>
          <div>
            <button className="btn btn-primary" onClick={this.props.onCancelButtonHandler}>
              Back
            </button>
          </div>
        </div>
        <hr
          className="mb-3"
          style={{
            marginTop: '-4px',
          }}
        />
        <div className="row justify-content-between">
          <div className="col-md-6">
            <label className="mr-1">
              <span style={{ color: 'red' }}>*</span>Store group name:
            </label>
            {this.props.isEditStoreGroup ? (
              <input
                style={{ background: '#dddddd', border: '1px solid #CCC' }}
                className="inputText"
                disabled
                type="text"
                value={storeGroupName}
              />
            ) : (
              // <span className="store-name">{storeGroupName}</span>
              <input
                className="inputText"
                type="text"
                onChange={this.onStoreGroupNameChangeHandler}
              />
            )}
          </div>
          <div className="col-md-2">
            {/* <button className="col-md-5 btn btn-primary" onClick={() => {}}>
              Upload &nbsp;&nbsp;
              <span>
                <img className="img-icons-create-promo" src={excelIcon} alt="" />
              </span>
            </button> */}
          </div>
        </div>
        <hr />
        <div className="row mx-2 create-store-grp-table">
          <div className="col-md-6">
            <div>All Stores</div>
            <div style={{ height: '370px' }}>
              <DataTable
                visibleColumns="onlyRowSelection"
                getRowProps={(rowInfo) => ({
                  onClick: () => this.onAllStoresSelectionChangeHandler(rowInfo),
                })}
                columns={this.header}
                data={this.props.storeGroupTableData || []}
                isPagination={true}
              />
            </div>
            <div>
              <button className="btnStyle btn btn-primary" onClick={this.onAddClickHandler}>
                Add
              </button>
            </div>
          </div>
          <div className="col-md-6">
            <div>
              <span className="mr-2">Current Stores : {currentStores && currentStores.length}</span>
            </div>
            <div style={{ height: '370px' }}>
              <DataTable
                visibleColumns="onlyRowSelection"
                getRowProps={(rowInfo) => ({
                  onClick: () => this.onCurrentStoresSelectionChangeHandler(rowInfo),
                })}
                columns={this.header}
                data={currentStores}
                isPagination={true}
              />
            </div>
            <div>
              <button className="btnStyle btn btn-primary" onClick={this.onRemoveClickHandler}>
                Remove
              </button>
            </div>
          </div>
        </div>
        <div className="mb-3 mt-4" style={{ textAlign: 'center' }}>
          <button
            style={{ width: '7%' }}
            className=" btn btn-primary"
            onClick={this.onCreateStoreGroup}>
            {this.props.isEditStoreGroup ? 'Update' : 'Create'}
          </button>
          <button
            className=" btn ml-1 cancel-button"
            style={{ width: '7%' }}
            onClick={this.props.onCancelButtonHandler}>
            Cancel
          </button>
        </div>
      </div>
    );
  }
}

export default CreateStoreGroup;
