import React, { Component } from 'react';
import { connect } from 'react-redux';
import DraftAction from '../../../actions/draftStausFinalizeAction';
import Filters from '../../../services/filters';
import DataTable from '../../../services/reactTable';
import ColumnSelection from '../../columnSelection';
import Coldef from './coldef';
import styled from 'styled-components';
import _ from 'lodash';
import cloneDeep from 'clone-deep';
import RowData from './data';
import moment from 'moment';
import { toast } from 'react-toastify';
import '../draftStatus.css';
import NoData from '../../noTableData';
import IndeterminateCheckbox from '../../../components/cellRenders/inderterminateCheckbox';
import { disabled } from 'glamor';

class Draft extends Component {
  constructor(props) {
    super(props);
    this.state = {
      // tableData: RowData,
      columnOptions: [],
      selLabels: [],
      columns: [],
      selectedRows: [],
    };
  }

  findStatusColor = (status, color) => {
    switch (status) {
      case 'high':
        return color.high;
      case 'medium':
        return color.medium;
      case 'low':
        return color.low;
      default:
        break;
    }
  };

  bgmCol = (cellInfo) => {
    if (cellInfo.column.id === 'md_status') var status = cellInfo.row.original.md_status;
    let bgCol = {
      high: '#F8E8E8',
      medium: '#FCF3D1',
      low: '#E8F8F5',
    };
    return this.findStatusColor(status, bgCol);
  };
  txtCol = (cellInfo) => {
    if (cellInfo.column.id === 'md_status') var status = cellInfo.row.original.md_status;
    let txtCol = {
      high: '#D38485',
      medium: '#F0C53A',
      low: '#66CFBA',
    };
    return this.findStatusColor(status, txtCol);
  };
  sendForApproval = () => {
    let msg = [];
    let payload = {
      markdown_data: this.state.selectedRows.map((item, i) => {
        let arrLength = this.state.selectedRows.length;
        let msgStr = `Scenario '${item.scenario}' of Plan '${item.md_plan}'${
          i === arrLength - 2 ? ' and ' : i === arrLength - 1 ? '' : ', '
        }`;
        msg.push(msgStr);
        return {
          id: item.markdown_id,
          scenario: item.scenario,
        };
      }),
      update_status: 'pending',
    };
    this.props
      .sendForApproval(payload)
      .then((resp) => {
        if (resp && resp.data.status == true) {
          let successMsg = `${msg.join('')} has been sent for Approval successfully`;
          toast.success(successMsg, {
            autoClose: 15000,
            hideProgressBar: true,
          });
          this.props.getMarkdowns({ status: 'draft' });
          this.setState({
            selectedRows: [],
          });
        }
      })
      .catch((err) => {
        toast.error(err.message, {
          autoClose: 15000,
          hideProgressBar: true,
        });
      });
  };
  updateMyData = (rowIndex, columnId, value) => {
    // We also turn on the flag to not reset the page
    this.setState((old) => {
      let val = old.tableData.map((row, index) => {
        if (index === rowIndex) {
          return {
            ...row,
            [columnId]: value,
          };
        }
        return row;
      });
      return { tableData: val };
    });
  };
  mandatoryFiltersVal = () => {
    return (
      this.props.selectedHierarchy1 &&
      this.props.selectedHierarchy2.length &&
      this.props.startDate &&
      this.props.endDate
    );
  };
  onFilter = () => {
    if (!this.mandatoryFiltersVal()) {
      toast.error(`Please select mandatory filters`, {
        autoClose: 15000,
        hideProgressBar: true,
      });
      return;
    }
    let payload = {
      filters: {
        hierarchy1: this.props.selectedHierarchy1,
        hierarchy2: this.props.selectedHierarchy2,
        hierarchy3: this.props.selectedHierarchy3,
        hierarchy4: this.props.selectedHierarchy4,
        hierarchy5: this.props.selectedHierarchy5,
        hierarchy6: this.props.selectedHierarchy6,
        hierarchy7: this.props.selectedHierarchy7,
        hierarchy8: this.props.selectedHierarchy8,
        tiers: this.props.selectedStores,
      },
      startDate: this.props.startDate ? moment(this.props.startDate).format('YYYY-MM-DD') : null,
      endDate: this.props.endDate ? moment(this.props.endDate).format('YYYY-MM-DD') : null,
      status: 'draft',
    };
    this.props.getMarkdowns(payload);
  };

  componentDidUpdate() {
    if (this.props.scenarioSelected.value) {
      this.props.scenarioSelectedHandler(false, []);
      this.onScenarioSelection(this.props.scenarioSelected.row);
    }
  }
  onScenarioSelection = (rowData) => {
    let { selectedRows } = this.state;
    let foundEle = _.find(selectedRows, {
      markdown_id: rowData.original.markdown_id,
    });
    if (foundEle) {
      _.remove(selectedRows, (item) => item.markdown_id === rowData.original.markdown_id);
    } else {
      selectedRows.push(rowData.original);
    }
    this.setState({
      selectedRows,
    });
  };

  render() {
    console.log(this.props.markdowns.message, 'action.value.message');
    return (
      <div className="createMd pl-2 pr-2">
        <div className="card">
          <div>
            <h4 className="font-size-14  pad-10 markdown-heading">
              Markdown Optimization /<span className="font-bold color-blue">Draft</span>
            </h4>
          </div>
          <hr
            className="mb-2"
            style={{
              marginTop: '-4px',
            }}
          />
          <div className="filterCol mb-4">
            <Filters
              onFilter={this.onFilter}
              stores
              dateRange
              hierarchy={[
                {
                  type: 'hierarchy1',
                  isMandatory: true,
                  isMultiple: false,
                },
                {
                  type: 'hierarchy2',
                  isMultiple: true,
                  isMandatory: true,
                },
                {
                  type: 'hierarchy3',
                  isMultiple: true,
                },
                {
                  type: 'hierarchy4',
                  isMultiple: true,
                },
                {
                  type: 'hierarchy5',
                  isMultiple: true,
                },
                {
                  type: 'hierarchy6',
                  isMultiple: true,
                },
                {
                  type: 'hierarchy7',
                  isMultiple: true,
                },
                {
                  type: 'hierarchy8',
                  isMultiple: true,
                },
              ]}
              storeIsNotMandatory={true}
            />
          </div>
        </div>
        {this.props.markdowns && this.props.markdowns.length ? (
          <div className="card pl-3 pr-3 mt-3">
            <ColSelBtn>
              {this.props.columnHeaders && Object.keys(this.props.columnHeaders).length && (
                <ColumnSelection
                  onHandleCheckBox={this.props.onHandleCheckBox}
                  columnHeaders={this.props.columnHeaders}
                  onHandleSelect={this.props.onHandleSelect}
                  columnOptions={this.props.columnOptions}
                  selectedItems={this.props.selectedItems}
                  selLabels={this.props.selLabels}
                />
              )}
              <div style={{ padding: '1rem' }}>
                <button
                  onClick={this.sendForApproval}
                  disabled={!this.state.selectedRows.length}
                  className="btn btn-primary">
                  Send for Approval
                </button>
                &nbsp;
              </div>
            </ColSelBtn>
            <div className="tablel">
              <DataTable
                columns={this.props.columns}
                data={this.props.markdowns}
                isSort={true}
                isExpanded={true}
                updateMyData={this.updateMyData}
                isPagination={true}
                NoDataComponent="This is the dynamic title"
                getCellProps={(cellInfo) => ({
                  // onClick: () => this.onCellClick(cellInfo),
                  style: {
                    backgroundColor: this.bgmCol(cellInfo),
                    color: this.txtCol(cellInfo),
                    margin: '4px 4px',
                    borderRadius: '2px',
                  },
                })}
              />
            </div>
          </div>
        ) : (
          <NoData
            description={
              this.props.markdowns && this.props.markdowns.length <= 0
                ? 'Please select filters to show Markdown plans.'
                : 'No adatat'
            }
          />
        )}
      </div>
    );
  }
}
const mapStateToProps = (store) => {
  return {
    markdowns: store.draftStatusReducer.markdowns,
    columnHeaders: store.draftStatusReducer.columnHeaders,
    selectedHierarchy1: store.filtersReducer.selectedHierarchy1,
    selectedHierarchy2: store.filtersReducer.selectedHierarchy2,
    selectedHierarchy3: store.filtersReducer.selectedHierarchy3,
    selectedHierarchy4: store.filtersReducer.selectedHierarchy4,
    selectedHierarchy5: store.filtersReducer.selectedHierarchy5,
    selectedHierarchy6: store.filtersReducer.selectedHierarchy6,
    selectedHierarchy7: store.filtersReducer.selectedHierarchy7,
    selectedHierarchy8: store.filtersReducer.selectedHierarchy8,
    selectedStores: store.filtersReducer.selectedStores,
    startDate: store.filtersReducer.startDate,
    endDate: store.filtersReducer.endDate,
  };
};

const mapActionToProps = {
  sendForApproval: DraftAction.sendForApproval,
};

export default connect(mapStateToProps, mapActionToProps)(Draft);

const ColSelBtn = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: space-between;
`;
