import React, { Component } from 'react';
import { connect } from 'react-redux';
import { withRouter } from 'react-router-dom';
import Filters from '../../services/filters';
import './summaryStyle.css';
import styled from 'styled-components';
import LoadingOverlay from '../../utils/Loader';
import NoData from '../noTableData';
import SummaryAction from '../../actions/summaryAction';
import plannedReportsAction from '../../actions/plannedReportsAction';
import { aggColDef, binToWeekColDef, weekToBinColDef, detailedColDef } from './aggColdef';
import _ from 'lodash';
import DataTable from '../../services/reactTable';
import ColumnSelection from '../columnSelection';
import moment from 'moment';
import card1Image from '../../images/card1Image.png';
import card2Image from '../../images/card2Image.png';
import card3Image from '../../images/card3Image.png';
import card4Image from '../../images/card4Image.png';
import Switch from '../../services/toggleSwitch';
import CardComponent from '../../services/cardComponent/cardComponent';
import filterActions from '../../actions/filtersAction';
import { toPercentageMultipliedByHundred, toUnit, toDollar } from '../../helper/formatter';

class Summary extends Component {
  state = {
    switchedPlans: false, //default aggregated view
    textStr: 'Please select filters to show Markdown plans',
    // firstCardText: 'KPIs to be decided',
    firstCardFirstValue: '12%',
    firstCardSecondValue: '24%',
    secondCardFirstValue: '16%',
    secondCardSecondValue: '20%',
    thirdCardFirstValue: '6%',
    thirdCardSecondValue: '10%',
    fourthCardFirstValue: '345',
    fourthCardSecondValue: '3,457',
    cardWeekText: 'LW',
    cardMonthText: 'TW',
    cardTotalText: 'Total',
    selectedItems: [
      {
        label: 'Margin %',
        value: 'margin_perc', //for subcomponents
        overall: 'overall_margin_perc', //for main table(agg-parent)
        detailed: 'week_level_margin_perc', //for detailed table
      },
    ],
    columnOptions: [
      {
        label: 'Margin %',
        value: 'margin_perc',
        overall: 'overall_margin_perc',
        detailed: 'week_level_margin_perc',
      },
      {
        label: 'Margin $',
        value: 'margin$',
        overall: 'overall_margin$',
        detailed: 'week_level_margin$',
      },
      {
        label: 'AUM $',
        value: 'aum',
        overall: 'overall_aum',
        detailed: 'week_level_aum',
      },
      {
        label: 'Revenue $',
        value: 'revenue$',
        overall: 'overall_revenue$',
        detailed: 'week_level_revenue$',
      },
      // {
      //   label: 'Planned units',
      //   value: 'planned_units',
      //   overall: 'overall_planned_units',
      //   detailed: 'week_level_planned_units',
      // },
      {
        label: 'Markdown units',
        value: 'markdown_units',
        overall: 'overall_markdown_units',
        detailed: 'week_level_markdown_units',
      },
      {
        label: 'AUR $',
        value: 'aur',
        overall: 'overall_aur',
        detailed: 'week_level_aur',
      },
    ],
    selLabels: ['Margin', 'Sales'],
    columns: [],
    aggColDef: [],
    weekToBinColDef: [],
    binToWeekColDef: [],
    selectedRows: [],
    tableData: [],
    showKPIS: false,
    expandedRowDetails: [],
    pageNum: 1,
    KPILoader: false,
    tableLoader: false,
  };

  componentWillUnmount() {
    this.props.onResetSummaryTable();
  }
  findStatusColor = (status, color) => {
    switch (status) {
      case 'high':
        return color.high;
      case 'medium':
        return color.medium;
      case 'low':
        return color.low;
      default:
        break;
    }
  };

  bgmCol = (cellInfo) => {
    if (cellInfo.column.id === 'md_status') var status = cellInfo.row.original.md_status;
    let bgCol = {
      high: '#F8E8E8',
      medium: '#FCF3D1',
      low: '#E8F8F5',
    };
    return this.findStatusColor(status, bgCol);
  };
  txtCol = (cellInfo) => {
    if (cellInfo.column.id === 'md_status') var status = cellInfo.row.original.md_status;
    let txtCol = {
      high: '#D38485',
      medium: '#F0C53A',
      low: '#66CFBA',
    };
    return this.findStatusColor(status, txtCol);
  };
  metricHandler = (columns, selOptions, val) => {
    // val=detailed for detailed table, val=overall for agg-parent col and val=value for subcomponent cols
    let headers = _.cloneDeep(columns);
    _.remove(headers, (item) => item.metric || item.hideColumn); //removing metric:true and hideColumn: true cols
    _.forEach(selOptions, (item) => {
      let newTableCols = _.filter(headers, (a) => {
        return a.accessor === item[val];
      });
      let found = _.find(columns, { accessor: item[val] });
      if (newTableCols.length == 0) {
        let spliceAt = headers.length - 0; //modify here if the new col needs to be added at a specific index
        headers.splice(spliceAt, 0, found);
      }
    });

    return headers;
  };
  onHandleSelect = (selOptions) => {
    let headers, weekToBinHeader, binToWeekHeader;
    if (this.state.switchedPlans) {
      //detailed table case
      headers = this.metricHandler(detailedColDef, selOptions, 'detailed');
      this.setState({
        columns: headers,
        selectedItems: selOptions,
      });
    } else {
      //agg-table case: needs to update 3 coldefs,
      //imported coldefs already has all of the required columns,
      //we are just filtering out needed cols(filtering is done in metricHandler method)
      let newAggColDef = this.addMDPlanColumnHandler();
      headers = this.metricHandler(newAggColDef, selOptions, 'overall');
      weekToBinHeader = this.metricHandler(weekToBinColDef(), selOptions, 'value');
      binToWeekHeader = this.metricHandler(binToWeekColDef(), selOptions, 'value');

      this.setState({
        aggColDef: headers,
        weekToBinColDef: weekToBinHeader,
        binToWeekColDef: binToWeekHeader,
        selectedItems: selOptions,
      });
    }
  };
  handleInputChange = (value) => {
    // let data = [...this.state.data];
    // console.log(cellInfo.index, 'kkkkk');
    // data[cellInfo.index][cellInfo.column.id] = event.target.value;

    // this.setState({ data });
    console.log(value, 'value');
  };
  isSwitchedPaln = (val) => {
    if (val && !_.isEmpty(this.state.reqObj) && _.isEmpty(this.props.detailedSummary))
      this.props.getSummaryDetails(this.state.reqObj);
    let newAggColDef = this.addMDPlanColumnHandler();

    this.setState({
      switchedPlans: val,
      columns: _.filter(detailedColDef, (item) => !item.hideColumn),
      aggColDef: _.filter(newAggColDef, (item) => !item.hideColumn),
      weekToBinColDef: _.filter(weekToBinColDef, (item) => !item.hideColumn),
      binToWeekColDef: _.filter(binToWeekColDef, (item) => !item.hideColumn),
      selectedItems: [
        {
          label: 'Margin %',
          value: 'margin_perc',
          overall: 'overall_margin_perc',
          detailed: 'week_level_margin_perc',
        },
      ],
      expandedRowDetails: [],
    });
  };
  onFilter = ({ filters }) => {
    let payload = {
      filters: {
        hierarchy1: filters.hierarchy1,
        hierarchy2: filters.hierarchy2,
        hierarchy3: filters.hierarchy3,
        hierarchy4: filters.hierarchy4,
        hierarchy5: filters.hierarchy5,
        season: this.props.selectedSeason,
        clearance: this.props.selectedClearance,
        matrix: this.props.selectedMatrix,
        exclusion_list: this.props.selectedExclusionList,
        style_color: this.props.selectedStyleColor,
        country: this.props.selectedCountry,
        store_cluster: this.props.selectedStoreCluster,
        store: this.props.selectedStore,
        channel: this.props.selectedChannel,
        customStyleGroup: this.props.selectedCustomStyleGroup,
        customStoreGroup: this.props.selectedCustomStoreGroup,
      },
      startDate: moment(this.props.startDate).format('YYYY-MM-DD'),
      endDate: moment(this.props.endDate).format('YYYY-MM-DD'),
      week: this.props.weeks,
      page_num: 1,
      page_count: 5,
    };
    if (this.props.selectedChannel[0] === 'Web') payload.filters['ecom_flag'] = [1];
    if (this.props.selectedChannel[0] === 'Outlet') payload.filters['retail_flag'] = [1];

    this.getSummary(payload);
    this.props.getKPIdata(payload).then((res) => {
      if (res) this.setState({ showKPIS: true, KPILoader: false });
      else this.setState({ KPILoader: false });
    });
    this.setState({
      reqObj: payload,
      pageNum: 2,
      expandedRowDetails: [],
      KPILoader: true,
      tableLoader: true,
      switchedPlans: false,
    });
  };
  roundOffNumber = (val, key) => {
    if ((!val && val !== 0) || val === 'NA') return val === 'NA' ? val : '-';
    if (key === 'revenue' || key === 'margin' || key === 'cogs' || key === 'avg_retail') {
      return toDollar({ value: val });
    }
    if (key === 'margin_rate' || key === 'md_perc' || key === 'sell_through') {
      return toPercentageMultipliedByHundred({ value: val });
    }
    if (key === 'sales' || key === 'inv_oh' || key === 'wos') {
      return toUnit({ value: val });
    }
    return val;
  };

  addMDPlanColumnHandler = () => {
    let newAggColDef = _.cloneDeep(aggColDef);
    newAggColDef.splice(0, 0, {
      Header: 'MD Plan',
      accessor: 'md_plan',
      cellClassName: 'summary_md_plan',
      Cell: ({ row }) => {
        let dateCheck = moment().isBefore(row.original.end_date); //show edit btn only when current date is before plan end date, i.e.,for ongoing and future plans
        //add isSameOrBefore to show edit btn when current date and end date are same, i.e., plan ends today
        return (
          <div>
            <span>{row.original.md_plan ? row.original.md_plan : null}</span>
            <div className="mt-1 mb-2">
              {row.original.md_plan && dateCheck ? (
                <button
                  className="btn btn-secondary"
                  onClick={async () => {
                    await this.props.setRetainFilters(true);
                    this.props.updateSidebar('/vis/Markdown/create-markdown');
                    this.props.history.push({
                      pathname: `/vis/Markdown/create-markdown`,
                      state: { isEdit: true, planId: row.original.markdown_id },
                    });
                    // this.setState({ showEditModal: true, selectedPlanDetails: row.original });
                  }}>
                  <span>
                    <i className="fa fa-pencil color-blue">Edit</i>
                  </span>
                </button>
              ) : (
                ''
              )}
            </div>
          </div>
        );
      },
      align: 'center',
      downloadValue: 'plan',
    });
    return newAggColDef;
  };
  getSummary = async (payload) => {
    await this.props.getSummaryAggregated(payload).then((resp) => {
      let newAggColDef = this.addMDPlanColumnHandler();
      this.setState({
        columns: _.filter(detailedColDef, (item) => !item.hideColumn),
        aggColDef: _.filter(newAggColDef, (item) => !item.hideColumn),
        weekToBinColDef: _.filter(
          weekToBinColDef(this.handleInputChange),
          (item) => !item.hideColumn
        ),
        binToWeekColDef: _.filter(
          binToWeekColDef(this.handleInputChange),
          (item) => !item.hideColumn
        ),
        tableLoader: false,
      });
    });
    return true;
  };
  renderRowSubComponent = ({ row }) => {
    let { expandedRowDetails } = this.state; //contains all the expanded rows details
    let foundLevel = _.filter(expandedRowDetails, {
      //check if this row is expanded
      markdown_id: row.original.markdown_id,
    });
    let data = [];
    if (!foundLevel[0]) return; //if not expanded then return

    if (foundLevel[0] && foundLevel[0].level === 'week_to_bin') {
      //if row is expanded on week_to_bin level, directly take the data from row
      let newData = _.cloneDeep(row.original.week_level_data);
      if (!newData) return;
      _.map(newData, (item) => {
        item.subRows = item.bin_level_data; //changing bin_level_data key into subRows, to acquire Expandable feature from reatc-table
        delete item.bin_level_data;
        return item;
      });
      data = newData;
    }
    if (foundLevel[0] && foundLevel[0].level === 'bin_to_week') {
      //if row is expanded on bin_to_week level, then find row data using markdown_id from the reducer aggregatedSummary
      let newData = _.find(this.props.aggregatedSummary.bin_to_week_agg_data, {
        markdown_id: row.original.markdown_id,
      });
      if (!newData) return;
      newData = _.cloneDeep(newData);
      _.map(newData.bin_level_data, (item) => {
        item.subRows = item.week_level_data; //changing name to subRows to get Expandable feature from react-table
        item.start_date = newData.start_date;
        item.end_date = newData.end_date;
        delete item.week_level_data;
      });

      data = newData.bin_level_data;
    }
    let columns =
      foundLevel[0] && foundLevel[0].level === 'week_to_bin' && row.original.week_level_data
        ? this.state.weekToBinColDef
        : foundLevel[0] && foundLevel[0].level === 'bin_to_week'
        ? this.state.binToWeekColDef
        : []; //selecting colfdef according to the level user has choosen
    return (
      <div className="child-table">
        <DataTable
          columns={columns}
          data={data}
          isHeaderHidden={true}
          isChildComp={true} //true for only subComponents, added to make some styling changes
        />
      </div>
    );
  };
  expandedRowDetailsHandler = (row, level) => {
    //called whenever user clicks on a ">" icon in the cell
    //this is to track expanded row details, and on which level its expanded(week to bin/bin to week)
    let { expandedRowDetails } = this.state;
    if (_.find(expandedRowDetails, { markdown_id: row.markdown_id })) {
      _.remove(expandedRowDetails, { markdown_id: row.markdown_id });
    } else {
      expandedRowDetails.push({
        markdown_id: row.markdown_id,
        level,
      });
    }
    this.setState({ expandedRowDetails });
  };
  nextPageHandler = async () => {
    let payload = _.cloneDeep(this.state.reqObj);
    payload.page_num = this.state.pageNum;
    this.setState({ pageNum: this.state.pageNum + 1 });
    await this.getSummary(payload);
    return true;
  };
  render() {
    console.log(this.state.weekToBinColDef, 'weekToBinColDef');
    const imageArray = [
      card1Image,
      card2Image,
      card3Image,
      card4Image,
      card1Image,
      card2Image,
      card3Image,
      card4Image,
      card1Image,
      card2Image,
    ];
    const cardStyle = [
      'card-border1',
      'card-border2',
      'card-border3',
      'card-border4',
      'card-border1',
      'card-border2',
      'card-border3',
      'card-border4',
      'card-border1',
      'card-border2',
    ];

    return (
      <LoadingOverlay
        loader={this.props.getOverlayLoaderState || this.state.KPILoader || this.state.tableLoader}
        spinner>
        <div className="createMd pl-2 pr-2 summary-comp">
          <div className="card CalendarMain promoCalendarDiv1 mt-3">
            <div>
              <h4 className="font-size-14  pad-10 markdown-heading">
                MarkSmart /<span className="font-bold color-blue">Summary</span>
              </h4>
            </div>
            <hr
              className="mb-2"
              style={{
                marginTop: '-4px',
              }}
            />
            <div className="filterCol mb-4">
              <Filters
                storeClusterDisable={true}
                onReset={(e) => {
                  this.setState({
                    textStr: 'Please select filters to show Markdown plans',
                    showKPIS: false,
                  });
                  this.props.onResetSummaryTable();
                }}
                hierarchy={this.state.filters}
                onFilter={this.onFilter}
                dateRange
                filtersOptions={[
                  { type: 'country', isMandatory: true, isMultiple: false },
                  { type: 'channel', isMandatory: true, isMultiple: false },
                  { type: 'storeCluster', isMandatory: false, isMultiple: true },
                  { type: 'customStoreGroup', isMandatory: false, isMultiple: true },
                  { type: 'store', isMandatory: false, isMultiple: true },
                  {
                    type: 'hierarchy1',
                    isMandatory: true,
                    isMultiple: false,
                    isHierarchy: true,
