              />
            </div>
          </div>
          {/* <div className="row" style={{ marginTop: '15px' }}>
            {this.props.summaryKPIData.map((summaryKPIData) => (
              <div className="col-md-3 col-sm-3 col-xs-6 p-2">
                <CardComponent
                  cardImage={summaryKPIData.cardImage}
                  cardText={this.state.firstCardText}
                  cardValue={summaryKPIData.cardValue}
                  cardValueSecond={summaryKPIData.cardValueSecond}
                  cardWeekText={this.state.cardWeekText}
                  cardMonthText={this.state.cardMonthText}
                  cardStyles={{
