import React, { Component } from 'react';
import Filters from '../../services/filters';
import MarkdownAnalysis from '../MarkdownAnalysis';
import AnalysisTable from '../MarkdownAnalysis/AnalysisTable';
import InventoryInfo from './InventoryInfo';
import Constraints from './Constraints';
import CreateCadence from './CreateCadence';
import Modal from 'react-awesome-modal';
import { withRouter } from 'react-router-dom';
import Select from 'react-select';
import { connect } from 'react-redux';
import Download from '../../services/download';
import LoadingOverlay from '../../utils/Loader';
import { config } from '../../utils/config';
import _ from 'lodash';
import GetMarkdownPercentageHeader from './GetMarkdownPercentageHeader';
import manuallyCreateMdActions from '../../actions/manuallyCreateMdActions.js';
import plannedReportsAction from '../../actions/plannedReportsAction';
import './index.css';
import moment from 'moment';
import { Button, Tabs, Tab } from 'react-bootstrap';
import MarkdownDetailedTable from './MardownDetailedTable';
import cloneDeep from 'clone-deep';
import ComparisonTable from './ComparisonTable';
import ComparisonMetricTable from './ComparisonMetricTable';
import classNames from 'classnames';
import ExcludeStoreTable from './excludeStoreTable';
import PlanSavePopup from './planSavePopup';
import { toast } from 'react-toastify';
// import FormikFilter from './FormikFilter';
import comparisonColdef from './comparisonColdef';
import ConstraintsModal from './ConstraintsModal';
import Switch from '../../services/toggleSwitch';
import io from 'socket.io-client';

class ManuallyCreateMD extends Component {
  constructor(props) {
    super(props);
    this.state = {
      // minMdFreqLimit: '',
      // maxMdFreqLimit: '',
      // maxNoDistinctMdLimit: '',
      // minNoDistinctMdLimit: '',
      // minDiscountBinsLimit: 1,
      // maxDiscountBinsLimit: 4,
      maxDiscountLimit: '',
      minDiscountLimit: '',
      maxStepSizeLimit: '',
      minStepSizeLimit: '',
      minDiscountDiffLimit: '',
      maxDiscountDiffLimit: '',
      stores: [],
      selectedStyle: 'all',
      saveCadenceRequest: {},
      selectedSkuId: null,
      targetSellThroughVal: null,
      saveAsDefault: false,
      markdownTypeVal: { label: 'Hard MD', value: 'Hard MD' },
      mdFrequency: { min: 1, max: null },
      noOfDistinctMd: { min: null, max: null },
      discountBins: 1,
      discount: { min: 20, max: 80 },
      stepSize: { min: null, max: null },
      discountDiff: { min: null, max: null },
      // mdLevel: '',
      cadenceConstraintsVal: { label: 'Overall', value: 'overall' },
      offerType: { label: '%off', value: '%off', key: 'percent_off' },
      offerIcon: '%',
      offerIconText: 'discount %',
      marginVal: null,
      overallCadenceData: [],
      skuLabelCadenceData: [],
      simulatedCadenceData: [], //reset edited input field data
      offset: 10,
      minTS: 0,
      maxTS: 100,
      minMargin: -100,
      maxMargin: 100,
      totalInDc: 0,
      totalOnHand: 0,
      totalOnOrder: 0,
      totalInTransit: 0,
      cadenceData: ['20', '25', '30', '35', '40', '45', '50', '55', '60', '65', '70', '75', '80'],
      currentPage: 1,
      filters: [
        {
          type: 'hierarchy1',
          isMandatory: true,
          isMultiple: false,
          isHierarchy: true,
        },
        {
          type: 'hierarchy2',
          isMandatory: true,
          isMultiple: true,
          isHierarchy: true,
        },
        {
          type: 'hierarchy3',
          isMandatory: true,
          isMultiple: true,
          isHierarchy: true,
        },
        {
          type: 'hierarchy4',
          isMandatory: true,
          isMultiple: true,
          isHierarchy: true,
        },
        {
          type: 'hierarchy5',
          isMandatory: true,
          isMultiple: true,
          isHierarchy: true,
        },
      ],
      // filteredData: {},
      inventoryDc: 0,
      onHandInventory: 0,
      onOrderInventory: 0,
      inTransitInventory: 0,
      totalInventory: 0,
      currentInventory: 0,
      totalStores: 0,
      selectedStores: 0,
      disableSimBtn: true,
      selectedView: 'Overview',
      selectedSku: [],
      selectedCustomGroup: [],
      selectedStyleColor: [],
      showExcludeStorePopup: false,
      showPlanSavePopup: false,
      viewSimulationResults: false,
      TabTitles: ['Optimal'],
      defaultActiveKey: 'Optimal',
      comparisonData: [],
      showCadence: false,
      simulatedData: false,
      comparisonRow: [],
      status: 'pending',
      md_level: 'week',
      isDraftDisabled: false,
      detailedTableData: {},
      excludedRows: [],
      isEdit: false,
      planId: null,
      showConstaints: false,
      editFrom: null,
      editBy: false, //false: Simulate, true: Reoptimize
      fromNotification: false,
      showProcessing: false,
      fromDraft: false,
    };
    this.socket = null;
  }
  detailedTableDataHandler = (data) => {
    this.setState({ detailedTableData: data });
  };

  showConstaintsHandler = (val) => {
    if (!val) document.body.setAttribute('style', '');
    this.setState({ showConstaints: val });
  };
  onFilterStyles = () => {
    let { selectedSkuId, selectedCustomGroup, selectedStyleColor } = this.state;

    this.setState({
      selectedStyle: selectedSkuId ? selectedSkuId.value : 'all',
    });
    if (this.state.isEdit && selectedSkuId) {
      this.getStyleColorData(selectedSkuId);
      return;
    }
    if (this.state.isEdit && !selectedSkuId) {
      //for all case we are gonna use markDownOptimization data for prevData, its already stored in analysisData reducer
      return;
    }
    selectedSkuId && selectedSkuId.value
      ? this.callAPISimulate(selectedSkuId.value)
      : this.callAPISimulate();
  };
  selectedSkuHandler = (val) => {
    this.setState({
      selectedStyle: val ? val.value : 'all',
      selectedSkuId: val,
    });
    if (this.state.isEdit && val) {
      this.getStyleColorData(val);
      return;
    }
    if (this.state.isEdit && !val) {
      //for all case we are gonna use markDownOptimization data for prevData, its already stored in analysisData reducer
      return;
    }
    val && val.value ? this.callAPISimulate(val.value) : this.callAPISimulate();
  };

  selectedCustomGroupHandler = (val) =>
    this.setState({ selectedCustomGroup: val, selectedStyleColor: '' });

  selectedStyleColorHandler = (val) => this.setState({ selectedStyleColor: val });

  comparisonCheckbox = (comparisonRowVal) => {
    console.log(comparisonRowVal, 'comparisonRowVal');
    this.setState({ comparisonRow: comparisonRowVal });
    // this.setState({ disableSimBtn: false });
  };
  onComparisonInputChange = (data) => {
    let cadence =
      this.state.cadenceConstraintsVal.value == 'overall'
        ? cloneDeep(this.state.overallCadenceData)
        : cloneDeep(this.state.skuLabelCadenceData);
    cadence[this.state.TabTitles.indexOf('Comparison')] = data;
    let dataArray = [];
    _.map(data, (item) => {
      dataArray = [...dataArray, ...Object.values(item)];
    });
    this.setState({
      overallCadenceData: cadence,
      skuLabelCadenceData: cadence,
    });
    this.setState({ disableSimBtn: dataArray.includes('') ? true : false });
  };
  onUpdateMarkdownPercentage = (data, index) => {
    if (this.state.cadenceConstraintsVal.value === 'overall') {
      let data1 = cloneDeep(this.state.overallCadenceData);
      data1[index] = data;
      this.setState({
        overallCadenceData: data1,
        disableSimBtn: Object.values(data[0]).includes('') ? true : false,
      });
    } else {
      let data1 = cloneDeep(this.state.skuLabelCadenceData);
      data1[index] = data;
      this.setState({
        skuLabelCadenceData: data1,
        disableSimBtn: Object.values(data[0]).includes('') ? true : false,
      });
    }
    // this.setState({ disableSimBtn: false });
  };
  // getComparsionHeader = (header) => {
  //   if (this.state.cadenceConstraintsVal === 'sku' && header === 'Comparison') {
  //     let optimalData = this.state.skuLabelCadenceData[0];
  //     let options = optimalData.map((data) => {
  //       return { label: data.SKU_Id, value: data.SKU_Id };
  //     });
  //     return (
  //       <div className="row skuLabelStyle">
  //         <label className="skuLabel">SKU/SKU Group</label>
  //         <div className="col-md-2">
  //           <Select name="category" options={options} />
  //         </div>
  //       </div>
  //     );
  //   }
  //   return (
  //     <div className="add-scenario-cont">
