  comparisonDataHandler = () => {
    let { overallCadenceData } = this.state;
    let comparisonTabs = _.map(
      overallCadenceData[overallCadenceData.length - 1],
      (item) => item.scenarios
    );
    if (this.state.cadenceConstraintsVal.value === 'sku') {
      let { skuLabelCadenceData } = this.state;
      comparisonTabs = _.map(
        skuLabelCadenceData[skuLabelCadenceData.length - 1],
        (item) => item.scenarios
      );
    }
    let comparisonData = _.cloneDeep(this.props.comparisonData);

    _.map(Object.keys(comparisonData), (item) => {
      if (!comparisonTabs.includes(item)) {
        delete comparisonData[item];
      }
    });
    return comparisonData;
  };
