  validateMargin = (e) => {
    if (
      this.state.minTS <= parseInt(this.state.marginVal) &&
      this.state.maxTS >= parseInt(this.state.marginVal)
    ) {
      return true;
    }
    let msg = _.isEmpty(this.state.marginVal)
      ? 'Please enter Margin % value'
      : 'Margin % value should be between 0 to 100';
    toast.error(msg, {
      autoClose: 15000,
      hideProgressBar: true,
    });
    return false;
  };
  validateSellThrough = (e) => {
    if (
      this.state.minTS <= parseInt(this.state.targetSellThroughVal) &&
      this.state.maxTS >= parseInt(this.state.targetSellThroughVal)
    ) {
      return true;
    }
    let msg = _.isEmpty(this.state.targetSellThroughVal)
      ? 'Please enter Target Sell through % value'
      : 'Target Sell through % value should be between 0 to 100';
    toast.error(msg, {
      autoClose: 15000,
      hideProgressBar: true,
    });
    return false;
  };
  checkDateRange = () => {
    let todayDate = moment().format('YYYY-MM-DD'),
      endDate = moment().add(14, 'weeks').format('YYYY-MM-DD'), //user can select 13 weeks from today date
      weeksCheck = moment(this.state.endDate.format('YYYY-MM-DD')).isBetween(todayDate, endDate);

    if (
      this.state.startDate &&
      this.state.endDate &&
      moment().isBefore(this.state.startDate, 'week') &&
      weeksCheck
    ) {
      return true;
    }
    let msg = _.isEmpty(this.state.startDate)
      ? 'Please enter Start Date'
      : _.isEmpty(this.state.endDate)
      ? 'Please enter End Date'
      : 'Start week should be greater than current week and end date less than 13 weeks';
    toast.error(msg, {
      autoClose: 15000,
      hideProgressBar: true,
    });
    return false;
  };
  validateAdditionalConstraints = (fieldName, input) => {
    const regex = /^[0-9\b]+$/;
    let numOfWeeks = this.getWeekRange(
      this.state.startDate && this.state.startDate.week(),
      this.state.endDate && this.state.endDate.week()
    );
    switch (fieldName) {
      case 'discountBins':
        if (!(input >= 1 && input <= 4 && regex.test(input))) {
          this.constraintHandler(1, 'discountBins');
          toast.error('Please enter values between 1 and 4', {
            autoClose: 15000,
            hideProgressBar: true,
          });
          break;
        }
        break;
      case 'mdFrequency':
        if (!numOfWeeks || !numOfWeeks.length) {
          this.constraintHandler({ min: 1, max: '' }, 'mdFrequency');
          toast.error('Please enter dates', {
            autoClose: 15000,
            hideProgressBar: true,
          });
          break;
        }
        if (!(regex.test(input.min) && input.min >= 1 && input.min <= numOfWeeks.length)) {
          this.constraintHandler({ min: 1, max: this.state.mdFrequency.max }, 'mdFrequency');
          toast.error('Please enter values between 1 and Number of Weeks for min MD Frequency', {
            autoClose: 15000,
            hideProgressBar: true,
          });
          break;
        }
        if (!(regex.test(input.max) && input.max >= 1 && input.max <= numOfWeeks.length)) {
          this.constraintHandler(
            { min: this.state.mdFrequency.min, max: numOfWeeks.length },
            'mdFrequency'
          );
          toast.error('Please enter values between 1 and Number of Weeks for max MD Frequency', {
            autoClose: 15000,
            hideProgressBar: true,
          });
          break;
        }

        break;
      case 'noOfDistinctMd':
        if (!numOfWeeks || !numOfWeeks.length) {
          this.constraintHandler({ min: 1, max: '' }, 'noOfDistinctMd');
          toast.error('Please enter dates', {
            autoClose: 15000,
            hideProgressBar: true,
          });
          break;
        }
        if (!(regex.test(input.min) && input.min >= 1 && input.min <= numOfWeeks.length)) {
          this.constraintHandler({ min: 1, max: this.state.noOfDistinctMd.max }, 'noOfDistinctMd');
          toast.error('Please enter values between 1 and Number of Weeks for Distinct MD', {
            autoClose: 15000,
            hideProgressBar: true,
          });
          break;
        }
        if (!(regex.test(input.max) && input.max >= 1 && input.max <= numOfWeeks.length)) {
          this.constraintHandler(
            { min: this.state.noOfDistinctMd.min, max: numOfWeeks.length },
            'noOfDistinctMd'
          );
          toast.error('Please enter values between 1 and Number of Weeks for Distinct MD', {
            autoClose: 15000,
            hideProgressBar: true,
          });
          break;
        }

        break;
      case 'discount':
        if (this.state.offerType.value === '%off') {
          if (!(input.min >= 20 && input.min <= 80 && regex.test(input.min))) {
            this.constraintHandler({ min: 20, max: this.state.discount.max }, 'discount');
            toast.error('Please enter valid values for min discount %off', {
              autoClose: 15000,
              hideProgressBar: true,
            });
          }
          if (!(input.max >= 20 && input.max <= 80 && regex.test(input.max))) {
            this.constraintHandler({ min: this.state.discount.min, max: 80 }, 'discount');
            toast.error('Please enter valid values for max discount %off', {
              autoClose: 15000,
              hideProgressBar: true,
            });
          }
        } else if (this.state.offerType.value === 'pp') {
          if (!(input.min >= 0 && input.min <= 9999 && regex.test(input.min))) {
            this.constraintHandler({ min: 0, max: this.state.discount.max }, 'discount');
            toast.error('Please enter valid values for min discount PP', {
              autoClose: 15000,
              hideProgressBar: true,
            });
          }
          if (!(input.max >= 0 && input.max <= 9999 && regex.test(input.max))) {
            this.constraintHandler({ min: this.state.discount.min, max: 9999 }, 'discount');
            toast.error('Please enter valid values for max discount PP', {
              autoClose: 15000,
              hideProgressBar: true,
            });
          }
        }

        break;
      default:
        return true;
        break;
    }
  };
  acceptViewSimulationResults = () => {
    this.fetchCadenceHandler(this.state.simulationIdState); // this.props.simulationId
    this.setState({
      viewSimulationResults: false,
    });
  };
  generateCadence = () => {
    if (
      !this.validateSellThrough() ||
      // !this.validateMargin() || //bcs margin non mandatory
      !this.checkDateRange()
    ) {
      return;
    }
    this.resetCadence();
    this.setState({
      showCadence: false,
      simulatedData: true,
      // viewSimulationResults: true,
    });
    let startWeek = this.state.startDate && this.state.startDate.week();
    let endWeek = this.state.endDate && this.state.endDate.week();

    let weeks = this.getWeekRange(startWeek, endWeek);
    let dataObj = this.props.editPlanDetails[0] && this.props.editPlanDetails[0].request_obj;
    let editMdfilters = {
      filters: dataObj && dataObj.filters,
    };
    let { isEdit, editBy } = this.state;
    let payloadForCustomDetails = cloneDeep(
      this.state.filteredData && this.state.filteredData.filters
    );
    delete payloadForCustomDetails['customStoreGroup'];

    let request = isEdit && editBy ? editMdfilters : cloneDeep(this.state.filteredData);

    let inputData = {
      cadenceData: this.state.cadenceData,
      inventorySelected: '',
      storeSelected: '',
      excludeStore: this.state.excludedStores,
      sellThrough: this.state.targetSellThroughVal || '',
      margin: this.state.marginVal || '',
      offer_value: this.state.offerType.value || '',
      markdown_type: this.state.markdownTypeVal.value || '',
      cadence_type: this.state.cadenceConstraintsVal.value || 'overall',
      min_md_frequency: this.state.mdFrequency.min || '',
      max_md_frequency: this.state.mdFrequency.max || '',
      min_no_of_distinct_md: this.state.noOfDistinctMd.min || '',
      max_no_of_distinct_md: this.state.noOfDistinctMd.max || '',
      no_of_discount_bins: this.state.discountBins || '',
      min_discount: this.state.discount.min || '',
      max_discount: this.state.discount.max || '',
      min_step_size: this.state.stepSize.min || '',
      max_step_size: this.state.stepSize.max || '',
      min_discount_difference: this.state.discountDiff.min || '',
      max_discount_difference: this.state.discountDiff.max || '',
      startDate: this.state.startDate && this.state.startDate.format('YYYY-MM-DD'),
      endDate: this.state.endDate && this.state.endDate.format('YYYY-MM-DD'),
      week: weeks,
      totalInDc: isEdit && editBy && dataObj ? dataObj.totalInDc : this.state.totalInDc,
      totalOnHand: isEdit && editBy && dataObj ? dataObj.totalOnHand : this.state.totalOnHand,
      totalOnOrder: isEdit && editBy && dataObj ? dataObj.totalOnHand : this.state.totalOnOrder,
      totalInTransit: isEdit && editBy && dataObj ? dataObj.totalOnHand : this.state.totalInTransit,
      stores: isEdit && editBy && dataObj ? dataObj.stores : this.state.stores,
      md_level: this.state.md_level,
      md_level_no_range: weeks,
      startWeek,
      endWeek,
      save_as_default: this.state.saveAsDefault,
    };
    let saveCadenceRequest = { ...request, ...inputData };
    this.setState({
      saveCadenceRequest,
      showProcessing: true,
    });
    let token = localStorage.getItem('token');

    this.props.fetchCustomStyleDetails({ filters: payloadForCustomDetails }).then((res) => {
      let customStyleInfo = [];
      if (!_.isEmpty(res)) {
        customStyleInfo = res;
      }
      saveCadenceRequest.filters['custom_style_group_info'] = customStyleInfo;
      saveCadenceRequest.filters['customStyleGroup'] = this.props.selectedCustomStyleName;

      console.log(saveCadenceRequest, 'saveCadenceRequest');
      console.log('roomId log', this.state.room_id);

      this.socket.emit('generate cadence', {
        room_id: this.state.room_id,
        url: `${config.cadenceUrl}/Markdown`,
        request_obj: saveCadenceRequest, //requesObject,//whole request obj which u send it for generating cadence,
        token: token, //token stored
        // userId: this.state.sessionUserId,
      });
      // this.props.fetchCadence(saveCadenceRequest);
    });
  };

