  onEditSimulate = async (skus) => {
    let array = this.getCadenceData(0),
      skusCalc = skus && skus.value ? skus.value : this.state.selectedStyle;
    let dataObj = this.props.editPlanDetails[0] && this.props.editPlanDetails[0].request_obj;
    let selectedCustomStyleName =
      this.props.editPlanDetails[0] &&
      this.props.editPlanDetails[0].request_obj &&
      this.props.editPlanDetails[0].request_obj.customStyleName;
    let custom_style_group_info =
      this.props.editPlanDetails[0] &&
      this.props.editPlanDetails[0].request_obj &&
      this.props.editPlanDetails[0].request_obj.custom_style_group_info;

    let scenario = Object.keys(dataObj.analysisData);
    let analysisDataObj = dataObj.analysisData[scenario[0]];
    if (_.isEmpty(dataObj)) return;
    let startWeek = dataObj.startWeek;
    let endWeek = dataObj.endWeek;
    let weeks = this.getWeekRange(startWeek, endWeek);

    let currentWeek = this.props.editPlanDetails[0] && this.props.editPlanDetails[0].current_week;
    let fiscalWeekList = this.props.editPlanDetails[0] && this.props.editPlanDetails[0].weeks_list;

    let scenario_data_old = {};
    let scenario_data_new = {},
      sell_through_old_overall = {};
    let { pastWeeks, futureWeeks } = this.props.weekInfo;
    _.map(array, (arrayItem) => {
      //map the array to handle multiple bins
      let newArry = {};
      let oldArry = {};

      _.map(Object.keys(arrayItem), (item, index) => {
        //map each bin(level)
        //store old weeks data and new weeks data in seperate objects
        if (pastWeeks && pastWeeks.includes(item)) {
          oldArry[item] = arrayItem[item];
          sell_through_old_overall[item] =
            dataObj.analysisData[scenario[0]].overview.sellThrough[index]; // sell though is bin/level independent
        }

        if (futureWeeks && futureWeeks.includes(item)) {
          newArry[item] = arrayItem[item];
        }
      });
      //store in object of key value pair format, where key will b bin data and value will be week data
      scenario_data_old[arrayItem.bins] = oldArry;
      scenario_data_new[arrayItem.bins] = newArry;
    });

    let sell_through_old_sku = [];
    if (!_.isEmpty(Object.keys(scenario_data_old[1]))) {
      _.map(analysisDataObj.detailed.data, (item) => {
        let newObj = {};
        _.map(analysisDataObj.detailed.weeks, (weekNum) => {
          let newWeekNum = weekNum.replace('w', 'W');
          if (pastWeeks && pastWeeks.includes(newWeekNum)) {
            newObj[weekNum.replace('w', 'W')] = item[`${weekNum}sellThrough`];
          }
        });
        newObj.SKU_Id = item.SKU_Id;
        sell_through_old_sku.push(newObj);
      });
    }
    let inputData = {
      // filters: {
      //   hierarchy1: this.props.selectedHierarchy1,
      //   hierarchy2: this.props.selectedHierarchy2,
      //   hierarchy3: this.props.selectedHierarchy3,
      //   hierarchy4: this.props.selectedHierarchy4,
      //   hierarchy5: this.props.selectedHierarchy5,
      //   hierarchy6: this.props.selectedHierarchy6,
      //   hierarchy7: this.props.selectedHierarchy7,
      //   hierarchy8: this.props.selectedHierarchy8,
      //   tiers: this.props.selectedStores,
      // },
      filters: dataObj.filters,

      // let inputData = {
      //   filters: {
      //     hierarchy1: this.props.selectedHierarchy1,
      //     hierarchy2: this.props.selectedHierarchy2,
      //     hierarchy3: this.props.selectedHierarchy3,
      //     hierarchy4: this.props.selectedHierarchy4,
      //     hierarchy5: this.props.selectedHierarchy5,
      //     hierarchy6: this.props.selectedHierarchy6,
      //     hierarchy7: this.props.selectedHierarchy7,
      //     hierarchy8: this.props.selectedHierarchy8,
      //     customStyleGroup: selectedCustomStyleName,
      //     custom_style_group_info: custom_style_group_info,
      //     // customStyleGroup: this.props.selectedCustomStyleGroup,
      //     customStoreGroup: this.props.selectedCustomStoreGroup,
      //     tiers: this.props.selectedStores,
      //   },

      inventorySelected: '',
      storeSelected: '',
      sellThrough: dataObj.sellThrough || '',
      margin: dataObj.margin || '',
      markdown_type: this.state.markdownTypeVal.value || '',
      cadence_type: this.state.cadenceConstraintsVal.value || 'overall',
      scenario: 'true',
      cadenceData: this.state.cadenceData,
      startDate: dataObj.startDate,
      endDate: dataObj.endDate,
      week: weeks,
      totalInDc: dataObj.totalInDc,
      totalOnHand: dataObj.totalOnHand,
      totalOnOrder: dataObj.totalOnOrder,
      totalInTransit: dataObj.totalInTransit,
      stores: dataObj.stores,
      md_level: dataObj.md_level,
      md_level_no_range: dataObj.md_level_no_range,
      startWeek,
      endWeek,
      sell_through_old_sku,
      sell_through_old_overall: _.isEmpty(Object.keys(sell_through_old_overall))
        ? []
        : [sell_through_old_overall],
      scenario_data_old: _.isEmpty(Object.keys(scenario_data_old[1])) ? {} : scenario_data_old,
      scenario_data_new: _.isEmpty(Object.keys(scenario_data_new[1])) ? {} : scenario_data_new,
      skus: skusCalc,
      // customStyleGroup: _.isEmpty(this.state.selectedCustomGroup)
      //   ? ''
      //   : this.state.selectedCustomGroup.value,
      // styleColor: _.isEmpty(this.state.selectedStyleColor)
      //   ? ''
      //   : this.state.selectedStyleColor.value,
      compressedData: {
        saveData: dataObj.simulatedData[scenario[0]],
      },
    };

    inputData.filters['customStyleGroup'] = selectedCustomStyleName;

    let res = await this.props.editSimulate(inputData, this.state.defaultActiveKey);
    if (res && res.status == false) {
      this.setState({
        isDraftDisabled: true,
      });
      return res;
    } else {
      this.setState({
        isDraftDisabled: false,
      });
    }
    // if (!res.status) return;
    if (!_.isEqual(this.state.selectedStyle, skusCalc)) {
      this.setState({
        selectedStyle: skusCalc,
        selectedSkuId: skusCalc === 'all' ? null : skus,
      });
    }
    // this.setState({ disableSimBtn: true, isDraftDisabled: false });
