      this.createRooms();
    });

    this.socket.on('disconnect', async (reason) => {
      console.log('reason', reason);
    });

    this.socket.on('roomDetails', (data) => {
      let userId = localStorage.getItem('userId');
      console.log('testing back end room data', data, userId);
      if (userId == data.user_id) {
        //no need of type check, since the types are different
        this.setState(
          {
            room_id: data.room_id,
          },
          () => {
            console.log('roomId set', this.state.room_id);
          }
        );
      }
    });

    this.socket.on('simulationOutput', (data) => {
      console.log('simulationOutput', data);
      // in data.simulation_id u will get the id of the simulation response, which u will use it to to call get simulation data to fetch its data
      // this.setState({
      //   simulation_id: data.simulation_id,
      // });
      if (!data.simulation_id) {
        //if data is not saved/it's the err case, show error in toast
        toast.error(`${data.message}`, {
          autoClose: 15000,
          hideProgressBar: true,
        });
        this.setState({ showProcessing: false }); //loader
