        });
        this.fetchCadenceHandler(this.state.simulationIdState); // this.props.simulationId

        // return; // uncomment this if notification need to be updated when user is in create md screen
      }
      let newSimulationIdArray = this.props.simulationId;
      newSimulationIdArray.push(data.simulation_id);
      this.props.setSimulationId(_.uniq(newSimulationIdArray));
    });
