                        // disableSimBtn: true,
                      });
                    }}>
                    Send for approval
                  </button>

                  <button
                    className={
                      (this.state.isEdit && this.props.editFrom) ||
                      (this.state.isEdit && this.state.editFrom !== 'notEditModal')
                        ? 'd-none'
                        : 'btn btn-primary resetButton'
                    }
                    disabled={
                      this.state.isEdit
                        ? this.disabledButtonHandler('edit')
                        : this.disabledButtonHandler('approved')
                    }
                    onClick={() => {
                      // this.resetStyleValue();
                      this.setState({
                        showPlanSavePopup: true,
                        status: 'draft',
                        // disableSimBtn: true,
                      });
                    }}>
                    Send to drafts
                  </button>
                </div>
              </div>
            )}
          {this.state.cadenceConstraintsVal.value === 'overall' &&
            this.state.defaultActiveKey === 'Comparison' &&
            this.showComparisonMetricTable()}
          {this.state.cadenceConstraintsVal.value === 'sku' &&
            this.state.defaultActiveKey === 'Comparison' &&
            this.showComparisonMetricTable()}

          {this.state.showCadence &&
            this.state.simulatedData &&
            this.props.markdownAnalysisGraphData &&
            this.props.markdownAnalysisGraphData.overview &&
            this.state.defaultActiveKey !== 'Comparison' && (
              <div className="card mb-4">
                {this.state.isEdit && this.state.editFrom !== 'notEditModal' ? (
                  <div className="row justify-content-center">
                    <label className="font-size-14 markdown-percentage-heading ">
                      Simulation Results
                    </label>
                  </div>
                ) : (
                  <>
                    <div className="optimalheading pl-3 pt-2">{this.state.defaultActiveKey}</div>
                    <hr />
                    {this.state.defaultActiveKey !== 'Comparison' && (
                      <div className="overviewtoggle">
                        <button
                          type="button"
                          style={{
                            fontSize: '15px',
                            border: '1px solid',
                            borderWidth: '1px 0px 1px 1px',
                          }}
                          className={classNames({
                            active: this.state.selectedView === 'Overview',
                            btn: true,
                            'btn-outline-primary cal-btn': true,
                            overView: true,
                            detailed: true,
                          })}
                          onClick={() => this.changedView('Overview')}>
                          Overview
                        </button>
                        <button
                          type="button"
                          className={classNames({
                            active: this.state.selectedView === 'Detailed',
                            ' btn': true,
                            'btn-outline-primary cal-btn': true,
                            overView: true,
                            detailed: true,
                          })}
                          onClick={() => this.changedView('Detailed')}
                          style={{
                            fontSize: '15px',
                            border: '1px solid',
                            borderWidth: '1px 1px 1px 0px',
                          }}>
                          Detailed
                        </button>
                      </div>
                    )}
                  </>
                )}
                {this.state.showCadence &&
                  this.state.simulatedData &&
                  (this.state.selectedStyle === 'all'
                    ? this.props.markdownAnalysisGraphData
                    : this.props.markdownAnalysisGraphDataBySkus) &&
                  (this.state.selectedStyle === 'all'
                    ? this.props.markdownAnalysisGraphData.overview
                    : this.props.markdownAnalysisGraphDataBySkus.overview) &&
                  this.state.selectedView === 'Overview' &&
                  this.state.defaultActiveKey !== 'Comparison' && (
                    <div>
                      {this.state.isEdit && this.state.editFrom !== 'notEditModal' ? (
                        <div className="col mt-4">
                          <AnalysisTable
                            tableData={
                              this.state.selectedStyle === 'all'
                                ? this.props.analysisTable.analysisTabledata
                                : this.props.analysisTableBySkus[keyName]
                            }
                            tableData2={
                              this.state.selectedStyle === 'all'
                                ? this.props.editAnalysisData.analysisTabledata
                                : this.props.editAnalysisTableDataBySkus.analysisTabledata
                            }
                            isEdit={this.state.isEdit}
                            editFrom={this.state.editFrom}
                          />
                        </div>
                      ) : (
                        <MarkdownAnalysis
                          // anaTableData={anaTableData}
                          // graphData={graphData}
                          level={this.state.md_level}
                          analysisTable={
                            this.state.selectedStyle === 'all'
                              ? this.props.analysisTable.analysisTabledata
                              : this.props.analysisTableBySkus[keyName]
                          }
                          markdownAnalysisGraphData={
                            this.state.selectedStyle === 'all'
                              ? this.props.markdownAnalysisGraphData.overview
                              : this.props.markdownAnalysisGraphDataBySkus.overview
                          }
                          editMarkdownAnalysisGraphData={
                            this.state.selectedStyle === 'all'
                              ? this.props.editMarkdownAnalysisGraphData.overview
                              : this.props.editAnalysisTableDataBySkus.overview
                          }
                          editAnalysisData={
                            this.state.selectedStyle === 'all'
                              ? this.props.editAnalysisData.analysisTabledata
                              : this.props.editAnalysisTableDataBySkus.analysisTabledata
                          }
                          selectedSku={this.state.selectedSkuId}
                          selectedCustomGroup={this.state.selectedCustomGroup}
                          selectedStyleColor={this.state.selectedStyleColor}
                          optionSku={this.props.skuData}
                          selectedSkuHandler={(val) => this.selectedSkuHandler(val)}
                          onFilterStyles={this.onFilterStyles}
                          selectedCustomGroupHandler={(val) => this.selectedCustomGroupHandler(val)}
                          selectedStyleColorHandler={(val) => this.selectedStyleColorHandler(val)}
                          isEdit={this.state.isEdit}
                        />
                      )}
                    </div>
                  )}
                <div
                  className={
                    this.state.selectedView === 'Detailed' &&
                    this.state.defaultActiveKey !== 'Comparison'
                      ? 'visible'
                      : 'position-absolute invisible' //this component made visible instead of display-none to solve detailed table data mess up issue
                  }>
                  <div className="detailedTable ml-4 mr-4" style={{ paddingBottom: '2rem' }}>
                    <MarkdownDetailedTable
                      data={
                        this.state.selectedStyle === 'all'
                          ? this.props.markdownAnalysisGraphData.detailed
                          : this.props.markdownAnalysisGraphDataBySkus.detailed
                      }
                      editData={
                        this.state.selectedStyle === 'all'
                          ? this.props.editMarkdownAnalysisGraphData.detailed
                          : this.props.editAnalysisTableDataBySkus.detailed
                      }
                      columnHeaders={this.props.columnHeaders}
                      detailedTableDataHandler={this.detailedTableDataHandler}
                      detailedTableData={this.state.detailedTableData}
                      isEdit={this.state.isEdit}
                    />
                  </div>
                </div>
              </div>
            )}
          <Modal
            visible={this.state.viewSimulationResults}
            // effect="fadeInDown"
            effect="fadeInRight"
            width="22%"
            style={{ height: 'auto', overflow: 'auto' }}
            onClickAway={() => this.showPlanSavePopup()}>
            <div className="card" style={{ height: '132px' }}>
              <div className="card-body">
                <h6 className="card-title">
                  Do you want to view simulation results?
                  <span
                    onClick={() => this.viewSimulationResults()}
                    className="pull-right select-store-span">
                    <i
                      style={{ cursor: 'pointer' }}
                      className="fa fa-times"
                      onClick={() => this.viewSimulationResults()}
                      aria-hidden="true"
                    />
                  </span>
                </h6>
                <div className="d-flex justify-content-center mt-4">
                  <button className="btn btn-primary mr-3" onClick={this.viewSimulationResults}>
                    No
                  </button>
                  <button
                    className="btn btn-primary mr-3"
                    onClick={this.acceptViewSimulationResults}>
                    Yes
                  </button>
                </div>
              </div>
            </div>
          </Modal>
          <Modal
            visible={this.state.showPlanSavePopup}
            // effect="fadeInDown"
            effect="fadeInRight"
            width="30%"
            style={{ height: 'auto', overflow: 'auto' }}
            onClickAway={() => this.showPlanSavePopup()}>
            <div className="card">
              <div className="card-body">
                <h6 className="card-title">
                  Enter MD Plan Details
                  <span
                    onClick={() => this.showPlanSavePopup()}
                    className="pull-right select-store-span">
                    <i
                      style={{ cursor: 'pointer' }}
                      className="fa fa-times"
                      onClick={() => this.showPlanSavePopup()}
                      aria-hidden="true"
                    />
                  </span>
                </h6>
                <hr />
              </div>

              <div className="mr-3" style={{ display: 'flex', textAlign: 'right' }}>
                <PlanSavePopup
                  data={
                    this.state.cadenceConstraintsVal.value === 'overall'
                      ? this.state.overallCadenceData
                      : this.state.skuLabelCadenceData
                  }
                  activeIndex={this.getActiveIndex()}
                  comparsionSelectedData={cloneDeep(this.state.comparisonRow)}
                  activeTab={this.state.defaultActiveKey}
                  isEdit={this.state.isEdit}
                  editPlanDetails={this.props.editPlanDetails}
                  onSave={(planName, sceniosNames) =>
                    this.state.isEdit
                      ? this.onSaveEdit(planName, sceniosNames)
                      : this.onSave(planName, sceniosNames)
                  }
                />
              </div>
            </div>
          </Modal>
          <Modal
            visible={this.state.showExcludeStorePopup}
            effect="fadeInDown"
            width="75%"
            height="95%"
            onClickAway={() => this.showTablePopup()}>
            {/* <div className="card"> */}
            <div className="card-body pb-0">
              <h6 className="card-title" style={{ fontWeight: 'bold' }}>
                Select stores
                <span
                  onClick={() => this.showTablePopup()}
                  className="pull-right select-store-span">
                  <i
                    style={{ cursor: 'pointer' }}
                    className="fa fa-times"
                    onClick={() => this.showTablePopup()}
                    aria-hidden="true"
                  />
                </span>
              </h6>
              <hr />
            </div>
            {this.state.showExcludeStorePopup && this.props.inventoryInfoData && (
              <div className="ai-pre-grid-table overflow-handler">
                <ExcludeStoreTable
                  currentPage={this.state.currentPage}
                  offset={this.state.offset}
                  showTablePopup={this.showTablePopup}
                  columnHeaders={this.props.inventoryInfoData.columnHeaders || []}
                  excludeStoreValHandler={this.excludeStoreValHandler}
                  storeData={this.props.inventoryInfoData.store_data || []}
                  excludedRows={this.state.excludedRows}
                />
              </div>
            )}
            {/* </div> */}
          </Modal>
          {this.state.showConstaints ? (
            <ConstraintsModal
              offerTypeHandler={this.offerTypeHandler}
              showConstaintsHandler={this.showConstaintsHandler}
              mdLevelHandler={this.mdLevelHandler}
              markdownTypeHandler={this.markdownTypeHandler}
              //mdFrequencyHandler={this.mdFrequencyHandler}
              //noOfDistinctMdHandler={this.noOfDistinctMdHandler}
              //discountBinsHandler={this.discountBinsHandler}
              //discountHandler={this.discountHandler}
              stepSizeHandler={this.stepSizeHandler}
              discountDiffHandler={this.discountDiffHandler}
              saveAsDefaultHandler={this.saveAsDefaultHandler}
              offerType={this.state.offerType}
              mdLevel={this.state.cadenceConstraintsVal}
              markdownTypeVal={this.state.markdownTypeVal}
              mdFrequency={this.state.mdFrequency}
              noOfDistinctMd={this.state.noOfDistinctMd}
              discountBins={this.state.discountBins}
              saveAsDefault={this.state.saveAsDefault}
              discount={this.state.discount}
              stepSize={this.state.stepSize}
              discountDiff={this.state.discountDiff}
              offerIcon={this.state.offerIcon}
              offerIconText={this.state.offerIconText}
              // minMdFreqLimit={minMdFreqLimit}
              // maxMdFreqLimit={maxMdFreqLimit}
              numOfWeeks={this.getWeekRange(
                this.state.startDate && this.state.startDate.week(),
                this.state.endDate && this.state.endDate.week()
              )}
              // maxNoDistinctMdLimit={maxNoDistinctMdLimit}
              // minNoDistinctMdLimit={minNoDistinctMdLimit}
              minDiscountBinsLimit={minDiscountBinsLimit}
              maxDiscountBinsLimit={maxDiscountBinsLimit}
              maxDiscountLimit={maxDiscountLimit}
              minDiscountLimit={minDiscountLimit}
              maxStepSizeLimit={maxStepSizeLimit}
              minStepSizeLimit={minStepSizeLimit}
              minDiscountDiffLimit={minDiscountDiffLimit}
              maxDiscountDiffLimit={maxDiscountDiffLimit}
              validateAdditionalConstraints={this.validateAdditionalConstraints}
              constraintHandler={this.constraintHandler}
            />
          ) : null}
        </div>
      </LoadingOverlay>
    );
  }
}
const mapStateToProps = (state) => {
  return {
    getOverlayLoaderState: state.loaderReducer.overlayLoaderState,
    getOverlayLoaderText: state.loaderReducer.loaderText,
    inventoryInfoData: state.manuallyCreateMdData.inventoryInfoData,
    weekInfo: state.manuallyCreateMdData.weekInfo,
    cadenceData: state.manuallyCreateMdData.cadenceData,
    savedStatus: state.manuallyCreateMdData.status,
    skuData: state.manuallyCreateMdData.skus,
    startDate: state.filtersReducer.startDate,
    endDate: state.filtersReducer.endDate,
    weeks: state.filtersReducer.weeks,
    csv_names: state.manuallyCreateMdData.csv_names,
    editPlanDetails: state.manuallyCreateMdData.editPlanDetails,
    drops: state.filtersReducer.selectedDrops,
    selectedHierarchy1: state.filtersReducer.selectedHierarchy1,
    selectedHierarchy2: state.filtersReducer.selectedHierarchy2,
    selectedHierarchy3: state.filtersReducer.selectedHierarchy3,
    selectedHierarchy4: state.filtersReducer.selectedHierarchy4,
    selectedHierarchy5: state.filtersReducer.selectedHierarchy5,
    selectedSeason: state.filtersReducer.selectedSeason,
    selectedClearance: state.filtersReducer.selectedClearance,
    selectedMatrix: state.filtersReducer.selectedMatrix,
    selectedExclusionList: state.filtersReducer.selectedExclusionList,
    selectedextraStyleColor: state.filtersReducer.selectedStyleColor,
    selectedCountry: state.filtersReducer.selectedCountry,
    selectedStoreCluster: state.filtersReducer.selectedStoreCluster,
    selectedStore: state.filtersReducer.selectedStore,
    selectedChannel: state.filtersReducer.selectedChannel,
    selectedCustomStyleGroup: state.filtersReducer.selectedCustomStyleGroup,
    selectedCustomStyleName: state.filtersReducer.selectedCustomStyleName,
    selectedCustomStoreGroup: state.filtersReducer.selectedCustomStoreGroup,
    markdownAnalysisGraphData: state.markdownAnalysisData.markdownAnalysisGraphData,
    editMarkdownAnalysisGraphData: state.markdownAnalysisData.editMarkdownAnalysisGraphData,
    editAnalysisData: state.markdownAnalysisData.editAnalysisData,
    markdownAnalysisGraphDataBySkus: state.markdownAnalysisData.markdownAnalysisGraphDataBySkus,
    analysisTable: state.markdownAnalysisData.analysisTable,
    analysisTableBySkus: state.markdownAnalysisData.analysisTableBySkus,
    analysisDataBySkus: state.markdownAnalysisData.analysisDataBySkus,
    comparisonData: state.markdownAnalysisData.comparisonData,
    editMarkdownAnalysisGraphDataBySkus:
      state.markdownAnalysisData.editMarkdownAnalysisGraphDataBySkus,
    editAnalysisTableDataBySkus: state.markdownAnalysisData.editAnalysisTableDataBySkus,
    analysisData: state.markdownAnalysisData.analysisData,
    savedData: state.manuallyCreateMdData.savedData,
    columnHeaders: state.manuallyCreateMdData.columnHeaders,
    customStyleDetails: state.manuallyCreateMdData.customStyleDetails,
    cadenceInput: state.manuallyCreateMdData.cadenceInput,
    simulationId: state.manuallyCreateMdData.simulationId,
  };
};
const mapActionsToProps = {
  fetchCustomStyleDetails: manuallyCreateMdActions.fetchCustomStyleDetails,
  fetchInventoryInfo: manuallyCreateMdActions.fetchInventoryInfo,
  setSimulationId: manuallyCreateMdActions.setSimulationId,
  fetchSkuAndCsv: manuallyCreateMdActions.fetchSkuAndCsv,
  fetchCadence: manuallyCreateMdActions.fetchCadence,
  simulate: manuallyCreateMdActions.simulate,
  saveData: manuallyCreateMdActions.saveData,
  savedStatusFalse: manuallyCreateMdActions.savedStatusFalse,
  updateAnalysisData: manuallyCreateMdActions.updateAnalysisData,
  onResetInventoryInfo: manuallyCreateMdActions.onResetInventoryInfo,
  onResetEditedInfo: manuallyCreateMdActions.onResetEditedInfo,
  updateSidebar: plannedReportsAction.updateSidebar,
  fetchEditPlanData: manuallyCreateMdActions.fetchEditPlanData,
  fetchStyleColorData: manuallyCreateMdActions.fetchStyleColorData,
  editSimulate: manuallyCreateMdActions.editSimulate,
  fetchSimulationDataBySocket: manuallyCreateMdActions.fetchSimulationDataBySocket,
};

export default connect(mapStateToProps, mapActionsToProps)(withRouter(ManuallyCreateMD));
