import React, { Component } from 'react';
import StyleFilters from '../StyleFilters/StyleFilters';
import Select from '../../../services/select';
import Filters from '../../../services/filters';
// import TableComponent from '../../../components/TableComponent/TableComponent';
// import TableContextProvider from '../../../components/TableComponent/context';
// import CreateStore from '../../../utils/columnDefs/CreateStore';
import { cloneDeep, differenceBy } from 'lodash';
import excelIcon from '../../../images/leftPanel/status.svg';

const years = [
  // { label: 2019, value: 2019 },
  { label: 2020, value: 2020 },
  { label: 2021, value: 2021 },
  { label: 2022, value: 2022 },
];

const months = [
  { label: 'January', value: 'January' },
  { label: 'February', value: 'February' },
  { label: 'March', value: 'March' },
  { label: 'April', value: 'April' },
  { label: 'May', value: 'May' },
  { label: 'June', value: 'June' },
  { label: 'July', value: 'July' },
  { label: 'August', value: 'August' },
  { label: 'September', value: 'September' },
  { label: 'October', value: 'October' },
  { label: 'November', value: 'November' },
  { label: 'December', value: 'December' },
];

export class CreateStoreGroup extends Component {
  state = {
    storeGroupName: '',
    year: '',
    month: '',
    // allStores: CreateStore.data,
    currentStores: [],
    selectedAllStores: [],
    selectedCurrentStores: [],
    showEDLP: false,
    showGeo: true,
  };

  onStoreGroupNameChangeHandler = (e) => {
    this.setState({
      storeGroupName: e.target.value,
    });
  };

  onMonthChangeHandler = (value) => {
    this.setState({
      month: value,
    });
  };

  onYearChangeHandler = (value) => {
    this.setState({
      year: value,
    });
  };

  onAllStoresSelectionChangeHandler = (rowData) => {
    console.log('adsflasdf', rowData);
    this.setState({
      selectedAllStores: rowData,
    });
  };

  onCurrentStoresSelectionChangeHandler = (rowData) => {
    this.setState({
      selectedCurrentStores: rowData,
    });
  };

  onAddClickHandler = () => {
    const allStores = cloneDeep(this.state.allStores);
    const currentStores = cloneDeep(this.state.currentStores);
    currentStores.push(...this.state.selectedAllStores);
    const newAllStores = differenceBy(allStores, this.state.selectedAllStores, 'store_number');
    console.log('asfasdf', this.state.selectedAllStores);
    console.log('adfadf', newAllStores);
    this.setState({
      allStores: newAllStores,
      currentStores,
      selectedAllStores: [],
    });
  };

  onRemoveClickHandler = () => {
    const currentStores = cloneDeep(this.state.currentStores);
    const allStores = cloneDeep(this.state.allStores);
    const newCurrentStStores = differenceBy(
      currentStores,
      this.state.selectedCurrentStores,
      'store_number'
    );
    allStores.push(...this.state.selectedCurrentStores);
    this.setState({
      allStores,
      currentStores: newCurrentStStores,
      selectedCurrentStores: [],
    });
  };

  render() {
    return (
      <div className="card p-2">
        <div style={{ display: 'flex', justifyContent: 'space-between' }}>
          <h4 className="font-size-14  pad-10 markdown-heading">
            Store Configuration/<span className="font-bold color-blue">Create Store Group</span>
          </h4>
          <div>
            <button className="btn btn-primary" onClick={this.props.onCancelButtonHandler}>
              Back
            </button>
          </div>
        </div>
        <hr
          className="mb-2"
          style={{
            marginTop: '-4px',
          }}
        />
        <div className="row justify-content-between">
          <div className="col-md-6">
            <label className="mr-1">
              <span style={{ color: 'red' }}>*</span>Store group name:
            </label>
            <input
              type="text"
              value={this.state.storeGroupName}
              onChange={this.onStoreGroupNameChangeHandler}
            />
          </div>
          <div className="col-md-2">
            <button type="button" className="btn btn-primary" onClick={() => {}}>
              Upload &nbsp;&nbsp;
              <span>
                <img className="img-icons-create-promo" src={excelIcon} alt="" />
              </span>
            </button>
          </div>
        </div>
        <hr />
        <Filters
          hierarchy={['hierarchy1', 'hierarchy2', 'hierarchy3', 'hierarchy4']}
          // onFilter={this.onFilter}
        />
        <hr />
        <div className="row">
          {/* <div className="col-md-1">Year</div>
          <div className="col-md-2">
            <Select
              name="year"
              placeholder="Select Year"
              options={years}
              value={this.state.year}
              onChange={this.onYearChangeHandler}
            />
          </div>
          <div className="col-md-1">Month</div>
          <div className="col-md-2">
            <Select
              name="year"
              placeholder="Select Month"
              options={months}
              value={this.state.month}
              onChange={this.onMonthChangeHandler}
            />
          </div> */}
        </div>
        <div className="row" style={{ justifyContent: 'space-around' }}>
          <div className="col-md-5">
            <div>All Stores</div>
            <div style={{ height: '370px' }}>
              {/* <TableContextProvider
                value={{
                  tableComponent: {
                    colDefs: CreateStore.colDefs,
                    data: this.state.allStores,
                    autoSizeCol: true,
                    suppressRowTransform: true,
                    suppressRowClickSelection: true,
                    suppressCellSelection: true,
                    rowSelection: 'multiple',
                    onSelectionChanged: this.onAllStoresSelectionChangeHandler,
                  },
                }}>
                <TableComponent />
              </TableContextProvider> */}
            </div>
            <div>
              <button className="btn btn-primary" onClick={this.onAddClickHandler}>
                Add
              </button>
            </div>
          </div>
          <div className="col-md-5">
            <div>
              <span className="mr-2">Current Stores</span>
              <span className="mr-2">Stores: 0</span>
              {/* <span>Overall QC: 0</span> */}
            </div>
            <div style={{ height: '370px' }}>
              {/* <TableContextProvider
                value={{
                  colDefs: CreateStore.colDefs,
                  data: this.state.currentStores,
                  autoSizeCol: true,
                  suppressRowTransform: true,
                  suppressRowClickSelection: true,
                  suppressCellSelection: true,
                  rowSelection: 'multiple',
                  onSelectionChanged: this.onCurrentStoresSelectionChangeHandler,
                }}>
                <TableComponent />
              </TableContextProvider> */}
            </div>
            <div>
              <button className="btn btn-primary" onClick={this.onRemoveClickHandler}>
                Remove
              </button>
            </div>
          </div>
        </div>
        <div style={{ textAlign: 'center', marginTop: '1rem' }}>
          <button
            className="btn btn-primary"
            onClick={() => this.props.onCreateStoreGroup(this.state)}>
            Create
          </button>
          <button className="btn ml-1 cancel-button" onClick={this.props.onCancelButtonHandler}>
            Cancel
          </button>
        </div>
      </div>
    );
  }
}

export default CreateStoreGroup;
