import React, { Component } from 'react';
import Filters from '../../services/filters';
import Breadcrumbs from '../../services/breadcrumbs';
import SkuCockpit from '../../components/SKUCockpit/SKUCockpit';
import LineChart from '../support/Charts/LineChart';
import BarChart from '../support/Charts/Barchart';
import { connect } from 'react-redux';
import * as chartjs from 'chart.js';
import './index.css';
import reportingActions from '../../actions/reportingActions';
import manuallyCreateMdActions from '../../actions/manuallyCreateMdActions';

import LoadingOverlay from '../../utils/Loader';
import moment from 'moment';

import filterActions from '../../actions/filtersAction';

chartjs.pluginService.register({
  beforeDraw: function (chart) {
    if (chart.config.options && chart.config.options.backgroundColor) {
      var ctx = chart.chart.ctx;
      var chartArea = chart.chartArea;
      ctx.save();
      ctx.fillStyle = chart.config.options.backgroundColor;
      ctx.fillRect(
        chartArea.left,
        chartArea.top,
        chartArea.right - chartArea.left,
        chartArea.bottom - chartArea.top
      );
      ctx.restore();
    }
  },
});
class MarkdownReporting extends Component {
  sellThroughOptions = {
    backgroundColor: '#fff',
    scales: {
      xAxes: [
        {
          scaleLabel: {
            display: true,
            labelString: 'Period',
          },
        },
      ],
      yAxes: [
        {
          scaleLabel: {
            display: true,
            labelString: 'Sell through %',
          },
          ticks: {
            suggestedMin: 0, // minimum will be 0, unless there is a lower value.
          },
        },
      ],
    },
  };
  inventoryOptions = {
    backgroundColor: '#fff',
    scales: {
      xAxes: [
        {
          scaleLabel: {
            display: true,
            labelString: 'Period',
          },
        },
      ],
      yAxes: [
        {
          scaleLabel: {
            display: true,
            labelString: 'Units',
          },
          ticks: {
            suggestedMin: 0, // minimum will be 0, unless there is a lower value.
          },
        },
      ],
    },
  };
  marginOptions = {
    backgroundColor: '#fff',
    scales: {
      xAxes: [
        {
          scaleLabel: {
            display: true,
            labelString: 'Period',
          },
        },
      ],
      yAxes: [
        {
          scaleLabel: {
            display: true,
            labelString: 'Margin %',
          },
          ticks: {
            suggestedMin: 0, // minimum will be 0, unless there is a lower value.
          },
        },
      ],
    },
  };
  salesComparisonOptions = {
    legend: { display: false },
    backgroundColor: '#fff',
    scales: {
      xAxes: [
        {
          scaleLabel: {
            display: true,
            labelString: 'Period',
          },
        },
      ],
      yAxes: [
        {
          scaleLabel: {
            display: true,
            labelString: 'Units',
          },
          ticks: {
            suggestedMin: 0, // minimum will be 0, unless there is a lower value.
            beginAtZero: true, // minimum value will be 0.
          },
        },
      ],
    },
  };
  onFilter = (filters) => {
    let req = {
      filters: {
        hierarchy1: this.props.selectedHierarchy1,
        hierarchy2: this.props.selectedHierarchy2,
        hierarchy3: this.props.selectedHierarchy3,
        hierarchy4: this.props.selectedHierarchy4,
        hierarchy5: this.props.selectedHierarchy5,
        hierarchy6: this.props.selectedHierarchy6,
        hierarchy7: this.props.selectedHierarchy7,
        hierarchy8: this.props.selectedHierarchy8,
        tiers: this.props.selectedStores,
      },
      startDate: moment(this.props.startDate).format('YYYY-MM-DD'),
      endDate: moment(this.props.endDate).format('YYYY-MM-DD'),
      week: this.props.weeks,
    };
    this.props.getReportingData(req);
  };
  onCreateMarkdown = async (items) => {
    await this.props.setRetainFilters(true);
    this.props.history.push({
      pathname: '/vis/Markdown/create-markdown',
      state: { styles: items },
    });
  };

  render() {
    return (
      <React.Fragment>
        <div className="card">
          <Breadcrumbs module="Markdown Optimization" subModules={['Reporting Section']} />
          <hr className="hr-style" />

          <Filters
            drop
            stores
            dateRange
            hierarchy={[
              {
                type: 'hierarchy1',
                isMandatory: true,
                isMultiple: false,
              },
              {
                type: 'hierarchy2',
                isMandatory: true,
                isMultiple: true,
              },
              {
                type: 'hierarchy3',
                isMandatory: true,
                isMultiple: true,
              },
              {
                type: 'hierarchy4',
                isMandatory: true,
                isMultiple: true,
              },
              {
                type: 'hierarchy5',
                isMandatory: true,
                isMultiple: true,
              },
              {
                type: 'hierarchy6',
                isMandatory: false,
                isMultiple: true,
              },
              {
                type: 'hierarchy7',
                isMandatory: false,
                isMultiple: false,
              },
              {
                type: 'hierarchy8',
                isMandatory: false,
                isMultiple: false,
              },
            ]}
            onFilter={this.onFilter}
          />
          <hr className="hr-style mt-4 mb-4" />

          <LoadingOverlay loader={this.props.overlayLoaderState} spinner>
            {this.props.cockpitData && this.props.cockpitData.message ? (
              <div className="no-data">No data found. Please try with different filters.</div>
            ) : (
              <React.Fragment>
                <div className="row justify-content-center">
                  <div className="card col-md-5 ml-4 mb-4 mr-4 reporting-chart-container">
                    Sell through
                    <div>
                      <LineChart
                        data={this.props.reportingData.sellThroughData}
                        options={this.sellThroughOptions}></LineChart>
                    </div>
                  </div>
                  <div className="card col-md-5 ml-4 mb-4 reporting-chart-container">
                    Inventory
                    <div>
                      <LineChart
                        data={this.props.reportingData.inventoryData}
                        options={this.inventoryOptions}></LineChart>
                    </div>
                  </div>
                  <div className="card col-md-5 ml-4 mb-4 mr-4 reporting-chart-container">
                    Margin
                    <div>
                      <LineChart
                        data={this.props.reportingData.marginData}
                        options={this.marginOptions}></LineChart>
                    </div>
                  </div>
                  <div className="card col-md-5 ml-4 mb-4 reporting-chart-container">
                    Sales comparison
                    <div>
                      <BarChart
                        data={this.props.reportingData.salesData}
                        options={this.salesComparisonOptions}></BarChart>
                    </div>
                  </div>
                </div>
              </React.Fragment>
            )}
          </LoadingOverlay>
        </div>
        {this.props.cockpitData.length && (
          <div className="card mt-4">
            <SkuCockpit
              cockpitData={this.props.cockpitData}
              onCreateMarkdown={this.onCreateMarkdown}
            />
          </div>
        )}
      </React.Fragment>
    );
  }
}
const mapStateToProps = (store) => {
  return {
    overlayLoaderState: store.loaderReducer.overlayLoaderState,
    reportingData: store.reportingReducer.reportingData,
    cockpitData: store.reportingReducer.cockpitData,
    selectedHierarchy1: store.filtersReducer.selectedHierarchy1,
    selectedHierarchy2: store.filtersReducer.selectedHierarchy2,
    selectedHierarchy3: store.filtersReducer.selectedHierarchy3,
    selectedHierarchy4: store.filtersReducer.selectedHierarchy4,
    selectedHierarchy5: store.filtersReducer.selectedHierarchy5,
    selectedHierarchy6: store.filtersReducer.selectedHierarchy6,
    selectedHierarchy7: store.filtersReducer.selectedHierarchy7,
    selectedHierarchy8: store.filtersReducer.selectedHierarchy8,
    selectedStores: store.filtersReducer.selectedStores,
    startDate: store.filtersReducer.startDate,
    endDate: store.filtersReducer.endDate,
    weeks: store.filtersReducer.weeks,
  };
};
const mapActionsToProps = {
  getReportingData: reportingActions.getReportingData,
  setRetainFilters: filterActions.setRetainFilters,
};
export default connect(mapStateToProps, mapActionsToProps)(MarkdownReporting);
