import React, { Component } from 'react';
import { connect } from 'react-redux';
import Filters from '../../../services/filters';
import DataTable from '../../../services/reactTable';
import ColumnSelection from '../../columnSelection';
import Coldef from './coldef';
import styled from 'styled-components';
import _ from 'lodash';
import cloneDeep from 'clone-deep';
import RowData from './data';
import moment from 'moment';
import NoData from '../../noTableData';

class Draft extends Component {
  constructor(props) {
    super(props);
    this.state = {
      selectedItems: [],
      tableData: RowData,
      columnOptions: [],
      selLabels: [],
      columns: [],
    };
  }

  componentDidMount() {
    this.props.getMarkdowns({ status: 'draft' });
  }

  onCellClick = (row) => {
    console.log(row, 'cell clicked .....');
  };

  findStatusColor = (status, color) => {
    switch (status) {
      case 'high':
        return color.high;
      case 'medium':
        return color.medium;
      case 'low':
        return color.low;
      default:
        break;
    }
  };

  bgmCol = (cellInfo) => {
    if (cellInfo.column.id === 'md_status') var status = cellInfo.row.original.md_status;
    let bgCol = {
      high: '#F8E8E8',
      medium: '#FCF3D1',
      low: '#E8F8F5',
    };
    return this.findStatusColor(status, bgCol);
  };
  txtCol = (cellInfo) => {
    if (cellInfo.column.id === 'md_status') var status = cellInfo.row.original.md_status;
    let txtCol = {
      high: '#D38485',
      medium: '#F0C53A',
      low: '#66CFBA',
    };
    return this.findStatusColor(status, txtCol);
  };

  updateMyData = (rowIndex, columnId, value) => {
    // We also turn on the flag to not reset the page
    this.setState((old) => {
      let val = old.tableData.map((row, index) => {
        if (index === rowIndex) {
          return {
            ...row,
            [columnId]: value,
          };
        }
        return row;
      });
      return { tableData: val };
    });
  };

  onFilter = (selectedFilters) => {
    let payload = { ...selectedFilters };
    payload.startDate = payload.startDate && moment(payload.startDate).format('YYYY-MM-DD');
    payload.endDate = payload.endDate && moment(payload.endDate).format('YYYY-MM-DD');
    payload['week'] = '';
    payload['status'] = 'draft';
    this.props.getMarkdowns(payload);
  };

  onHandleCheckBox = (e, label, index) => {
    //adding
    if (e.target.checked) {
      if (this.state.selLabels.indexOf(label) === -1) {
        let labels = [...this.state.selLabels, label];
        let tempArr = [];
        labels.forEach((label) => {
          let columns = this.props.columnHeaders[label];
          let options = columns.map((label) => {
            return {
              label: label,
              value: label,
            };
          });
          tempArr.push(...options);
        });
        this.setState({
          selLabels: labels,
          columnOptions: tempArr,
        });
      }
    } else {
      //removing
      let index = this.state.selLabels.indexOf(label);
      if (index !== -1) {
        let selectedLabel = cloneDeep(this.state.selLabels);
        selectedLabel.splice(index, 1);
        let tempArr = [];
        selectedLabel.forEach((label) => {
          let columns = this.props.columnHeaders[label];
          let options = columns.map((label) => {
            return {
              label: label,
              value: label,
            };
          });
          tempArr.push(...options);
        });
        this.setState({
          selLabels: selectedLabel,
          columnOptions: tempArr,
        });
      }
    }
  };
  onHandleSelect = (selColOpt) => {
    //finding diff on adding option
    let onAddDiff = _.differenceWith(selColOpt, this.state.selectedItems, _.isEqual);
    //finding diff on removing option
    let onRemoveDiff = _.differenceWith(this.state.selectedItems, selColOpt, _.isEqual);

    //Adding selected option and its respective column to the table
    if (onAddDiff.length) {
      //selected item label
      let item = onAddDiff[0].label;
      //finding the selected column from the main column definitions
      let findSelColFromMain = Coldef().filter((col) => col.Header === item);

      let currentCols = _.cloneDeep(this.state.columns);
      //adding selected column before actions column
      currentCols.splice(currentCols.length - 1, 0, findSelColFromMain[0]);
      this.setState({
        selectedItems: [...selColOpt],
        columns: currentCols,
      });
    }
    //Removing selected option and its respective column from the table
    if (onRemoveDiff.length) {
      //selected item label
      let item = onRemoveDiff[0].label;
      let itemIndex = this.state.selectedItems.map((item) => item.label).indexOf(item);
      let colIndex = this.state.columns.map((col) => col.Header).indexOf(item);
      let currentItems = _.cloneDeep(this.state.selectedItems);
      currentItems.splice(itemIndex, 1);
      let currentCols = _.cloneDeep(this.state.columns);
      currentCols.splice(colIndex, 1);
      this.setState({
        selectedItems: currentItems,
        columns: currentCols,
      });
    }
  };

  render() {
    return (
      <div className="createMd pl-2 pr-2">
        <div className="card mb-4">
          <div>
            <h4 className="font-size-14  pad-10 markdown-heading">
              Markdown Optimization /<span className="font-bold color-blue">Draft</span>
            </h4>
          </div>
          <hr style={{ marginTop: '1px' }} />
          <Filters
            onFilter={this.onFilter}
            drop
            stores
            dateRange
            hierarchy={[
              {
                type: 'hierarchy1',
                isMandatory: true,
                isMultiple: false,
              },
              {
                type: 'hierarchy2',
                isMandatory: true,
                isMultiple: true,
              },
              {
                type: 'hierarchy3',
                isMandatory: true,
                isMultiple: true,
              },
              {
                type: 'hierarchy4',
                isMandatory: true,
                isMultiple: true,
              },
              {
                type: 'hierarchy5',
                isMandatory: true,
                isMultiple: true,
              },
              {
                type: 'hierarchy6',
                isMandatory: false,
                isMultiple: true,
              },
              {
                type: 'hierarchy7',
                isMandatory: false,
                isMultiple: false,
              },
              {
                type: 'hierarchy8',
                isMandatory: false,
                isMultiple: false,
              },
            ]}
          />
        </div>
        {this.props.markdowns && this.props.markdowns.length ? (
          <div className="card CalendarMain promoCalendarDiv1 mt-3">
            <ColSelBtn>
              {this.props.columnHeaders && Object.keys(this.props.columnHeaders).length && (
                <ColumnSelection
                  onHandleCheckBox={this.onHandleCheckBox}
                  columnHeaders={this.props.columnHeaders}
                  onHandleSelect={this.onHandleSelect}
                  columnOptions={this.state.columnOptions}
                  selectedItems={this.state.selectedItems}
                  selLabels={this.state.selLabels}
                />
              )}
              <div style={{ padding: '1rem' }}>
                <button className="btn btn-primary">Send for Approval</button>
                &nbsp;
              </div>
            </ColSelBtn>
            <DataTable
              columns={this.props.columns}
              data={this.props.markdowns}
              updateMyData={this.updateMyData}
              visibleColumns="markDownDrafts"
              isPagination={true}
              NoDataComponent="This is the dynamic title"
              getColumnProps={(column) => ({
                // onClick: () => this.onColumnClick(column),
              })}
              getCellProps={(cellInfo) => ({
                onClick: () => this.onCellClick(cellInfo),
                style: {
                  backgroundColor: this.bgmCol(cellInfo),
                  color: this.txtCol(cellInfo),
                  // textAlign: "center",
                  margin: '4px 4px',
                  borderRadius: '2px',
                },
              })}
            />
          </div>
        ) : (
          <NoData description="Please select filters to show Markdown plans." />
        )}
      </div>
    );
  }
}

const mapStateToProps = (store) => {
  return {
    markdowns: store.markDownReducers.markdowns,
    columnHeaders: store.markDownReducers.columnHeaders,
  };
};

const mapDispatchToProps = {};

export default connect(mapStateToProps, mapDispatchToProps)(Draft);

const ColSelBtn = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: space-between;
`;
