import React from "react";
import PropTypes from "prop-types";

const checkboxes = [
  {
    name: "TV",
    key: "TV",
    label: "ISS"
  },
  {
    name: "Flyer",
    key: "Flyer",
    label: "Direct Mail"
  },
  {
    name: "Radio",
    key: "Radio",
    label: "Tab"
  },
  {
    name: "Social Media",
    key: "Social Media",
    label: "Weekend event"
  },
  // {
  //   name: 'FOC',
  //   key: 'FOC',
  //   label: 'FOC'
  // },
  // {
  //   name: 'BBM',
  //   key: 'BBM',
  //   label: 'BBM'
  // }
];

const Checkbox = ({ type = "checkbox", name, checked = false, onChange }) => (
  <input type={type} name={name} checked={checked} onChange={onChange} className="custom-checkbox-style" />
);

class CheckboxContainer extends React.Component {
  render() {
    return (
      <div>
        {checkboxes.map(item => (
          <label key={item.key} className="ml-3">
            <Checkbox
              name={item.name}
              checked={this.props.checkedItems.get(item.name)}
              onChange={this.props.handleCheckChange}
            />
            <span className="ml-1">{item.label}</span>
          </label>
        ))}
      </div>
    );
  }
}

export default CheckboxContainer;
