import React, { Component } from 'react';
import StoreConfig from './StoreConfigContainers/StoreConfig';
import CreateStoreGroup from './StoreConfigContainers/CreateStoreGroup';
import Modal from 'react-bootstrap/Modal';
import { connect } from 'react-redux';
import LoadingOverlay from '../../utils/Loader';
import StoreConfigActions from '../../actions/storeConfigActions';
const CONFIG = 'storeConfig';
const CREATE_STORE_GROUP = 'createStoreGroup';

export class StoreConfiguration extends Component {
  constructor(props) {
    super(props);
    this.state = {
      compToRender: this.props.history.location.pathname.includes('create-store')
        ? CREATE_STORE_GROUP
        : CONFIG,
      isEditStoreGroup: false,
      showPopup: false,
      createStoreData: null,
    };
  }

  onCreateStoreGroupHandler = (isEditStoreGroup = false) => {
    console.log('sad23irw', isEditStoreGroup);
    this.setState({
      compToRender: CREATE_STORE_GROUP,
      isEditStoreGroup,
    });
  };

  onCreateStoreGroup = (data) => {
    console.log('sadfa', data);
    this.setState({
      showPopup: true,
      createStoreData: data,
    });
  };

  onCancelButtonHandler = () => {
    if (this.props.history.location.pathname.includes('create-store')) {
      this.props.history.goBack();
    } else {
      this.setState({
        compToRender: CONFIG,
      });
    }
  };

  onPopupClose = () => {
    this.setState({
      showPopup: false,
    });
  };

  onConfirmHandler = () => {
    this.setState({
      showPopup: false,
      createStoreData: null,
    });
  };

  renderComponent = (type) => {
    switch (type) {
      case 'storeConfig':
        return (
          <LoadingOverlay loader={this.props.getOverlayLoaderState} spinner>
            <StoreConfig
              storeGroupTableData={this.props.storeGroupTableData}
              getStoreGroupTableData={this.props.getStoreGroupTableData}
              storeGroups={this.props.storeGroups}
              fetchStoreGroups={this.props.fetchStoreGroups}
              onCreateStoreGroupHandler={this.onCreateStoreGroupHandler}
            />
          </LoadingOverlay>
        );
      case 'createStoreGroup':
        return (
          <LoadingOverlay loader={this.props.getOverlayLoaderState} spinner>
            <CreateStoreGroup
              getStoreGroupTableData={this.props.getStoreGroupTableData}
              storeGroupTableData={this.props.storeGroupTableData}
              createStoreGroup={this.props.createStoreGroup}
              onCreateStoreGroup={this.onCreateStoreGroup}
              onCancelButtonHandler={this.onCancelButtonHandler}
              isEditStoreGroup={this.state.isEditStoreGroup}
            />
          </LoadingOverlay>
        );
      default:
        return null;
    }
  };

  render() {
    return (
      <>
        {this.renderComponent(this.state.compToRender)}
        <Modal size="lg" show={this.state.showPopup} onHide={this.onPopupClose}>
          <Modal.Body>
            <div>Save Changes?</div>
            <div>changes will be reflected within 24hrs</div>
          </Modal.Body>
          <Modal.Footer>
            <button className="btn btn-primary" onClick={this.onConfirmHandler}>
              Yes
            </button>
            <button className="btn btn-primary" onClick={this.onPopupClose}>
              No
            </button>
          </Modal.Footer>
        </Modal>
      </>
    );
  }
}

const mapStateToProps = (store) => {
  return {
    getOverlayLoaderState: store.loaderReducer.overlayLoaderState,
    getOverlayLoaderText: store.loaderReducer.loaderText,
    storeGroups: store.storeConfigReducer.storeGroups,
    storeGroupTableData: store.storeConfigReducer.storeGroupTableData,
  };
};
const mapDispatchToProps = {
  fetchStoreGroups: StoreConfigActions.fetchStoreGroups,
  getStoreGroupTableData: StoreConfigActions.getStoreGroupTableData,
  createStoreGroup: StoreConfigActions.createStoreGroup,
};
export default connect(mapStateToProps, mapDispatchToProps)(StoreConfiguration);
