import axios from 'axios';
import { config } from '../utils/config';

const manuallyCreateMdActions = {
  fetchInventoryInfo: (reqObj) => {
    return (dispatch) => {
      axios({
        url: `${config.createMarkdownURL}/getInventoryInfo/`,
        method: 'POST',
        data: reqObj,
      })
        .then((resp) => {
          dispatch({
            type: 'GET_INVENTORY_INFO',
            payload: resp.data.data,
          });
        })
        .catch((err) => {
          console.log(err);
        });
    };
  },
  fetchCadence: (reqObj) => {
    return (dispatch) => {
      dispatch({
        type: 'LOADER',
      });
      axios({
        // url: `${config.createMarkdownURL}/generateCadence/`,
        url: `${config.cadenceUrl}/MarkdownOptimization`,
        method: 'POST',
        data: reqObj,
      })
        .then((resp) => {
          let skus = resp.data.skus.map((sku) => {
            return {
              label: sku,
              value: sku,
            };
          });
          dispatch({
            type: 'LOADER',
          });
          dispatch({
            type: 'GET_CADENCE_DATA',
            payload: [resp.data.cadenceData],
            csv_names: resp.data.csv_names,
            skus: skus,
          });
          dispatch({
            type: 'GET_MARKDOWNANALYSIS_GRAPH_DATA',
            payload: resp.data.simulateData,
          });
          dispatch({
            type: 'SET_ANALYSIS_TABLE_DATA',
            payload: resp.data.simulateData.analysisTabledata,
          });
        })
        .catch((err) => {
          console.log(err);
          dispatch({
            type: 'LOADER',
          });
        });
    };
  },

  simulate: (reqObj) => {
    return (dispatch) => {
      axios({
        url: `${config.cadenceUrl}/CalculationData`,
        method: 'POST',
        data: reqObj,
      })
        .then((resp) => {
          dispatch({
            type: 'GET_MARKDOWNANALYSIS_GRAPH_DATA',
            payload: resp.data,
          });
          dispatch({
            type: 'SET_ANALYSIS_TABLE_DATA',
            payload: resp.data,
          });
        })
        .catch((err) => {
          console.log(err);
        });
    };
  },
  sendApproval: (reqObj) => {
    return (dispatch) => {
      axios({
        url: '/sendapproval',
        method: 'POST',
        data: reqObj,
      })
        .then((resp) => {
          dispatch({
            type: 'GET_INVENTORY_INFO',
            payload: resp.data,
          });
        })
        .catch((err) => {
          console.log(err);
        });
    };
  },
  sendDrafts: (reqObj) => {
    return (dispatch) => {
      axios({
        url: '/senddrafts',
        method: 'POST',
        data: reqObj,
      })
        .then((resp) => {
          dispatch({
            type: 'GET_INVENTORY_INFO',
            payload: resp.data,
          });
        })
        .catch((err) => {
          console.log(err);
        });
    };
  },
};
export default manuallyCreateMdActions;
