import React, { Component, Fragment } from 'react';
import Filters from '../../services/filters';
import MarkdownAnalysis from '../MarkdownAnalysis';
import InventoryInfo from './InventoryInfo';
import Constraints from './Constraints';
import CreateCadence from './CreateCadence';
import Modal from 'react-awesome-modal';
import { withRouter } from 'react-router-dom';
import Select from 'react-select';
import { connect } from 'react-redux';
import LoadingOverlay from '../../utils/Loader';
import _ from 'lodash';
import GetMarkdownPercentageHeader from './GetMarkdownPercentageHeader';
import manuallyCreateMdActions from '../../actions/manuallyCreateMdActions.js';
import './index.css';
import moment from 'moment';
import { Button, Tabs, Tab } from 'react-bootstrap';
import MarkdownDetailedTable from './MardownDetailedTable';
import cloneDeep from 'clone-deep';
import ComparisonTable from './ComparisonTable';
import ComparisonMetricTable from './ComparisonMetricTable';
import classNames from 'classnames';
import ExcludeStoreTable from './excludeStoreTable';
// import config from '../../utils/config';
import PlanSavePopup from './planSavePopup';
class ManuallyCreateMD extends Component {
  constructor(props) {
    super(props);
    this.state = {
      saveCadenceRequest: {},
      selectedSkuId: { label: null, value: null },
      targetSellThroughVal: null,
      markdownTypeVal: false,
      cadenceConstraintsVal: 'overall',
      marginVal: null,
      overallCadenceData: [],
      skuLabelCadenceData: [],
      offset: 10,
      cadenceData: ['5.0', '10.0', '15.0', '20.0', '25.0', '30.0', '35.0', '40.0', '45.0', '50.0'],
      currentPage: 1,
      filters: ['hierarchy1', 'hierarchy2', 'hierarchy3', 'hierarchy4', 'hierarchy5', 'hierarchy6'],
      filteredData: {},
      totalInventory: 0,
      currentInventory: 0,
      totalStores: 0,
      selectedStores: 0,
      selectedView: 'Overview',
      selectedSku: [],
      showExcludeStorePopup: false,
      showPlanSavePopup: false,
      TabTitles: ['Optimal'],
      defaultActiveKey: 'Optimal',
      comparisonData: [],
      showCadence: false,
      simulatedData: false,
      comparisonRow: [],
      status: 'pending',
      md_level: 'week',
    };
  }
  selectedSkuHandler = (val) => {
    this.callAPISimulate(val.value);
    this.setState({ selectedSkuId: val.value });
  };
  compariosnCheckbox = (comparisonRowVal) => {
    this.setState({ comparisonRow: comparisonRowVal });
  };
  onComparisonInputChange = (data, changedRows) => {
    let cadence = cloneDeep(this.state.overallCadenceData);
    cadence[this.state.TabTitles.indexOf('Comparison')] = data;
    this.setState({
      overallCadenceData: cadence,
    });
  };
  onUpdateMarkdownPercentage = (data, index) => {
    if (this.state.cadenceConstraintsVal === 'overall') {
      let data1 = cloneDeep(this.state.overallCadenceData);
      data1[index] = data;
      this.setState({
        overallCadenceData: data1,
      });
    } else {
      let data1 = cloneDeep(this.state.skuLabelCadenceData);
      data1[index] = data;
      this.setState({
        skuLabelCadenceData: data1,
      });
    }
  };
  getComparsionHeader = (header) => {
    if (this.state.cadenceConstraintsVal === 'sku' && header === 'Comparison') {
      let optimalData = this.state.skuLabelCadenceData[0];
      let options = optimalData.map((data) => {
        return { label: data.SKU_Id, value: data.SKU_Id };
      });
      return (
        <div className="row skuLabelStyle">
          <label className="skuLabel">SKU/SKU Group</label>
          <div className="col-md-2">
            <Select name="category" options={options} />
          </div>
        </div>
      );
    }
    return (
      <div className="row justify-content-center skuLabelStyle">
        <label className="font-size-14 markdown-percentage-heading ">Markdown Percentages</label>
      </div>
    );
  };
  getCadenceData = (index) => {
    if (this.state.cadenceConstraintsVal === 'overall') return this.state.overallCadenceData[index];
    return this.state.skuLabelCadenceData[index];
  };
  getTabTag = (tabTitle, index) => {
    console.log(
      'this.state.overallCadenceData[index]:',
      this.state.overallCadenceData,
      index,
      this.state.overallCadenceData[index]
    );
    if (tabTitle === 'Comparison') {
      if (this.state.cadenceConstraintsVal === 'sku') {
        return (
          <div>
            <ComparisonMetricTable
              showCheckBoxComparsionTable={true}
              data={this.props.comparisonData}
            />
          </div>
        );
      } else {
        return (
          <ComparisonTable
            compariosnCheckbox={(comparisonRowVal, index) =>
              this.compariosnCheckbox(comparisonRowVal, index)
            }
            data={this.state.overallCadenceData[index]}
            updatedData={this.onComparisonInputChange}
          />
        );
      }
    } else {
      return (
        <div className="cadencetable">
          <CreateCadence
            overallCadenceData={this.getCadenceData(index)}
            onUpdateMarkdownPercentage={this.onUpdateMarkdownPercentage}
            addScenario={this.addScenario}
            selectedSku={this.state.selectedSku}
            key={tabTitle}
            index={index}
            activeTab={this.state.defaultActiveKey}
          />
        </div>
      );
    }
  };
  checkTabIsCompasionAndSkuType = (tabTitle) => {
    return this.state.cadenceConstraintsVal === 'sku' && tabTitle === 'Comparison';
  };
  isSKUType = (header) => {
    return this.state.cadenceConstraintsVal === 'sku';
  };
  isNotComparsionTab = (tabTitle) => {
    return tabTitle !== 'Comparison';
  };
  getTabTable = (activeTab, index) => {
    if (this.isNotComparsionTab()) {
      return (
        <>
          <GetMarkdownPercentageHeader />
          <div className="cadencetable">
            <CreateCadence
              overallCadenceData={this.getCadenceData(index)}
              onUpdateMarkdownPercentage={this.onUpdateMarkdownPercentage}
              addScenario={this.addScenario}
              selectedSku={this.state.selectedSku}
              key={activeTab}
              index={index}
              activeTab={this.state.defaultActiveKey}
            />
          </div>
        </>
      );
    } else {
      if (this.isSKUType(activeTab)) {
        let optimalData = this.state.skuLabelCadenceData[0];
        let options = optimalData.map((data) => {
          return { label: data.SKU_Id, value: data.SKU_Id };
        });
        return (
          <>
            <div className="row skuLabelStyle">
              <label className="skuLabel">SKU/SKU Group</label>
              <div className="col-md-2">
                <Select name="category" options={options} />
              </div>
            </div>
            <ComparisonMetricTable
              showCheckBoxComparsionTable={true}
              data={this.props.comparisonData}
            />
          </>
        );
      } else {
        return (
          <ComparisonTable
            compariosnCheckbox={(comparisonRowVal, index) =>
              this.compariosnCheckbox(comparisonRowVal, index)
            }
            data={this.state.overallCadenceData[index]}
            updatedData={this.onComparisonInputChange}
          />
        );
      }
    }
  };
  getTab() {
    return this.state.TabTitles.map((tabTitle, index) => {
      return (
        <Tab eventKey={tabTitle} title={tabTitle} key={tabTitle} style={{ textAlign: 'center' }}>
          {this.getTabTable(tabTitle, index)}
        </Tab>
      );
    });
  }
  onSelectTabs = (key) => {
    this.setState(
      {
        defaultActiveKey: key,
      },
      () => {
        this.callAPISimulate();
      }
    );
  };
  addTitleInTabs = () => {
    //checl comparison is there or not if yes -1
    let TabTitles = this.state.TabTitles;
    let comparisonTitleIndex = TabTitles.indexOf('Comparison');
    let newSceniosIndex = TabTitles.length;
    let defaultSceniosTitle = `Scenario 0${TabTitles.length}`;
    if (comparisonTitleIndex !== -1) {
      //comparsion present
      newSceniosIndex = comparisonTitleIndex;
      defaultSceniosTitle = `Scenario 0${newSceniosIndex}`;
      TabTitles.splice(newSceniosIndex, 0, defaultSceniosTitle);
    } else {
      TabTitles.splice(newSceniosIndex, 0, defaultSceniosTitle);
    }
    this.setState({
      TabTitles,
      defaultActiveKey: defaultSceniosTitle,
    });
    this.addContentsInTabs(newSceniosIndex);
  };
  addContentsInTabs = (newSceniosIndex) => {
    let data = this.getCadenceData(newSceniosIndex);
    data.splice(newSceniosIndex, 0, data[0]);
    let obj = {
      skuLabelCadenceData: data,
    };
    if (this.state.cadenceConstraintsVal === 'overall') {
      obj = {
        overallCadenceData: data,
      };
    }
    this.setState(obj);
  };
  addScenario = () => {
    this.addTitleInTabs();
  };

  generateCadence = () => {
    this.setState({
      showCadence: true,
      simulatedData: true,
    });
    let request = cloneDeep(this.state.filteredData);
    let inputData = {
      cadenceData: this.state.cadenceData,
      inventorySelected: '',
      storeSelected: '',
      excludeStore: this.state.excludedStores,
      sellThrough: this.state.targetSellThroughVal || '',
      margin: this.state.marginVal || '',
      markdownType: this.state.markdownTypeVal || '',
      cadenceType: this.state.cadenceConstraintsVal || 'overall',
      startDate: moment(this.props.startDate).format('YYYY-MM-DD'),
      endDate: moment(this.props.endDate).format('YYYY-MM-DD'),
    };

    let saveCadenceRequest = { ...request, ...inputData };
    this.setState({
      saveCadenceRequest,
    });
    this.props.fetchCadence(saveCadenceRequest);
  };
  checkComparsionDataPresent = (data) => {
    return data.indexOf('Comparison') !== -1 ? true : false;
  };
  updateComparsionDataInTab = (tabTitles) => {
    let comparsionData = [];
    // this.state.TabTitles.indexOf('Comparsion')
    for (let i = 0; i < tabTitles.length - 1; i++) {
      if (this.state.cadenceConstraintsVal === 'overall') {
        comparsionData.push({
          scenarios: tabTitles[i],
          ...this.state.overallCadenceData[i][0],
        });
      } else {
        let rows = cloneDeep(this.state.skuLabelCadenceData[i][0]);
        delete rows['SKU ID'];
        delete rows['MD Status'];
        comparsionData.push({
          scenarios: tabTitles[i],
          ...rows,
        });
      }
    }
    let data = [];
    let minusIndex = 0;
    if (this.checkComparsionDataPresent(this.state.TabTitles)) {
      minusIndex = 1;
    }
    if (this.state.cadenceConstraintsVal === 'overall') {
      data = cloneDeep(this.state.overallCadenceData);
      data.splice(data.length - minusIndex, 1, comparsionData);
      this.setState({
        overallCadenceData: data,
        simulatedData: true,
      });
    } else {
      data = cloneDeep(this.state.skuLabelCadenceData);
      data.splice(data.length - minusIndex, 1, comparsionData);
      this.setState({
        skuLabelCadenceData: data,
        simulatedData: true,
      });
    }
  };
  onSimulate = () => {
    //adding comparsion title
    console.log('this.state.overallCadenceData:', this.state.overallCadenceData);
    let tabTitles = cloneDeep(this.state.TabTitles);
    if (this.state.TabTitles.indexOf('Comparison') === -1) {
      let array = [...this.state.TabTitles, 'Comparison'];
      tabTitles = array;
      this.setState({
        TabTitles: array,
      });
    }
    this.updateComparsionDataInTab(tabTitles);
    this.callAPISimulate();
  };
  callAPISimulate = (skus = 'all') => {
    let index = this.state.TabTitles.indexOf(this.state.defaultActiveKey);
    let array = [];

    if (this.state.defaultActiveKey === 'Comparison') {
      let cadence = this.getCadenceData();
      let lastComparisonArray = cadence[cadence.length - 1];
      for (let i = 1; i < lastComparisonArray.length; i++) {
        let scenios = cloneDeep(lastComparisonArray[i]);
        delete scenios.scenarios;
        cadence[i] = [scenios];
      }
      console.log('final cadence:', cadence);
      this.setState({
        overallCadenceData: cadence,
      });
      let data = cadence[this.state.TabTitles.indexOf('Comparison')];
      for (let i = 1; i < data.length; i++) {
        let row = cloneDeep(data[i]);
        delete row.scenarios;
        this.props.simulate(
          {
            csv_names: this.props.csv_names,
            skus: 'all',
            cadenceType: this.state.cadenceConstraintsVal,
            scenario: 'true',
            scenario_data: [row],
          },
          this.state.defaultActiveKey
        );
      }
    } else {
      array = this.getCadenceData(index);

      this.props.simulate(
        {
          csv_names: this.props.csv_names,
          skus,
          cadenceType: this.state.cadenceConstraintsVal,
          scenario: 'true',
          scenario_data: array,
        },
        this.state.defaultActiveKey
      );
    }
  };
  changedView = (view) => {
    this.setState({ selectedView: view });
  };
  // selectedSkuHandler = (val) => {
  //   this.setState({ selectedSkuId: val });
  // };
  onExcludeStores = () => {
    this.setState({ showExcludeStorePopup: true });
  };
  showPlanSavePopup = () => {
    this.setState({ showPlanSavePopup: false });
  };
  excludeStoreValHandler = (selectedRowId) => {
    let inv = 0;
    let rowIds = Object.values(selectedRowId);
    rowIds.forEach((obj) => {
      inv += obj.inventory;
    });
    this.setState({
      excludedStores: selectedRowId,
      currentInventory: this.state.currentInventory - inv,
      selectedStores: this.state.selectedStores - rowIds.length,
    });
    console.log(selectedRowId, 'selectedRowId');
  };
  showTablePopup = () => {
    this.setState({ showExcludeStorePopup: false });
  };
  getWeekRange = (startWeek, endWeek) => {
    let arr = [];
    for (let i = startWeek; i <= endWeek; i++) {
      arr.push(i);
    }
    return arr;
  };

  onFilter = () => {
    let startWeek = moment(this.props.startDate, 'MM-DD-YYYY').week();
    let endWeek = moment(this.props.endDate, 'MM-DD-YYYY').week();
    let weeks = this.getWeekRange(startWeek, endWeek);
    let payload = {
      filters: {
        drop: this.props.drops,
        hierarchy1: this.props.selectedHierarchy1,
        hierarchy2: this.props.selectedHierarchy2,
        hierarchy3: this.props.selectedHierarchy3,
        hierarchy4: this.props.selectedHierarchy4,
        hierarchy5: this.props.selectedHierarchy5,
        hierarchy6: this.props.selectedHierarchy6,
        stores: this.props.selectedStores,
      },
      startDate: moment(this.props.startDate).format('YYYY-MM-DD'),
      endDate: moment(this.props.endDate).format('YYYY-MM-DD'),
      md_level: this.state.md_level,
      md_level_no_range: weeks,
      week: weeks,
      startWeek,
      endWeek,
      inventorySelected: '',
      storeSelected: '',
      excludeStore: '',
      sellThrough: '',
      margin: '',
      markdownType: '',
      cadenceType: 'overall',
      page: this.state.currentPage,
      offset: this.state.offset,
    };
    this.setState({
      filteredData: payload,
    });
    this.props.fetchInventoryInfo(payload);
  };

  constraintsCadenceHandler = (value) => {
    this.setState({
      cadenceConstraintsVal: value,
      showCadence: false,
      TabTitles: ['Optimal'],
      defaultActiveKey: 'Optimal',
    });
  };
  markdownTypeHandler = (val) => {
    this.setState({
      markdownTypeVal: val,
    });
  };
  marginValueHandler = (e) => {
    this.setState({
      marginVal: e.target.value,
    });
  };
  targetSellThrough = (e) => {
    this.setState({
      targetSellThroughVal: e.target.value,
    });
  };
  componentDidUpdate(prevProps, precState) {
    if (this.props.cadenceData !== prevProps.cadenceData) {
      this.state.cadenceConstraintsVal === 'overall' &&
        this.setState({
          overallCadenceData: cloneDeep(this.props.cadenceData),
        });
      this.state.cadenceConstraintsVal === 'sku' &&
        this.setState({
          skuLabelCadenceData: cloneDeep(this.props.cadenceData),
        });
    }
    if (!_.isEqual(prevProps.inventoryInfoData, this.props.inventoryInfoData)) {
      let totalInv = this.props.inventoryInfoData && this.props.inventoryInfoData.totalInv;
      let totalStores =
        this.props.inventoryInfoData && this.props.inventoryInfoData.storeData.length;
      this.setState({
        totalInventory: totalInv,
        currentInventory: totalInv,
        selectedStores: totalStores,
        totalStores: totalStores,
      });
    }
    console.log(precState.overallCadenceData, this.state.overallCadenceData);
    console.log(this.state.overallCadenceData === precState.overallCadenceData);
  }
  onSave = (planName, sceniosNames) => {
    let index = this.getActiveIndex();
    let obj = cloneDeep(this.state.saveCadenceRequest);
    obj.title = planName;
    obj.status = this.state.status;
    obj.cadenceData = {};
    obj.simulatedData = {};

    if (this.state.defaultActiveKey === 'Comparison') {
      sceniosNames.forEach((sceniosObject) => {
        let temp = sceniosObject.value;
        obj.simulatedData[temp] = this.props.analysisData[sceniosObject.scenarios]
          ? this.props.analysisData[sceniosObject.scenarios].saveData
          : [];
        delete sceniosObject.scenarios;
        delete sceniosObject.label;
        delete sceniosObject.value;
        obj.cadenceData[temp] = [sceniosObject];
      });
    } else {
      let key = sceniosNames[0].value || this.state.defaultActiveKey;
      obj.simulatedData[key] = this.props.analysisData[this.state.defaultActiveKey]
        ? this.props.analysisData[this.state.defaultActiveKey].saveData
        : [];
      let data =
        this.state.cadenceConstraintsVal === 'overall'
          ? this.state.overallCadenceData[index]
          : this.state.skuLabelCadenceData[index];
      obj.cadenceData[key] = data;
    }
    obj.startDate = moment(this.props.startDate).format('YYYY-MM-DD');
    obj.endDate = moment(this.props.endDate).format('YYYY-MM-DD');
    this.setState({
      showPlanSavePopup: false,
    });
    this.props.saveData(obj);
  };
  getActiveIndex = () => {
    return this.state.TabTitles.indexOf(this.state.defaultActiveKey);
  };
  render() {
    return (
      <LoadingOverlay
        loader={this.props.getOverlayLoaderState}
        spinner
        // text={this.props.getOverlayLoaderText}
      >
        <div className="createMd pl-2 pr-2">
          <div className="card mb-4">
            <div>
              <h4 className="font-size-14  pad-10 markdown-heading">
                Markdown Optimization /
                <span className="font-bold color-blue">Manually Create MD</span>
              </h4>
            </div>
            <hr
              className="mb-2"
              style={{
                marginTop: '-4px',
              }}
            />
            <Filters hierarchy={this.state.filters} onFilter={() => this.onFilter()} drop />
            {/* <Filters filters={this.state.filters} onFilter={() => this.onFilter()} /> */}
            <hr className="dotted-line"></hr>
            <InventoryInfo
              onExcludeStores={this.onExcludeStores}
              totalInventory={this.state.totalInventory}
              currentInventory={this.state.currentInventory}
              totalStores={this.state.totalStores}
              selectedStores={this.state.selectedStores}
            />
            <hr className="dotted-line"></hr>
            <Constraints
              markdownTypeHandler={this.markdownTypeHandler}
              marginValueHandler={this.marginValueHandler}
              markdownTypeVal={this.state.markdownTypeVal}
              targetSellThrough={this.targetSellThrough}
              constraintsCadenceHandler={this.constraintsCadenceHandler}
              cadenceConstraintsVal={this.state.cadenceConstraintsVal}
            />
            <hr className="dotted-line"></hr>
            <div className="cadencebtn">
              <Button onClick={this.generateCadence}>Generate Cadence</Button>
            </div>
          </div>

          {this.state.showCadence &&
            (this.state.overallCadenceData.length || this.state.skuLabelCadenceData.length) && (
              <div className="card mb-4">
                <Tabs
                  activeKey={this.state.defaultActiveKey}
                  transition={false}
                  id="noanim-tab-example"
                  onSelect={this.onSelectTabs}>
                  {this.getTab()}
                </Tabs>
                {/* {this.state.defaultActiveKey === 'Comparison' ? (
                  <div>
                    <Button
                      className="btn btn-secondary pull-right mt-2 postion-addcomp-button"
                      onClick={this.addScenario}>
                      + Add Scenario
                    </Button>
                  </div>
                ) : (
                  <div>
                    <Button
                      className="btn btn-secondary pull-right mt-2 postion-button"
                      onClick={this.addScenario}>
                      + Add Scenario
                    </Button>
                  </div>
                )} */}
                <div
                  className="addScenarioBtn comparisonbtn"
                  style={{ bottom: '2px', left: '0px', textAlign: 'center' }}>
                  <Button className="btn btn-secondary pull-right mt-2" onClick={this.addScenario}>
                    + Add Scenario
                  </Button>
                </div>
                <div className="simulatebtn">
                  {this.state.defaultActiveKey !== 'Optimal' && (
                    <button
                      className="btn btn-primary"
                      onClick={this.onSimulate}
                      // disabled={this.disabledSimulateButton()}
                    >
                      Simulate
                    </button>
                  )}
                  <button
                    className="btn btn-primary ml-3 mr-3"
                    onClick={() =>
                      this.setState({
                        showPlanSavePopup: true,
                        status: 'pending',
                      })
                    }>
                    Send for approval
                  </button>
                  <button
                    className="btn btn-primary resetButton"
                    onClick={() =>
                      this.setState({
                        showPlanSavePopup: true,
                        status: 'draft',
                      })
                    }>
                    Send to drafts
                  </button>
                </div>
              </div>
            )}
          {this.state.cadenceConstraintsVal === 'overall' &&
            this.state.defaultActiveKey === 'Comparison' && (
              <div className="card mb-4">
                <div className="optimalheading pl-3 pt-2">{this.state.defaultActiveKey}</div>
                <hr />
                <div>
                  <ComparisonMetricTable data={this.props.comparisonData} />
                </div>
              </div>
            )}

          {this.state.showCadence &&
            this.state.simulatedData &&
            this.props.markdownAnalysisGraphData &&
            this.props.markdownAnalysisGraphData.overview &&
            this.state.defaultActiveKey !== 'Comparison' && (
              <div className="card mb-4">
                <div className="optimalheading pl-3 pt-2">{this.state.defaultActiveKey}</div>
                <hr />
                {this.state.defaultActiveKey !== 'Comparison' && (
                  <div className="overviewtoggle">
                    <button
                      type="button"
                      className={classNames({
                        active: this.state.selectedView == 'Overview',
                        ' btn': true,
                        'btn btn-primary': true,
                        btnCalendr: true,
                        calPromo: true,
                      })}
                      onClick={() => this.changedView('Overview')}>
                      Overview
                    </button>
                    <button
                      type="button"
                      className={classNames({
                        active: this.state.selectedView == 'Detailed',
                        ' btn': true,
                        'btn-outline-primary': true,
                        btnCalendr: true,
                        calPromo: true,
                      })}
                      onClick={() => this.changedView('Detailed')}
                      style={{
                        fontSize: '15px',
                        borderColor: '#507a91',
                      }}>
                      Detailed
                    </button>
                  </div>
                )}
                {this.state.showCadence &&
                  this.state.simulatedData &&
                  this.props.markdownAnalysisGraphData &&
                  this.props.markdownAnalysisGraphData.overview &&
                  this.state.selectedView === 'Overview' &&
                  this.state.defaultActiveKey !== 'Comparison' && (
                    <div>
                      <MarkdownAnalysis
                        level={this.state.md_level}
                        analysisTable={this.props.analysisTable.analysisTabledata}
                        markdownAnalysisGraphData={this.props.markdownAnalysisGraphData.overview}
                        selectedSku={this.state.selectedSkuId}
                        optionSku={this.props.skuData}
                        value={this.props.selectedSkuId}
                        selectedSkuHandler={(val) => this.selectedSkuHandler(val)}
                      />
                    </div>
                  )}
                {this.state.selectedView === 'Detailed' &&
                  this.state.defaultActiveKey !== 'Comparison' && (
                    <div>
                      {/* <ColumnSelection attributes={this.state.attributes} /> */}
                      <div className="detailedTable ml-4 mr-4" style={{ paddingBottom: '2rem' }}>
                        <MarkdownDetailedTable
                          data={this.props.markdownAnalysisGraphData.detailed}
                        />
                      </div>
                    </div>
                  )}{' '}
              </div>
            )}

          <Modal
            visible={this.state.showPlanSavePopup}
            effect="fadeInDown"
            onClickAway={() => this.showPlanSavePopup()}>
            <div className="card" style={{ width: '130%' }}>
              <div className="card-body">
                <h6 className="card-title">
                  Enter MD Plan Details
                  <span
                    onClick={() => this.showPlanSavePopup()}
                    className="pull-right select-store-span">
                    <i
                      style={{ cursor: 'pointer' }}
                      className="fa fa-times"
                      onClick={() => this.showPlanSavePopup()}
                      aria-hidden="true"
                    />
                  </span>
                </h6>
                <hr />
              </div>

              <div className="ai-pre-grid-table" style={{ textAlign: 'right' }}>
                <PlanSavePopup
                  data={
                    this.state.cadenceConstraintsVal === 'overall'
                      ? this.state.overallCadenceData
                      : this.state.skuLabelCadenceData
                  }
                  activeIndex={this.getActiveIndex()}
                  comparsionSelectedData={cloneDeep(this.state.comparisonRow)}
                  activeTab={this.state.defaultActiveKey}
                  onSave={(planName, sceniosNames) => this.onSave(planName, sceniosNames)}
                />
              </div>
            </div>
          </Modal>
          <Modal
            visible={this.state.showExcludeStorePopup}
            effect="fadeInDown"
            onClickAway={() => this.showTablePopup()}>
            {/* <div className="card"> */}
            <div className="card-body">
              <h6 className="card-title">
                Select stores
                <span
                  onClick={() => this.showTablePopup()}
                  className="pull-right select-store-span">
                  <i
                    style={{ cursor: 'pointer' }}
                    className="fa fa-times"
                    onClick={() => this.showTablePopup()}
                    aria-hidden="true"
                  />
                </span>
              </h6>
              <hr />
            </div>
            {this.props.inventoryInfoData && (
              <div className="ai-pre-grid-table">
                <ExcludeStoreTable
                  currentPage={this.state.currentPage}
                  offset={this.state.offset}
                  showTablePopup={this.showTablePopup}
                  columnHeaders={this.props.inventoryInfoData.columnHeaders || []}
                  excludeStoreValHandler={this.excludeStoreValHandler}
                  storeData={this.props.inventoryInfoData.storeData || []}
                />
              </div>
            )}
            {/* </div> */}
          </Modal>
        </div>
      </LoadingOverlay>
    );
  }
}
const mapStateToProps = (state) => {
  console.log(state.loaderReducer.loaderText, 'laodetex');
  return {
    getOverlayLoaderState: state.loaderReducer.overlayLoaderState,
    getOverlayLoaderText: state.loaderReducer.loaderText,
    inventoryInfoData: state.manuallyCreateMdData.inventoryInfoData,
    cadenceData: state.manuallyCreateMdData.cadenceData,
    skuData: state.manuallyCreateMdData.skus,
    startDate: state.filtersReducer.startDate,
    endDate: state.filtersReducer.endDate,
    weeks: state.filtersReducer.weeks,
    csv_names: state.manuallyCreateMdData.csv_names,
    drops: state.filtersReducer.selectedDrops,
    selectedHierarchy1: state.filtersReducer.selectedHierarchy1,
    selectedHierarchy2: state.filtersReducer.selectedHierarchy2,
    selectedHierarchy3: state.filtersReducer.selectedHierarchy3,
    selectedHierarchy4: state.filtersReducer.selectedHierarchy4,
    selectedHierarchy5: state.filtersReducer.selectedHierarchy5,
    selectedHierarchy6: state.filtersReducer.selectedHierarchy6,
    selectedStores: state.filtersReducer.selectedStores,
    markdownAnalysisGraphData: state.markdownAnalysisData.markdownAnalysisGraphData,
    analysisTable: state.markdownAnalysisData.analysisTable,
    comparisonData: state.markdownAnalysisData.comparisonData,
    analysisData: state.markdownAnalysisData.analysisData,
    savedData: state.manuallyCreateMdData.savedData,
  };
};
const mapActionsToProps = {
  fetchInventoryInfo: manuallyCreateMdActions.fetchInventoryInfo,
  fetchCadence: manuallyCreateMdActions.fetchCadence,
  simulate: manuallyCreateMdActions.simulate,
  saveData: manuallyCreateMdActions.saveData,
  // sendDrafts: manuallyCreateMdActions.sendDrafts,
};

export default connect(mapStateToProps, mapActionsToProps)(withRouter(ManuallyCreateMD));
