import React, { Component } from 'react';
import StyleConfigFilter from '../StyleConfigFilter/StyleConfigFilter';
import DataTable from '../../../services/reactTable';

import './StyleConfig.css';

const Tabledata = [
    { style_family: 'aaaaa_aaa', id: '205-1-2-1-11203Y', name: '.308 20-20 CASE' },
    {
      style_family: 'aaaaa_aaa',
      id: '205-1-2-1-1C3006BHGN9',
      name: 'BH GOLD 30-06 178 GR ELD-X 20R',
    },
    {
      style_family: 'aaaaa_aaa',
      id: '205-1-2-1-1C300WMBHGN3',
      name: 'BH GOLD 300 WIN 180GR ACCUBOND',
    },
    {
      style_family: 'bbbb_aaa',
      id: '205-1-2-1-1C308BHGN6',
      name: 'BH GOLD 308 WIN 150 GR SST 20R',
    },
    {
      style_family: 'bbbb_aaa',
      id: '205-1-2-1-1C6.5CMBHGN1',
      name: 'BH GOLD 6.5 CM 143GR ELD-X 20R',
    },
    { style_family: 'ccc_123', id: '205-1-2-1-1C7MMRMBHGN5', name: 'BH GOLD 7MAG 139 GR GMX 20RD' },
    { style_family: 'ccc_123', id: '205-1-2-1-21292Z', name: 'CORELOKT 260REM 140GR' },
    { style_family: 'ddd', id: '205-1-2-1-21337', name: 'CORE-LOKT 7MM-08 140 PSP' },
  ],
  header = [
    {
      Header: 'Style Family',
      accessor: 'style_family',
      // Use a two-stage aggregator here to first
      // count the total rows being aggregated,
      // then sum any of those counts if they are
      // aggregated further
      aggregate: 'count',
      Aggregated: ({ value }) => `${value} Names`,
    },
    {
      Header: 'Style ID',
      accessor: 'id',
    },
    {
      Header: 'Style Description',
      accessor: 'name',
    },
    {
      Header: 'Edit Style',
      Cell: () => {
        return <i className="fa fa-pencil color-blue"></i>;
      },
    },
  ];
export class Config extends Component {
  state = {
    styleTable: 'styleFamily',
    showEDLP: true,
  };

  getTableData = (type, data) => {
    if (type === 'style') {
      return data.map((item) => ({
        ...item,
        buttonText: 'Edit Style',
      }));
    }
    if (type === 'styleFamily') {
      return data.map((item) => ({
        ...item,
        buttonText: 'Edit Family',
      }));
    }
    if (type === 'styleGroup') {
      return data.map((item) => ({
        ...item,
        buttonText: 'Edit Group',
      }));
    }
    return data;
  };

  renderTable = (styleName, data) => {
    if (styleName === 'style') {
      return <DataTable columns={header} data={Tabledata} />;
    }
    if (styleName === 'styleFamily') {
      return <DataTable columns={header} data={Tabledata} />;
    }
    if (styleName === 'styleGroup') {
      return <DataTable columns={header} data={Tabledata} />;
    }
    return null;
  };

  onStyleTypeChangeHandler = (value) => {
    this.setState({
      styleTable: value,
    });
  };

  render() {
    return (
      <div className="card p-2">
        <div className="mt-1" style={{ display: 'flex', justifyContent: 'space-between' }}>
          <h4 className="font-size-14  pad-10 markdown-heading">Store Configuration</h4>
          <div>
            <button
              className="btn btn-primary"
              onClick={() => this.props.onStyleButtonClickHandlers('styleGroup')}>
              Create Style Group
            </button>
            {/* <button
              className="btn btn-primary ml-1"
              onClick={() => this.props.onStyleButtonClickHandlers('styleFamily')}>
              Create Style Family
            </button> */}
            <button
              className="btn btn-primary ml-1"
              onClick={() => this.props.onStyleButtonClickHandlers('style')}>
              Create Style
            </button>
          </div>
        </div>
        <hr
          className="mb-2 mt-1"
          style={{
            marginTop: '-4px',
          }}
        />
        <StyleConfigFilter
          styleType={this.state.styleTable}
          onStyleTypeChangeHandler={this.onStyleTypeChangeHandler}
          showEDLP={this.state.showEDLP}
        />
        <hr />
        <div style={{ height: '372px', width: '64%', margin: '0 auto' }}>
          {this.renderTable(this.state.styleTable)}
        </div>
      </div>
    );
  }
}

export default Config;
