import React from 'react';
import {
  toPercentage,
  toPercentageWithDecimalDraft,
  toDollarWithlDraft,
  toUnitDraft,
  toFixedToTwoDraft,
} from '../../helper/formatter';
import _ from 'lodash';
import moment from 'moment';
import numeral from 'numeral';

export default () => {
  const colDef = [
    {
      Header: 'MD Plan',
      accessor: 'markdown_id',
      disableGroupBy: true,
      align: 'left',
      showFilter: true,
      enableRowSpan: true,
      Cell: ({ row, rows }) => {
        let check = _.find(rows, {
          original: { checked_for_approval: true, markdown_id: row.original.markdown_id },
        });
        return check ? row.original.md_plan : null;
      },
    },
    {
      Header: 'Scenario',
      accessor: 'scenario',
      showFilter: true,
      disableGroupBy: true,
      enableRowSpan: true,
      Cell: ({ row }) => {
        let check = row.original.checked_for_approval;
        return !check ? null : check ? <span>{row.original.scenario}</span> : '-';
      },
    },
    {
      // Build our expander column
      id: 'expander', // Make sure it has an ID
      // Header: ({ getToggleAllRowsExpandedProps, isAllRowsExpanded }) => (
      //   <span {...getToggleAllRowsExpandedProps()}>{isAllRowsExpanded ? 'ðŸ‘‡' : 'ðŸ‘‰'}</span>
      // ),
      Header: 'Date Range',
      width: 15,
      Cell: ({ row }) => {
        // Use the row.canExpand and row.getToggleRowExpandedProps prop getter
        // to build the toggle for expanding a row
        return row.canExpand && row.original.checked_for_approval ? (
          <span
            {...row.getToggleRowExpandedProps({
              style: {
                // We can even use the row.depth property
                // and paddingLeft to indicate the depth
                // of the row
                paddingLeft: `${row.depth * 2}rem`,
              },
            })}>
            {moment(row.original.start_date).format('YYYY/MM/DD')} -{' '}
            {moment(row.original.end_date).format('YYYY/MM/DD')}&nbsp;&nbsp;&nbsp;
            {row.isExpanded ? (
              <i class="fa fa-angle-down expanded-icon-size color-blue" aria-hidden="true"></i>
            ) : (
              <i class="fa fa-angle-right expanded-icon-size color-blue" aria-hidden="true"></i>
            )}
          </span>
        ) : null;
      },
    },
    {
      Header: 'Time Period',
      accessor: 'week_num',
      disableGroupBy: false,
      isGroupBy: false,
      Cell: ({ row }) => {
        let check = row.original.checked_for_approval;
        return !check ? null : check ? <span>{row.original.week_num}</span> : '-';
      },
      Aggregated: ({ value, row }) => {
        let check = row.original.checked_for_approval;
        return !check ? null : check ? `weeks (${row.subRows.length})` : '-';
      },
    },
    {
      Header: 'Sell through %',
      accessor: 'sell_through',
      disableGroupBy: true,
      // Cell: ({ row }) => {
      //   console.log(row, 'czseferuuuuuuuwweeee--');
      //   return row.original.checked_for_approval ? <span>{row.original.sell_through}</span> : null;
      // },
      Cell: (props) => <div> {toPercentageWithDecimalDraft(props)} </div>,
      Aggregated: ({ row }) => {
        let val = row.original.overall_sell_through;
        return row.original.checked_for_approval ? (
          <span>{val ? Number(val).toLocaleString('en') : '-'}</span>
        ) : null;
      },
    },
    {
      Header: 'Markdown %',
      accessor: 'markdown_perc',
      disableGroupBy: true,
      width: 70,
      Cell: (props) => <div> {toPercentage(props)} </div>,
      aggregate: 'average',
      Aggregated: ({ row }) => {
        let check = row.original.checked_for_approval;
        let val = row.original.overall_markdown_perc;
        return !check ? null : check ? numeral(val / 100).format('0%') : '-';
      },
    },
    // {
    //   Header: 'Margin %',
    //   accessor: 'margin_perc',
    //   disableGroupBy: true,
    //   width: 70,
    //   Cell: (props) => <div> {toPercentageWithDecimalDraft(props)} </div>,
    //   Aggregated: ({ row }) => {
    //     console.log('CSfdwepppppp', row);

    //     let val = row.original.overall_margin;
    //     let check = row.original.checked_for_approval;
    //     return check ? <span>{val ? Number(val).toFixed(2) : '-'}</span> : null;
    //   },
    // },
    // {
    //   Header: 'AUM',
    //   accessor: 'aum',
    //   disableGroupBy: true,
    //   Cell: (props) => <div>{toFixedToTwoDraft(props)}</div>,
    //   Aggregated: (row) => {
    //     let val = row.row.original.overall_aum;
    //     return <span>{val ? Number(val).toFixed(2) : '-'}</span>;
    //   },
    // },
    // {
    //   Header: 'AUR',
    //   accessor: 'aur',
    //   disableGroupBy: true,
    //   Cell: (props) => <div>{toFixedToTwoDraft(props)}</div>,
    //   Aggregated: (row) => {
    //     let val = row.row.original.overall_aur;
    //     return <span>{val ? Number(val).toFixed(2) : '-'}</span>;
    //   },
    // },
    // {
    //   Header: 'Planned Units',
    //   accessor: 'plannedUnits',
    //   Cell: (props) => <div>{toUnitDraft(props)}</div>,

    //   disableGroupBy: true,
    //   Aggregated: (row) => {
    //     let val = row.row.original.overall_planned_units;
    //     return <span>{val ? Number(val).toLocaleString('en') : '-'}</span>;
    //   },
    // },
    // {
    //   Header: 'Actual Units',
    //   accessor: 'actualUnits',
    //   Cell: (props) => <div>{toUnitDraft(props)}</div>,
    //   disableGroupBy: true,
    //   Aggregated: (row) => {
    //     let val = row.row.original.overall_units;
    //     return <span>{val ? Number(val).toLocaleString('en') : '-'}</span>;
    //   },
    // },
    // {
    //   Header: 'Revenue $',
    //   accessor: 'revenue$',
    //   Aggregated: (row) => {
    //     let val = row.row.original.overall_revenue$;
    //     console.log(val.toFixed(2), 'fixed');
    //     console.log(parseFloat(val).toFixed(2), 'valalala');
    //     console.log(Number(val).toFixed(2), 'nu,ber');
    //     return <span>{val ? parseInt(val).toLocaleString('en') : '-'}</span>;
    //   },
    //   disableGroupBy: true,
    //   Cell: (props) => <div> {toDollarWithlDraft(props)} </div>,
    // },
  ];
  return colDef;
};
