import React from 'react';
import DateRangePicker from '../../services/RetailCalendarDatePicker';
import * as moment from 'moment';

class Constraints extends React.Component {
  state = {
    focusedInput: null,
    startDate: null,
    endDate: null,
  };

  isOutsideRange = (day) => day.isBefore(moment().subtract(3, 'years'));

  render() {
    let { isEdit, editFrom, editBy } = this.props;
    let marginForEdit =
      isEdit && editFrom !== 'editModal' ? { paddingLeft: '2rem' } : { marginLeft: '5rem' };

    return (
      <div style={{ marginBottom: '5px' }} className={isEdit && !editBy ? 'edit-markdown' : ''}>
        <h5 className="font-size-14 font-bold" style={{ paddingLeft: '12px' }}>
          Strategy
        </h5>
        <div className="row constaints-row">
          <div className={isEdit && editFrom !== 'editModal' ? 'col-3 ml-3' : 'col-md-2 ml-3'}>
            <label className="input-label">
              Target Sell Through % <span style={{ color: 'red' }}> * </span>
            </label>
            <input
              disabled={isEdit && !editBy}
              className="form-control"
              type="number"
              value={
                isEdit && !editBy
                  ? this.props.editPlanDetails[0] &&
                    this.props.editPlanDetails[0].request_obj &&
                    this.props.editPlanDetails[0].request_obj.sellThrough
                  : this.props.targetSellThroughVal
              }
              min={this.props.minTS}
              max={this.props.maxTS}
              onChange={this.props.targetSellThrough}></input>
          </div>
          <div
            className={isEdit && editFrom !== 'editModal' ? 'col-3' : 'col-md-2'}
            style={marginForEdit}>
            <label className="input-label">Margin %</label>
            <input
              className="form-control"
              type="number"
              disabled={isEdit && !editBy}
              min={this.props.minMargin}
              max={this.props.maxMargin}
              value={
                isEdit && !editBy
                  ? this.props.editPlanDetails[0] &&
                    this.props.editPlanDetails[0].request_obj &&
                    this.props.editPlanDetails[0].request_obj.margin
                  : this.props.marginVal
              }
              onChange={this.props.marginValueHandler}></input>
          </div>
          <div className="col-md-3" style={marginForEdit}>
            <label className="input-label">
              Date range <span style={{ color: 'red' }}> * </span>
            </label>
            <DateRangePicker
              disabled={isEdit && !editBy}
              // showClearDates
              startDate={
                isEdit && !editBy
                  ? this.props.editPlanDetails[0] &&
                    this.props.editPlanDetails[0].request_obj &&
                    moment(this.props.editPlanDetails[0].request_obj.startDate)
                  : this.props.startDate
              }
              startDateId="startDate"
              endDate={
                isEdit && !editBy
                  ? this.props.editPlanDetails[0] &&
                    this.props.editPlanDetails[0].request_obj &&
                    moment(this.props.editPlanDetails[0].request_obj.endDate)
                  : this.props.endDate
              }
              endDateId="endDate"
              onDatesChange={this.props.onDatesChange}
              // onDatesChange={this.props.onDatesChange}
              // focusedInput={this.state.focusedInput}
              // onFocusChange={(focusedInput) => this.setState({ focusedInput })}
              // isOutsideRange={(day) => {
              //   if (this.state.startDate && this.state.endDate) {
              //     return true;
              //   } else if (this.state.receiptStartDate) {
              //     return moment(day).day() !== 6;
              //   } else {
              //     return moment(day).day() !== 0;
              //   }
              // }}
            />
          </div>
          <div className={isEdit && !editBy ? 'd-none' : 'col-md-2'} style={marginForEdit}>
            <label className="input-label"></label>

            <button
              className="btn btn-primary"
              onClick={() => this.props.showConstaintsHandler(true)}>
              <i class="fa fa-cog constaints-config-icon" aria-hidden="true"></i>
              Constraints
            </button>
          </div>
          {/* <div className="col-md-3 offset-md-1">
            <label className="input-label">Markdown type</label>
            <input
              type="radio"
              className="inverse-radio mr-2"
              name="inverse"
              onChange={() => this.props.markdownTypeHandler(true)}
              checked={this.props.markdownTypeVal}></input>
            Hard MD
            <input
              type="radio"
              className="inverse-radio mr-2 ml-4"
              name="inverse"
              onChange={() => this.props.markdownTypeHandler(false)}
              checked={!this.props.markdownTypeVal}></input>
            POS MD
          </div> */}
          {/* <div className="col-md-3">
            <label className="input-label">Cadence</label>
            <input
              type="radio"
              className="inverse-radio mr-2"
              name="overall"
              onChange={() => this.props.constraintsCadenceHandler('overall')}
              checked={this.props.cadenceConstraintsVal.value === 'overall'}></input>
            Overall
            <input
              type="radio"
              className="inverse-radio mr-2 ml-4"
              name="cadence"
              onChange={() => this.props.constraintsCadenceHandler('sku')}
              checked={this.props.cadenceConstraintsVal.value === 'sku'}></input>
            SKU/SKU Group level
          </div> */}
        </div>
      </div>
    );
  }
}
export default Constraints;
