import axios from 'axios';
import { config } from '../utils/config';

const manuallyCreateMdActions = {
  fetchInventoryInfo: (reqObj) => {
    return (dispatch) => {
      dispatch({
        type: 'LOADER_STATE',
        payload: {
          status: true,
        },
      });
      axios({
        url: `${config.createMarkdownURL}/getInventoryInfo/`,
        method: 'POST',
        data: reqObj,
      })
        .then((resp) => {
          dispatch({
            type: 'GET_INVENTORY_INFO',
            payload: resp.data.data,
          });
          dispatch({
            type: 'LOADER_STATE',
            payload: {
              status: false,
            },
          });
        })
        .catch((err) => {
          console.log(err);
        });
    };
  },
  fetchCadence: (reqObj) => {
    return (dispatch) => {
      dispatch({
        type: 'LOADER_STATE',
        payload: {
          status: true,
        },
      });
      axios({
        // url: `${config.createMarkdownURL}/generateCadence/`,
        url: `${config.cadenceUrl}/MarkdownOptimization`,
        method: 'POST',
        data: reqObj,
      })
        .then((resp) => {
          let skus = [];
          if (resp.data.skus && resp.data.skus.length) {
            skus = resp.data.skus.map((sku) => {
              return {
                label: sku,
                value: sku,
              };
            });
          }
          dispatch({
            type: 'GET_CADENCE_DATA',
            payload: [resp.data.cadenceData],
            csv_names: resp.data.csv_names,
            skus: skus,
            savedData: resp.data.saveData,
          });
          dispatch({
            type: 'GET_MARKDOWNANALYSIS_GRAPH_DATA',
            payload: resp.data.simulateData,
          });
          dispatch({
            type: 'SET_ANALYSIS_TABLE_DATA',
            payload: resp.data.simulateData,
            cadenceType: 'Optimal',
          });
          dispatch({
            type: 'LOADER_STATE',
            payload: {
              status: false,
            },
          });
        })
        .catch((err) => {
          console.log(err);
          dispatch({
            type: 'LOADER_STATE',
            payload: {
              status: false,
            },
          });
        });
    };
  },

  simulate: (reqObj, cadenceType) => {
    return (dispatch) => {
      dispatch({
        type: 'LOADER_STATE',
        payload: {
          status: true,
        },
      });
      axios({
        url: `${config.cadenceUrl}/CalculationData`,
        method: 'POST',
        data: reqObj,
      })
        .then((resp) => {
          dispatch({
            type: 'GET_MARKDOWNANALYSIS_GRAPH_DATA',
            payload: resp.data,
          });
          dispatch({
            type: 'SET_ANALYSIS_TABLE_DATA',
            payload: resp.data,
            cadenceType,
          });
          dispatch({
            type: 'LOADER_STATE',
            payload: {
              status: fasle,
            },
          });
        })
        .catch((err) => {
          dispatch({
            type: 'LOADER_STATE',
            payload: {
              status: fasle,
            },
          });
          console.log(err);
        });
    };
  },
  saveData: (reqObj) => {
    return (dispatch) => {
      dispatch({
        type: 'LOADER_STATE',
        payload: {
          status: true,
        },
      });
      axios({
        url: `${config.createMarkdownURL}/saveMarkDown/`,
        method: 'POST',
        data: reqObj,
      })
        .then((resp) => {
          alert('resp:', resp);
          dispatch({
            type: 'LOADER_STATE',
            payload: {
              status: false,
            },
          });
          // dispatch({
          //   type: 'GET_INVENTORY_INFO',
          //   payload: resp.data,
          // });
        })
        .catch((err) => {
          dispatch({
            type: 'LOADER_STATE',
            payload: {
              status: fasle,
            },
          });
          console.log(err);
        });
    };
  },
};
export default manuallyCreateMdActions;
