import draftTableData from '../components/draftStatus/draft/data';
import statusTableData from '../components/draftStatus/status/data';

const draftStatusDefaultState = {
  markdowns: [],
  columnHeaders: {},
  draftTableData: draftTableData,
  statusTableData: statusTableData,
  statusOptions: [
    { label: 'All', value: '1' },
    { label: 'Approved', value: '2' },
    { label: 'Rejected', value: '3' },
    { label: 'Pending', value: '4' },
  ],
  metricOptions: {
    draft: [
      { label: 'sell through', value: '1' },
      { label: 'margin %', value: '2' },
      { label: 'mark down %', value: '3' },
    ],
    status: [
      { label: 'sell through', value: '1' },
      { label: 'margin %', value: '2' },
      { label: 'mark down %', value: '3' },
    ],
  },
};

export default (state = draftStatusDefaultState, action) => {
  switch (action.type) {
    case 'SET_MARK_DOWNS':
      return {
        ...state,
        markdowns: action.payload.message
          ? { message: action.valpayloadue.message }
          : action.payload,
      };
    case 'SET_MD_COLUMN_HEADERS':
      return {
        ...state,
        columnHeaders: action.value,
      };
    case 'SET_DRAFT_DATA':
      return {
        ...state,
        draftTableData: action.value,
      };
    case 'SET_STATUS_DATA':
      return {
        ...state,
        statusTableData: action.value,
      };
    case 'SET_DRAFT_METRIC_OPTIONS':
      return {
        ...state,
        metricOptions: {
          ...state.metricOptions,
          draft: action.value,
        },
      };
    case 'SET_STATUS_METRIC_OPTIONS':
      return {
        ...state,
        metricOptions: {
          ...state.metricOptions,
          status: action.value,
        },
      };
    default:
      return {
        ...state,
      };
  }
};
