const initialState = {
  inventoryInfoData: '',
  cadenceData: [],
  csv_names: [],
  columnHeaders: {
    // default: {
    //   'MD Plan': 'md_plan',
    //   Scenario: 'scenario',
    //   'Start Date': 'start_date',
    //   'End Date': 'end_date',
    //   'Markdown %': 'markdown_perc',
    //   'Sell through %': 'sell_through',
    //   'Margin %': 'margin_perc',
    // },
    // fixed: {
    //   'MD Plan': 'md_plan',
    //   Scenario: 'scenario',
    //   'Start Date': 'start_date',
    //   'End Date': 'end_date',
    //   'Markdown %': 'markdown_perc',
    // },
    Sales: {
      'Revenue $': 'Revenue',
      'PVH Planned units': 'Planned units',
      'Markdown units': 'Units',
      'AUR $': 'AUR',
    },
    Margin: {
      'Margin %': 'Margin Percentage',
      'Margin $': 'margin',
      'AUM $': 'AUM',
    },
  },
};

export default function (state = initialState, action) {
  switch (action.type) {
    case 'GET_INVENTORY_INFO':
      return {
        ...state,
        inventoryInfoData: action.payload,
      };
    case 'GET_CADENCE_DATA':
      return {
        ...state,
        cadenceData: action.payload,
        csv_names: action.csv_names,
        skus: action.skus,
        savedData: action.savedData,
      };
    case 'MARKDOWN_SAVED_STATUS':
      return {
        ...state,
        status: action.status,
      };
    case 'GET_COLUMNHEADERS':
      return {
        ...state,
        columnHeaders: action.payload,
      };
    default:
      return {
        ...state,
      };
  }
}
