import React, { Component } from 'react';
import { connect } from 'react-redux';
import { withRouter } from 'react-router-dom';
import Filters from '../../services/filters';
import './finalizeMarkdownStyle.css';
import styled from 'styled-components';
import NoData from '../noTableData';
import FinalizeAction from '../../actions/draftStausFinalizeAction';
import Coldef from './coldef';
import _ from 'lodash';
import cloneDeep from 'clone-deep';
import DataTable from '../../services/reactTable';
import ColumnSelection from '../columnSelection';
import AlertWarning from '../../utils/AlertMessage/alertDanger';
import AlertSuccess from '../../utils/AlertMessage/alertSuccess';
import CardComponent from '../../services/cardComponent/cardComponent';
import moment from 'moment';
import card1Image from '../../images/card1Image.png';
import card2Image from '../../images/card2Image.png';
import card3Image from '../../images/card3Image.png';
import card4Image from '../../images/card4Image.png';
import IndeterminateCheckbox from '../../components/cellRenders/inderterminateCheckbox';

class FinalizeMarkdown extends Component {
  constructor(props) {
    super(props);
    this.state = {
      attributes: ['Margin', 'Sales', 'Inventory'],
      firstCardText: 'KPIs to be decided',
      firstCardFirstValue: '12%',
      firstCardSecondValue: '24%',
      secondCardFirstValue: '16%',
      secondCardSecondValue: '20%',
      thirdCardFirstValue: '6%',
      thirdCardSecondValue: '10%',
      fourthCardFirstValue: '345',
      fourthCardSecondValue: '3,457',
      cardWeekText: 'this Week',
      cardMonthText: 'this Month',
      selectedItems: [],
      columnOptions: [],
      selLabels: [],
      columns: [],
      selectedRows: [],
      filters: [
        {
          type: 'hierarchy1',
          isMandatory: true,
          isMultiple: false,
        },
        {
          type: 'hierarchy2',
          isMandatory: true,
          isMultiple: true,
        },
        {
          type: 'hierarchy3',
          isMandatory: true,
          isMultiple: true,
        },
        {
          type: 'hierarchy4',
          isMandatory: true,
          isMultiple: true,
        },
        {
          type: 'hierarchy5',
          isMandatory: true,
          isMultiple: true,
        },
        {
          type: 'hierarchy6',
          isMandatory: false,
          isMultiple: true,
        },
      ],
    };
  }

  componentWillUnmount() {
    this.props.setMarkDowns([]);
    this.props.setMDColumnHeaders({});
  }

  findStatusColor = (status, color) => {
    switch (status) {
      case 'high':
        return color.high;
      case 'medium':
        return color.medium;
      case 'low':
        return color.low;
      default:
        break;
    }
  };

  bgmCol = (cellInfo) => {
    if (cellInfo.column.id === 'md_status') var status = cellInfo.row.original.md_status;
    let bgCol = {
      high: '#F8E8E8',
      medium: '#FCF3D1',
      low: '#E8F8F5',
    };
    return this.findStatusColor(status, bgCol);
  };
  txtCol = (cellInfo) => {
    if (cellInfo.column.id === 'md_status') var status = cellInfo.row.original.md_status;
    let txtCol = {
      high: '#D38485',
      medium: '#F0C53A',
      low: '#66CFBA',
    };
    return this.findStatusColor(status, txtCol);
  };
  sendForReject = () => {
    let payload = {
      markdown_data: this.state.selectedRows.map((item) => {
        return {
          id: item.markdown_id,
        };
      }),
      update_status: 'rejected',
    };
    console.log(payload, 'sendForReject');
    this.props.sendForApproval(payload);
  };
  sendForApprove = () => {
    let payload = {
