const plannedReportsDefaultState = {
  plannedReportsTableData: [],
  metricOptions: {
    draft: [
      { label: 'sell through', value: '1' },
      { label: 'margin %', value: '2' },
      { label: 'mark down %', value: '3' },
    ],
    status: [
      { label: 'sell through', value: '1' },
      { label: 'margin %', value: '2' },
      { label: 'mark down %', value: '3' },
    ],
  },
};

export default function (state = plannedReportsDefaultState, action) {
  switch (action.type) {
    case 'RESET_PLANNED_REPORTS_DATA':
      return {
        ...state,
        plannedReportsTableData: [],
      };
    case 'GET_PLANNED_REPORTS_DATA':
      return {
        ...state,
        plannedReportsTableData: action.payload,
      };

    case 'SET_PLANNEDREPORTS_METRIC_OPTIONS':
      return {
        ...state,
        metricOptions: {
          ...state.metricOptions,
          draft: action.value,
        },
      };
    case 'SET_STATUS_METRIC_OPTIONS':
      return {
        ...state,
        metricOptions: {
          ...state.metricOptions,
          status: action.value,
        },
      };
    default:
      return {
        ...state,
      };
  }
}
