const initialState = {
  reportingData: {},
  cockpitData: {},
};
const getData = (data, year, ifPercent) => {
  return Object.keys(data)
    .sort((a, b) => {
      return parseInt(a.slice(1, a.length)) - parseInt(b.slice(1, b.length));
    })
    .map((e) => (ifPercent ? data[e][year] * 100 : data[e][year]));
};
const accumulate = (data) => {
  for (let i = 1; i < data.length; i++) {
    data[i] += data[i - 1];
  }
  return data;
};
export default function (state = initialState, action) {
  switch (action.type) {
    case 'SET_REPORTING_DATA':
      return {
        ...state,
        reportingData: {
          sellThroughData: {
            labels: Object.keys(action.payload.sell_through).sort((a, b) => {
              return parseInt(a.slice(1, a.length)) - parseInt(b.slice(1, b.length));
            }),
            datasets: [
              {
                label: 'TY',
                fill: false,
                borderColor: '#44677b',
                data: accumulate(getData(action.payload.sell_through, 'ty', true)),
                steppedLine: true,
              },
              {
                label: 'LY',
                fill: false,
                borderColor: '#a6cee3',
                data: accumulate(getData(action.payload.sell_through, 'ly', true)),
                steppedLine: true,
              },
            ],
          },
          inventoryData: {
            labels: Object.keys(action.payload.inventory).sort((a, b) => {
              return parseInt(a.slice(1, a.length)) - parseInt(b.slice(1, b.length));
            }),
            datasets: [
              {
                label: 'TY',
                fill: false,
                borderColor: '#44677b',
                data: getData(action.payload.inventory, 'ty'),
              },
              {
                label: 'LY',
                fill: false,
                borderColor: '#a6cee3',
                data: getData(action.payload.inventory, 'ly'),
              },
            ],
          },
          marginData: {
            labels: Object.keys(action.payload.margin).sort((a, b) => {
              return parseInt(a.slice(1, a.length)) - parseInt(b.slice(1, b.length));
            }),
            datasets: [
              {
                label: 'TY',
                fill: false,
                borderColor: '#44677b',
                data: getData(action.payload.margin, 'ty', true),
              },
              {
                label: 'LY',
                fill: false,
                borderColor: '#a6cee3',
                data: getData(action.payload.margin, 'ly', true),
              },
            ],
          },
          salesData: {
            labels: ['Last year', 'Actual'],
            datasets: [
              {
                fill: false,
                backgroundColor: ['#a6cee3', '#44677b'],
                data: [action.payload.sales.ly, action.payload.sales.ty],
              },
            ],
          },
        },
      };
    case 'GET_SKU_COCKPIT':
      return {
        ...state,
        cockpitData: action.payload,
      };
    default:
      return {
        ...state,
      };
  }
}
