import React, { Component } from 'react';
import Select from 'react-select';
import Filters from '../../../services/filters';
import { connect } from 'react-redux';

const deptData = [
  { label: 'Group 1', value: 'Group 1' },
  { label: 'Group 2', value: 'Group 2' },
  { label: 'Group 3', value: 'Group 3' },
];

export class StoreConfig extends Component {
  state = {
    storeGroup: '',
  };

  onStoreGroupChangeHandler = (value) => {
    this.setState({
      storeGroup: value,
    });
  };

  clearStoreFilter = () => {
    this.setState({
      storeGroup: '',
    });
  };

  render() {
    return (
      <div className="card p-2">
        <div className="row" style={{ justifyContent: 'space-between' }}>
          <div className="col-md-6">
            <div className="row">
              {/* <div className="col-md-3" style={{ textAlign: 'right' }}>
                Store Group
              </div> */}
              <div className="col-md-4">
                <label className="input-label">Store group</label>
                {/* <Select
                  name={__store_group}
                  placeholder="Select Store Group"
                  options={deptData}
                  value={this.state.storeGroup}
                  onChange={this.onStoreGroupChangeHandler}
                /> */}
                <Select
                  id="store-group-select"
                  placeholder="Select Store Group"
                  options={deptData}
                  value={this.state.storeGroup}
                  onChange={this.onStoreGroupChangeHandler}
                />
              </div>
              <div className="col-md-4 align-self-end pl-0">
                <button className="btn btn-primary">
                  <i className="fa fa-filter mr-1" aria-hidden="true"></i>
                  Filter
                </button>
                <button onClick={this.clearStoreFilter} className="btn btn-primary ml-2">
                  Reset
                </button>
              </div>
            </div>
          </div>
          {this.props.userRole !== 'Admin' && (
            <div className="col-md-2 align-self-end pl-0 text-right">
              <button
                className="btn btn-primary"
                onClick={() => this.props.onCreateStoreGroupHandler()}>
                Create store group
              </button>
            </div>
          )}
        </div>
        <hr />
        <Filters
          hierarchy={[
            'hierarchy1',
            'hierarchy2',
            'hierarchy3',
            'hierarchy4',
            'hierarchy5',
            'hierarchy6',
          ]}
          // onFilter={this.onFilter}
        />
        <hr />
        <div className="row">
          <div className="col-md-2">No of stores: 190</div>
          {/* <div className="col-md-2">Overall QC: 0.23</div> */}
          {this.props.userRole !== 'Admin' && (
            <div className="col-md-10 text-right">
              <button
                className="btn btn-primary"
                onClick={() => this.props.onCreateStoreGroupHandler(true)}>
                Edit store group
              </button>
            </div>
          )}
        </div>
        <div style={{ height: '370px', width: '65%', margin: '1.5rem auto' }}>
          {/* {this.props.userRole === 'Admin' ? (
            <TableContextProvider
              value={{
                tableComponent: {
                  colDefs: StoreConfigSchema.colDefs,
                  data: StoreConfigSchema.data,
                  autoSizeCol: true,
                  frameworkComponents: ['numericalEditor'],
                },
                numericalEditor: {
                  onNumericValueChange: () => {},
                },
              }}>
              <TableComponent />
            </TableContextProvider>
          ) : (
            <TableContextProvider
              value={{
                tableComponent: {
                  colDefs: StoreConfigSchema.colDefs,
                  data: StoreConfigSchema.data,
                  autoSizeCol: true,
                },
              }}>
              <TableComponent />
            </TableContextProvider>
          )} */}
        </div>
      </div>
    );
  }
}
const mapStateToProps = (store) => {
  return {
    selectedHierarchy1: store.filtersReducer.selectedHierarchy1,
    selectedHierarchy2: store.filtersReducer.selectedHierarchy2,
    selectedHierarchy3: store.filtersReducer.selectedHierarchy3,
    selectedHierarchy4: store.filtersReducer.selectedHierarchy4,
    selectedHierarchy5: store.filtersReducer.selectedHierarchy5,
  };
};
const mapActionsToProps = {
  // getReportingData: reportingActions.getReportingData,
};
export default connect(mapStateToProps, mapActionsToProps)(StoreConfig);
