import React from 'react';
import { DateRangePicker } from 'react-dates';
import * as moment from 'moment';

class Constraints extends React.Component {
  state = {
    focusedInput: null,
  };

  isOutsideRange = (day) => day.isBefore(moment().subtract(3, 'years'));

  render() {
    return (
      <div style={{ marginBottom: '5px' }}>
        <h5 className="font-size-14 font-bold" style={{ paddingLeft: '12px' }}>
          Strategy
        </h5>
        <div className="row constaints-row">
          <div className="col-md-2 ml-3">
            <label className="input-label">Target Sell Through %</label>
            <input
              className="form-control"
              type="number"
              min={this.props.minTS}
              max={this.props.maxTS}
              onChange={this.props.targetSellThrough}></input>
          </div>
          <div className="col-md-2" style={{ marginLeft: '5rem' }}>
            <label className="input-label">Margin %</label>
            <input
              className="form-control"
              type="number"
              min={this.props.minMargin}
              max={this.props.maxMargin}
              onChange={this.props.marginValueHandler}></input>
          </div>
          <div className="col-md-2" style={{ marginLeft: '5rem' }}>
            <label className="input-label">Date range</label>
            <DateRangePicker
              showClearDates
              startDate={this.state.startDate}
              startDateId="startDate"
              endDate={this.state.endDate}
              endDateId="endDate"
              // onDatesChange={({ startDate, endDate }) => this.setState({ startDate, endDate })}
              onDatesChange={this.onDatesChange}
              focusedInput={this.state.focusedInput}
              onFocusChange={(focusedInput) => this.setState({ focusedInput })}
              isOutsideRange={(day) => {
                if (this.state.startDate && this.state.endDate) {
                  return true;
                } else if (this.state.receiptStartDate) {
                  return moment(day).day() !== 6;
                } else {
                  return moment(day).day() !== 0;
                }
              }}
            />
          </div>
          {/* <div className="col-md-3 offset-md-1">
            <label className="input-label">Markdown type</label>
            <input
              type="radio"
              className="inverse-radio mr-2"
              name="inverse"
              onChange={() => this.props.markdownTypeHandler(true)}
              checked={this.props.markdownTypeVal}></input>
            Hard MD
            <input
              type="radio"
              className="inverse-radio mr-2 ml-4"
              name="inverse"
              onChange={() => this.props.markdownTypeHandler(false)}
              checked={!this.props.markdownTypeVal}></input>
            POS MD
          </div> */}
          {/* <div className="col-md-3">
            <label className="input-label">Cadence</label>
            <input
              type="radio"
              className="inverse-radio mr-2"
              name="overall"
              onChange={() => this.props.constraintsCadenceHandler('overall')}
              checked={this.props.cadenceConstraintsVal === 'overall'}></input>
            Overall
            <input
              type="radio"
              className="inverse-radio mr-2 ml-4"
              name="cadence"
              onChange={() => this.props.constraintsCadenceHandler('sku')}
              checked={this.props.cadenceConstraintsVal === 'sku'}></input>
            SKU/SKU Group level
          </div> */}
        </div>
      </div>
    );
  }
}
export default Constraints;
