import React from 'react';
import { toPercentage, toDollarWithDecimal, toUnit, toFixedToTwo } from '../../../helper/formatter';
import _ from 'lodash';
import moment from 'moment';

export default () => {
  const colDef = [
    {
      Header: 'MD Plan',
      accessor: 'md_plan',
      disableGroupBy: true,
      width: 50,
      align: 'left',
      enableRowSpan: true,
    },
    {
      Header: 'Scenario',
      accessor: 'markdown_id',
      disableGroupBy: true,
      enableRowSpan: true,
      width: 50,
      Cell: ({ row, rows }) => (row.original.scenario ? row.original.scenario : null),
    },
    {
      // Build our expander column
      id: 'expander', // Make sure it has an ID
      // Header: ({ getToggleAllRowsExpandedProps, isAllRowsExpanded }) => (
      //   <span {...getToggleAllRowsExpandedProps()}>{isAllRowsExpanded ? 'ðŸ‘‡' : 'ðŸ‘‰'}</span>
      // ),
      Header: 'Date Range',
      width: 15,
      Cell: ({ row }) => {
        // Use the row.canExpand and row.getToggleRowExpandedProps prop getter
        // to build the toggle for expanding a row
        console.log('czseferuuuuuuu', row);

        return row.canExpand ? (
          <span
            {...row.getToggleRowExpandedProps({
              style: {
                // We can even use the row.depth property
                // and paddingLeft to indicate the depth
                // of the row
                paddingLeft: `${row.depth * 2}rem`,
              },
            })}>
            {moment(row.original.start_date).format('YYYY/MM/DD')} -{' '}
            {moment(row.original.end_date).format('YYYY/MM/DD') + '  '}
            {row.isExpanded ? (
              <i class="fa fa-angle-down expanded-icon-size" aria-hidden="true"></i>
            ) : (
              <i class="fa fa-angle-right expanded-icon-size" aria-hidden="true"></i>
            )}
          </span>
        ) : null;
      },
    },
    {
      Header: 'Time Period',
      accessor: 'week_num',
      disableGroupBy: false,
      isGroupBy: false,
      width: 50,
      Aggregated: ({ value, row }) => `weeks (${row.subRows.length})`,
    },
    {
      Header: 'Sell through%',
      accessor: 'sell_through',
      disableGroupBy: true,
      width: 50,
      Cell: (props) => <div> {toPercentage(props)} </div>,
      Aggregated: (row) => {
        return <span>{row.row.original.overall_sell_through}</span>;
      },
    },
    {
      Header: 'Markdown %',
      accessor: 'markdown_perc',
      disableGroupBy: true,
      width: 70,
      Cell: (props) => <div> {toPercentage(props)} </div>,
      aggregate: 'average',
      Aggregated: ({ value }) => '-',
    },
    {
      Header: 'Margin%',
      accessor: 'margin_perc',
      disableGroupBy: true,
      width: 70,
      Cell: (props) => <div> {toPercentage(props)} </div>,
      Aggregated: (row) => `{row.row.original.overall_margin} %`,
    },
    {
      Header: 'Aum',
      accessor: 'aum',
      disableGroupBy: true,
      Cell: (props) => <div>{toFixedToTwo(props)}</div>,
      Aggregated: (row) => {
        return <span>{row.row.original.overall_aum}</span>;
      },
    },
    {
      Header: 'Aur',
      accessor: 'aur',
      disableGroupBy: true,
      Cell: (props) => <div>{toFixedToTwo(props)}</div>,
      Aggregated: (row) => {
        return <span>{row.row.original.overall_aur}</span>;
      },
    },
    {
      Header: 'Planned Units',
      accessor: 'plannedUnits',
      Cell: (props) => <div>{toUnit(props)}</div>,

      disableGroupBy: true,
      Aggregated: (row) => {
        return <span>{row.row.original.overall_planned_units}</span>;
      },
    },
    {
      Header: 'Actual Units',
      accessor: 'actualUnits',
      Cell: (props) => <div>{toUnit(props)}</div>,
      disableGroupBy: true,
      Aggregated: (row) => {
        return <span>{row.row.original.overall_units}</span>;
      },
    },
    {
      Header: 'Revenue',
      accessor: 'revenue$',
      Aggregated: (row) => {
        return <span>{row.row.original.overall_revenue}</span>;
      },
      disableGroupBy: true,
      Cell: (props) => <div> {toDollarWithDecimal(props)} </div>,
    },
  ];
  return colDef;
};
