const initialState = {
  reportingData: {},
  cockpitData: {},
  columnHeaders: {
    Sales: {
      'Revenue $': 'revenue$',
      'Actual sales (units)': 'units_sold',
      "Last week's sales": 'last_week_sales',
      'AUR $': 'aur',
    },
    Margin: {
      'Margin $': 'margin$',
      'AUM $': 'aum',
    },
    Inventory: {
      // 'In Transit': 'inventory.inventory_in_transit',
      // 'On Order': 'inventory.inventory_on_order',
      'On Hand': 'inventory.inventory_on_hand',
      'Weeks on supply': 'wos',
      'Total availabality': 'total_avail',
    },
    //do not move below two items to top of the object, will cause an issue in reporting metrics(_findKeys)
    default: {
      TY: 'margin_ty',
      LY: 'margin_ly',
      TY: 'sell_through_ty',
      LY: 'sell_through_ly',
      'Style Group': 'style_number',
      'Margin $': 'margin$',
      'AUM $': 'aum',
      'Revenue $': 'revenue$',
      'Actual sales (units)': 'units_sold',
      "Last week's sales": 'last_week_sales',
      'AUR $': 'aur',
      // 'In Transit': 'inventory.inventory_in_transit',
      // 'On Order': 'inventory.inventory_on_order',
      'On Hand': 'inventory.inventory_on_hand',
      // 'In DC': 'inventory.inventory_in_dc',
      'Weeks on supply': 'wos',
      // 'GM %': 'GM%',
      // 'Sell Through Avg': 'sell_through_avg',
      // 'No. of doors': 'no_of_doors',
      'Total availabality': 'total_avail',
      // 'Size integrity': 'size_integrity',
    },
    fixed: {
      'SKU ID': 'SKU_Id',
      'Style Description': 'style_desc',
      'ST %': 'sellThrough',
      'Markdown status': 'mdStatus',
    },
  },
};
const getData = (data, year) => {
  console.log(data, 'data');
  return Object.keys(data).map((e) => data[e][year]);
};

const setMarkdownStatus = (data) => {
  return data.map((e) => {
    if (e.wos > 25) e.mdStatus = 'High';
    else if (e.wos > 15 && e.wos < 25) e.mdStatus = 'Medium';
    else e.mdStatus = 'Low';
    return e;
  });
};

export default function (state = initialState, action) {
  switch (action.type) {
    case 'SET_REPORTING_DATA':
      return {
        ...state,
        reportingData: action.payload.message
          ? { message: action.payload.message }
          : {
              sellThroughData: {
                labels: Object.keys(action.payload.sell_through),
                datasets: [
                  {
                    label: 'TY',
                    fill: false,
                    borderColor: '#44677b',
                    data: getData(action.payload.sell_through, 'ty'),
                    steppedLine: true,
                  },
                  {
                    label: 'LY',
                    fill: false,
                    borderColor: '#a6cee3',
                    data: getData(action.payload.sell_through, 'ly'),
                    steppedLine: true,
                  },
                ],
              },
              inventoryData: {
                labels: Object.keys(action.payload.inventory),
                datasets: [
                  {
                    label: 'TY',
                    fill: false,
                    borderColor: '#44677b',
                    data: getData(action.payload.inventory, 'ty'),
                  },
                  {
                    label: 'LY',
                    fill: false,
                    borderColor: '#a6cee3',
                    data: getData(action.payload.inventory, 'ly'),
                  },
                ],
              },
              marginData: {
                labels: Object.keys(action.payload.margin),
                datasets: [
                  {
                    label: 'TY',
                    fill: false,
                    borderColor: '#44677b',
                    data: getData(action.payload.margin, 'ty'),
                  },
                  {
                    label: 'LY',
                    fill: false,
                    borderColor: '#a6cee3',
                    data: getData(action.payload.margin, 'ly'),
                  },
                ],
              },
              salesData: {
                labels: ['TY', 'LY'],
                datasets: [
                  // {
                  //   label: 'TY',
                  //   fill: false,
                  //   backgroundColor: '#44677b',
                  //   data: [action.payload.sales && action.payload.sales.ty],
                  // },
                  // {
                  //   label: 'LY',
                  //   fill: false,
                  //   backgroundColor: '#a6cee3',
                  //   data: [action.payload.sales && action.payload.sales.ly],
                  // },
                  {
                    fill: false,
                    backgroundColor: ['#a6cee3', '#44677b'],
                    data: [action.payload.sales.ly, action.payload.sales.ty],
                  },
                ],
              },
            },
      };
    case 'GET_SKU_COCKPIT':
      return {
        ...state,
        cockpitData: setMarkdownStatus(action.payload),
      };
    case 'GET_COLUMNHEADERS':
      return {
        ...state,
        columnHeaders: {
          ...state.columnHeaders,
        },
      };
    case 'RESET_REPORTING_DATA':
      return {
        ...state,
        reportingData: {},
        cockpitData: {},
      };
    default:
      return {
        ...state,
      };
  }
}
