import React from 'react';
import {
  toPercentage,
  toPercentageWithDecimalDraft,
  toDollarWithlDraft,
  toUnitDraft,
  toFixedToTwoDraft,
} from '../../../helper/formatter';
import _ from 'lodash';

import moment from 'moment';
import numeral from 'numeral';

export default () => {
  const colDef = [
    {
      Header: 'MD Plan',
      accessor: 'markdown_id',
      disableGroupBy: true,
      // showFilter: true,
      filter: (rows, id, filterType) =>
        rows.filter((row) => {
          return row.original.md_plan ? row.original.md_plan.startsWith(filterType) : null;
        }),
      width: 50,
      align: 'left',
      enableRowSpan: true,
      Cell: ({ row }) => {
        return row.original.md_plan ? row.original.md_plan : null;
      },
    },
    {
      Header: 'Scenario',
      accessor: 'scenario',
      // showFilter: true,
      disableGroupBy: true,
      enableRowSpan: true,
      width: 50,
      // Cell: ({ row, rows }) => {
      //   console.log('vzsmdfhweowepppppppp', row, rows);

      //   return row.original.scenario ? row.original.scenario : null;
      // },
    },
    {
      // Build our expander column
      id: 'expander', // Make sure it has an ID
      // Header: ({ getToggleAllRowsExpandedProps, isAllRowsExpanded }) => (
      //   <span {...getToggleAllRowsExpandedProps()}>{isAllRowsExpanded ? 'ðŸ‘‡' : 'ðŸ‘‰'}</span>
      // ),
      Header: 'Date Range',
      width: 15,
      Cell: ({ row }) => {
        // Use the row.canExpand and row.getToggleRowExpandedProps prop getter
        // to build the toggle for expanding a row

        return row.canExpand ? (
          <span
            {...row.getToggleRowExpandedProps({
              style: {
                // We can even use the row.depth property
                // and paddingLeft to indicate the depth
                // of the row
                paddingLeft: `${row.depth * 2}rem`,
              },
            })}>
            {moment(row.original.start_date).format('YYYY/MM/DD')} -{' '}
            {moment(row.original.end_date).format('YYYY/MM/DD')}&nbsp;&nbsp;&nbsp;
            {row.isExpanded ? (
              <i class="fa fa-angle-down expanded-icon-size color-blue" aria-hidden="true"></i>
            ) : (
              <i class="fa fa-angle-right expanded-icon-size color-blue" aria-hidden="true"></i>
            )}
          </span>
        ) : null;
      },
    },
    {
      Header: 'Time Period',
      accessor: 'week_num',
      disableGroupBy: false,
      isGroupBy: false,
      width: 50,
      Aggregated: ({ value, row }) => `weeks (${row.subRows.length})`,
      Cell: SortMethod,
    },
    {
      Header: 'Sell through %',
      accessor: 'sell_through',
      disableGroupBy: true,
      width: 50,
      Cell: (props) => <div> {toPercentage(props)} </div>,
      Aggregated: (row) => {
        let val = parseFloat(row.row.original.overall_sell_through);
        return <span>{val ? numeral(val / 100).format('0%') : '-'}</span>;
      },
    },
    {
      Header: 'Markdown %',
      accessor: 'markdown_perc',
      disableGroupBy: true,
      width: 70,
      Cell: (props) => <div> {toPercentage(props)} </div>,
      aggregate: 'average',
      Aggregated: ({ row }) => {
        let val = parseFloat(row.original.overall_markdown_perc);
        return <span>{val ? numeral(val / 100).format('0%') : '-'}</span>;
      },
    },
    // {
    //   Header: 'Margin %',
    //   accessor: 'margin_perc',
    //   disableGroupBy: true,
    //   width: 70,
    //   Cell: (props) => <div> {toPercentage(props)} </div>,
    //   Aggregated: (row) => {
    //     let val = row.row.original.overall_margin;

    //     return <span>{val ? parseFloat(val).toFixed(2) : '-'}</span>;
    //   },
    // },
    {
      Header: 'Status',
      accessor: 'status',
      disableGroupBy: true,
      // showFilter: true,
      Cell: StatusCellStyling,
    },

    // {
    //   Header: 'AUM',
    //   accessor: 'aum',
    //   disableGroupBy: true,
    //   Cell: (props) => <div>{toFixedToTwoDraft(props)}</div>,
    //   Aggregated: (row) => {
    //     let val = row.row.original.overall_aum;
    //     return <span>{val ? Number(val).toFixed(2) : '-'}</span>;
    //   },
    // },
    // {
    //   Header: 'AUR',
    //   accessor: 'aur',
    //   disableGroupBy: true,
    //   Cell: (props) => <div>{toFixedToTwoDraft(props)}</div>,
    //   Aggregated: (row) => {
    //     let val = row.row.original.overall_aur;
    //     return <span>{val ? Number(val).toFixed(2) : '-'}</span>;
    //   },
    // },
    // {
    //   Header: 'Planned Units',
    //   accessor: 'plannedUnits',
    //   Cell: (props) => <div>{toUnitDraft(props)}</div>,

    //   disableGroupBy: true,
    //   Aggregated: (row) => {
    //     let val = row.row.original.overall_planned_units;
    //     return <span>{val ? Number(val).toLocaleString('en') : '-'}</span>;
    //   },
    // },
    // {
    //   Header: 'Actual Units',
    //   accessor: 'actualUnits',
    //   Cell: (props) => <div>{toUnitDraft(props)}</div>,
    //   disableGroupBy: true,
    //   Aggregated: (row) => {
    //     let val = row.row.original.overall_units;
    //     return <span>{val ? Number(val).toLocaleString('en') : '-'}</span>;
    //   },
    // },
    // {
    //   Header: 'Revenue $',
    //   accessor: 'revenue$',
    //   Aggregated: (row) => {
    //     let val = row.row.original.overall_revenue$;
    //     console.log(val.toFixed(2), 'fixed');
    //     console.log(parseFloat(val).toFixed(2), 'valalala');
    //     console.log(Number(val).toFixed(2), 'nu,ber');
    //     return <span>{val ? parseInt(val).toLocaleString('en') : '-'}</span>;
    //   },
    //   disableGroupBy: true,
    //   Cell: (props) => <div> {toDollarWithlDraft(props)} </div>,
    // },
  ];
  return colDef;
};
function sortMethod(a, b) {
  console.log(a, 'aaaaaaaa', b, 'bbbbb');
}
function StatusCellStyling({ value }) {
  switch (value) {
    case 'rejected':
      return (
        <span style={{ textTransform: 'capitalize' }}>
          <i className="fa fa-ban fa-lg" aria-hidden="true" style={{ color: '#D84039' }}></i>{' '}
          {value} &nbsp; &nbsp;{' '}
          <i
            className="fa fa-commenting fa-lg"
            aria-hidden="true"
            style={{ cursor: 'pointer' }}></i>
        </span>
      );
    case 'draft':
      return (
        <span style={{ textTransform: 'capitalize' }}>
          <i className="fa fa-edit fa-lg" aria-hidden="true" style={{ color: '#44677B' }}></i>{' '}
          {value} &nbsp; &nbsp;{' '}
        </span>
      );
    case 'pending':
      return (
        <span style={{ textTransform: 'capitalize' }}>
          <i
            className="fa fa-exclamation-circle fa-lg"
            style={{ color: '#E0D337' }}
            aria-hidden="true"></i>{' '}
          {value}
        </span>
      );
    case 'approved':
      return (
        <span style={{ textTransform: 'capitalize' }}>
          <i
            className="fa fa-check-circle fa-lg"
            aria-hidden="true"
            style={{ color: '#59C770' }}></i>{' '}
          {value}
        </span>
      );

    default:
      return '';
  }
}
