import axios from "axios";

import {
  INVENTORY_DATA_BY_FILTERED,
  GET_ERRORS,
  SKU_COCKPIT_LINE_DATA,
  GET_MARKDOWN_CONFIG_DATA,
  CREATE_MARKDOWN_SKU_GROUP,
  UPDATE_MARKDOWN_GROUP,
  DELETE_MARKDOWN_GROUP,
  LOADER
} from "../../actions/types";
import { config } from "../../utils/config";

export const actionInvDataByFiltered = params => dispatch => {
  axios
    .get(`${config.markDownUrl}/abc`)
    .then(res => {
      dispatch({
        type: INVENTORY_DATA_BY_FILTERED,
        payload: res.data
      });
    })
    .catch(err => {
      dispatch({
        type: GET_ERRORS,
        payload: err
      });
    });
};

export const actionSkuLineData = params => dispatch => {
  dispatch({
    type: LOADER,
    payload: true
  });
  axios
    .get(`${config.markDownUrl}/markdown/skuHistory${params}`)
    .then(res => {
      dispatch({
        type: SKU_COCKPIT_LINE_DATA,
        payload: res.data
      });
      dispatch({
        type: LOADER,
        payload: false
      });
    })
    .catch(err => {
      dispatch({
        type: GET_ERRORS,
        payload: err
      });
    });
};

export const getMarkdownConfigureData = params => dispatch => {
  var url = `${config.markDownUrl}/markdown/configureData`;
  if (params) {
    url += params;
  }
  axios
    .get(url)
    .then(res => {
      dispatch({
        type: GET_MARKDOWN_CONFIG_DATA,
        payload: res.data
      });
    })
    .catch(err => {
      dispatch({
        type: GET_ERRORS,
        payload: err
      });
    });
};

export const actionMarkdownSkuCreate = params => dispatch => {
  axios
    .post(`${config.markDownUrl}/markdown/createGroup`, params)
    .then(res => {
      dispatch({
        type: CREATE_MARKDOWN_SKU_GROUP,
        payload: res.data
      });
    })
    .catch(err => {
      dispatch({
        type: GET_ERRORS,
        payload: err
      });
    });
};

export const actionMarkdownSkuUpdate = params => dispatch => {
  axios
    .post(`${config.markDownUrl}/markdown/updateConfiguration`, params)
    .then(res => {
      dispatch({
        type: UPDATE_MARKDOWN_GROUP,
        payload: res.data
      });
    })
    .catch(err => {
      dispatch({
        type: GET_ERRORS,
        payload: err
      });
    });
};

export const actionDeleteMarkdownGroup = params => dispatch => {
  axios
    .post(`${config.markDownUrl}/markdown/deleteGroup`, params)
    .then(res => {
      dispatch({
        type: DELETE_MARKDOWN_GROUP,
        payload: res.data
      });
    })
    .catch(err => {
      dispatch({
        type: GET_ERRORS,
        payload: err
      });
    });
};

export const actionMarkdownSKugroupGraphData = params => dispatch => {
  var url = `${config.markDownUrl}/markdown/skuGroupHistory`;
  if (params) {
    url += params;
  }
  axios
    .get(url)
    .then(res => {
      dispatch({
        type: SKU_COCKPIT_LINE_DATA,
        payload: res.data
      });
    })
    .catch(err => {
      dispatch({
        type: GET_ERRORS,
        payload: err
      });
    });
};
