};

export default (state = summaryKPIinitialState, action) => {
  switch (action.type) {
    case 'SET_KPI_DATA':
      return {
        ...state,
        summaryKPIData: action.value,
        // summaryKPIData: summaryKPIinitialState.summaryKPIData,
      };
    case 'SET_DETAILED_SUMMARY':
