import React, { Component } from 'react';
import { connect } from 'react-redux';
import { withRouter } from 'react-router-dom';
import Filters from '../../services/filters';
import './finalizeMarkdownStyle.css';
import styled from 'styled-components';
import Coldef from './coldef';
import DataTable from '../../services/reactTable';
import ColumnSelection from '../columnSelection';
import AlertWarning from '../../utils/AlertMessage/alertDanger';
import AlertSuccess from '../../utils/AlertMessage/alertSuccess';
import CardComponent from '../../services/cardComponent/cardComponent';

import card1Image from '../../images/card1Image.png';
import card2Image from '../../images/card2Image.png';
import card3Image from '../../images/card3Image.png';
import card4Image from '../../images/card4Image.png';
class FinalizeMarkdown extends Component {
  constructor(props) {
    super(props);
    this.state = {
      attributes: ['Margin', 'Sales', 'Inventory'],
      firstCardText: 'KPIs to be decided',
      firstCardFirstValue: '12%',
      firstCardSecondValue: '24%',
      secondCardFirstValue: '16%',
      secondCardSecondValue: '20%',
      thirdCardFirstValue: '6%',
      thirdCardSecondValue: '10%',
      fourthCardFirstValue: '345',
      fourthCardSecondValue: '3,457',
      cardWeekText: 'this Week',
      cardMonthText: 'this Month',
    };
  }

  findStatusColor = (status, color) => {
    switch (status) {
      case 'high':
        return color.high;
      case 'medium':
        return color.medium;
      case 'low':
        return color.low;
      default:
        break;
    }
  };

  bgmCol = (cellInfo) => {
    if (cellInfo.column.id === 'md_status') var status = cellInfo.row.original.md_status;
    let bgCol = {
      high: '#F8E8E8',
      medium: '#FCF3D1',
      low: '#E8F8F5',
    };
    return this.findStatusColor(status, bgCol);
  };
  txtCol = (cellInfo) => {
    if (cellInfo.column.id === 'md_status') var status = cellInfo.row.original.md_status;
    let txtCol = {
      high: '#D38485',
      medium: '#F0C53A',
      low: '#66CFBA',
    };
    return this.findStatusColor(status, txtCol);
  };

  render() {
    return (
      <div>
        <div className="alertDivs">
          {this.state.showAlertWarning ? (
            <AlertWarning
              message="Markdown Finalization Failed!"
              show={this.state.showAlertWarning}
            />
          ) : null}
          {this.state.showAlertSuccess ? (
            <AlertSuccess
              message={'Markdown(s) are ' + this.state.message}
              show={this.state.showAlertSuccess}
            />
          ) : null}
        </div>
        <div className="card CalendarMain promoCalendarDiv1 mt-3">
          <h4 className="promoCal-brdcrm mb-10">
            Markdown Optimization / <span className="font-bold color-blue">Finalize Markdown</span>
          </h4>
          <hr style={{ marginTop: '1px' }} />
          <Filters
            filters={[
              'season',
              'hierarchy1',
              'hierarchy2',
              'hierarchy3',
              'hierarchy4',
              'hierarchy5',
              'hierarchy6',
              'hierarchy7',
            ]}
          />
        </div>
        <div className="row" style={{ padding: '1px 18px', marginTop: '15px' }}>
          <div className="col-md-3 col-sm-3 col-xs-6 ">
            <CardComponent
              cardImage={card1Image}
              cardText={this.state.firstCardText}
              cardValue={this.state.firstCardFirstValue}
              cardValueSecond={this.state.firstCardSecondValue}
              cardWeekText={this.state.cardWeekText}
              cardMonthText={this.state.cardMonthText}
              cardStyles={{
                cardClass: 'card-border1',
                cardValue: 'card1-value',
                cardImg: 'card1-img',
                cardValueSecond: 'card-value-second',
                cardSubHeading: 'card-subHeading',
              }}
            />
          </div>
          <div className="col-md-3 col-sm-3 col-xs-6">
            <CardComponent
              cardImage={card2Image}
              cardText={this.state.firstCardText}
              cardValue={this.state.secondCardFirstValue}
              cardValueSecond={this.state.secondCardSecondValue}
              cardWeekText={this.state.cardWeekText}
              cardMonthText={this.state.cardMonthText}
              cardStyles={{
                cardClass: 'card-border2',
                cardValue: 'card1-value',
                cardImg: 'card2-img',
                cardValueSecond: 'card-value-second',
                cardSubHeading: 'card-subHeading',
              }}
            />
          </div>
          <div className="col-md-3 col-sm-3 col-xs-6 ">
            <CardComponent
              cardImage={card3Image}
              cardText={this.state.firstCardText}
              cardValue={this.state.thirdCardFirstValue}
              cardValueSecond={this.state.thirdCardSecondValue}
              cardWeekText={this.state.cardWeekText}
              cardMonthText={this.state.cardMonthText}
              cardStyles={{
                cardClass: 'card-border3',
                cardValue: 'card1-value',
                cardImg: 'card3-img',
                cardValueSecond: 'card-value-second',
                cardSubHeading: 'card-subHeading',
              }}
            />
          </div>
          <div className="col-md-3 col-sm-3 col-xs-6 ">
            <CardComponent
              cardImage={card4Image}
              cardText={this.state.firstCardText}
              cardValue={this.state.fourthCardFirstValue}
              cardValueSecond={this.state.fourthCardSecondValue}
              cardWeekText={this.state.cardWeekText}
              cardMonthText={this.state.cardMonthText}
              cardStyles={{
                cardClass: 'card-border4',
                cardValue: 'card1-value',
                cardImg: 'card4-img',
              }}
            />
          </div>
        </div>
        <div className="card CalendarMain promoCalendarDiv1 mt-3">
          <ColSelBtn>
            <ColumnSelection
              tab="draft"
              options={this.props.getMetricOptions}
              onHandleSelect={this.onHandleSelect}
              selectedItems={this.state.selectedItems}
              attributes={this.state.attributes}
              contentRenderer={true}
            />
            <div style={{ padding: '1rem' }}>
              <button className="btn btn-primary">Approve</button>&nbsp;
              <button className="btn btn-primary resetButton">Reject</button>
            </div>
          </ColSelBtn>
          <DataTable
            columns={Coldef()}
            data={this.props.getTableData}
            updateMyData={this.updateMyData}
            visibleColumns="markDownDrafts"
            isPagination={true}
            getColumnProps={(column) => ({
              // onClick: () => this.onColumnClick(column),
            })}
            getCellProps={(cellInfo) => ({
              style: {
                backgroundColor: this.bgmCol(cellInfo),
                color: this.txtCol(cellInfo),
                // textAlign: "center",
                margin: '4px 4px',
                borderRadius: '2px',
              },
            })}
          />
        </div>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    getTableData: state.finalizeReducer.finalizeTableData,
    getMetricOptions: state.finalizeReducer.metricOptions,
  };
};
export default connect(
  mapStateToProps,
  null
  // mapActionToProps
)(withRouter(FinalizeMarkdown));

const ColSelBtn = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: space-between;
`;
