import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { withRouter } from 'react-router-dom';
import { loginUser } from '../../actions/auth';
import { logoutUser } from '../../actions/auth';
import './loginStyle.css';

// import {setAuthToken} from '../utils/setAuthToken' ;

class Login extends Component {
  constructor(props) {
    super();
    this.state = {
      username: '',
      password: '',
      errors: {},
      errorMessage: false,
    };

    this.onChange = this.onChange.bind(this);
    this.onSubmit = this.onSubmit.bind(this);
  }

  onChange(e) {
    this.setState({ [e.target.name]: e.target.value });
  }

  componentWillReceiveProps(nextProps) {
    if (nextProps.authReducer.isAuthenticated) {
      this.props.history.push('/vis/Markdown/reporting');
    }

    if (nextProps.errors) {
      this.setState({ errors: nextProps.errors });
    }
  }

  onSubmit(e) {
    e.preventDefault();
    const userData = {
      email: this.state.email,
      password: this.state.password,
    };
    this.props.loginUser(userData);
  }

  keyPress = (e) => {
    if (e.keyCode === 13) {
      const userData = {
        email: this.state.email,
        password: this.state.password,
      };
      this.props.loginUser(userData);
    }
  };

  render() {
    // const { errors, email, password} = this.state;
    const { errors } = this.state;
    return (
      <div className="d-md-flex h-md-100 align-items-center" style={{ overflowY: 'hidden' }}>
        <div
          id="leftImgdiv"
          className="col-md-4 p-0 bg-indigo h-md-100"
          style={{ backgroundImage: 'url(img/New-up.png)' }}>
          <div className="divleftContent">
            <ul className="nav justify-content navTopLeft">
              <li className="nav-item">
                <img id="logoImgLeft" src="./img/Logo - IA.png" style={{ width: '26%' }} />
              </li>
            </ul>
            <div
              id="subdiv-login"
              className="text-white d-md-flex align-items-center p-5 text-center ">
              <div className="logoarea pt-5 pb-5" style={{ width: '100%' }}>
                <h5 className="mb-4 mt-3" style={{ color: 'white' }}>
                  Promo$mart Enterprise
                </h5>
                <div id="btndiv-login">
                  <button type="submit" className="btn btn-primary btn-square shadow-sm btncontact">
                    Contact Us
                  </button>
                </div>
              </div>
            </div>
            <footer className="page-footer">
              <div className="footer-copyright text-center py-3 text-white">
                ©copyright@impactanalytics2019
              </div>
            </footer>
          </div>
        </div>
        <div id="divRight" className="col-md-8 h-md-100 loginarea">
          <div id="signinform">
            <form className="formWidth" onSubmit={this.onSubmit} onKeyUp={(e) => this.keyPress(e)}>
              <h4 id="signIntext">
                Sign in to <span id="promoColor1">Promo$mart</span>
              </h4>
              <p>Enter your details below.</p>
              <div className="form-group">
                <label className="form-label" for="email">
                  EMAIL
                </label>
                <input
                  name="email"
                  type="email"
                  className="form-control"
                  id="loginInputEmail1"
                  aria-describedby="emailHelp"
                  placeholder="E-mail"
                  required=""
                  value={this.state.email}
                  onChange={this.onChange}
                  error={errors.email}
                />
              </div>
              <div className="form-group">
                <label className="form-label" for="password">
                  PASSWORD
                </label>

                <input
                  name="password"
                  type="password"
                  className="form-control"
                  id="loginPassword"
                  placeholder="Password"
                  required=""
                  value={this.state.password}
                  onChange={this.onChange}
                  error={errors.password}
                />
                {/* temporary dummy link */}
                <span id="forgtpas">
                  <div className="text-cyan" style={{ cursor: 'pointer' }}>
                    Forgot your password?
                  </div>
                </span>
              </div>
              {this.state.errors && Object.entries(this.state.errors).length ? (
                <span style={{ color: 'red' }}>Your Email or Password is Incorrect!</span>
              ) : (
                <span></span>
              )}
              {/* <p>{errors.message}</p> */}
              <div id="btndiv2">
                <button type="submit" className="btn btn-primary btn-square shadow-sm btnsignin">
                  Sign in
                </button>
              </div>
            </form>
          </div>
        </div>
      </div>
    );
  }
}

Login.propTypes = {
  loginUser: PropTypes.func.isRequired,
  errors: PropTypes.object.isRequired,
  success: PropTypes.object.isRequired,
};

const mapStateToProps = (state) => ({
  authReducer: state.authReducer,
  errors: state.errors,
  success: state.success,
});

export default connect(mapStateToProps, { loginUser })(withRouter(Login));
