import React, { Component } from 'react';
import Filters from '../../services/filters';
import MarkdownAnalysis from '../MarkdownAnalysis';
import InventoryInfo from './InventoryInfo';
import Constraints from './Constraints';
import CreateCadence from './CreateCadence';
import Modal from 'react-awesome-modal';
import { withRouter } from 'react-router-dom';
import Select from 'react-select';
import { connect } from 'react-redux';
import LoadingOverlay from '../../utils/Loader';
import _ from 'lodash';
import manuallyCreateMdActions from '../../actions/manuallyCreateMdActions.js';
import './index.css';
import moment from 'moment';
import { Button, Tabs, Tab } from 'react-bootstrap';
import MarkdownDetailedTable from './MardownDetailedTable';
import ColumnSelection from '../columnSelection';
import cloneDeep from 'clone-deep';
import ComparisonTable from './ComparisonTable';
import ComparisonMetricTable from './ComparisonMetricTable';
import classNames from 'classnames';
import ExcludeStoreTable from './excludeStoreTable';
// import LoadingOverlay from 'react-loading-overlay';

import PlanSavePopup from './planSavePopup';
class ManuallyCreateMD extends Component {
  constructor(props) {
    super(props);
    this.state = {
      selectedSkuId: { label: null, value: null },
      selectedSkuGroup: { label: null, value: null },
      targetSellThroughVal: null,
      markdownTypeVal: false,
      cadenceConstraintsVal: 'overall',
      marginVal: null,
      markdownDetailedData: [],
      overallCadenceData: [],
      skuLabelCadenceData: [],
      filters: [
        // 'season',
        'hierarchy1',
        'hierarchy2',
        'hierarchy3',
        'hierarchy4',
        'hierarchy5',
        'hierarchy6',
        'hierarchy7',
      ],
      totalInventory: 0,
      currentInventory: 0,
      totalStores: 0,
      selectedStores: 0,
      selectedView: 'Overview',
      selectedSku: [],
      showExcludeStorePopup: false,
      showPlanSavePopup: false,
      TabTitles: ['Optimal'],
      defaultActiveKey: 'Optimal',
      attributes: ['Sales', 'Inventory', 'Margin'],
      value: 1,
      comparisonData: [],
      showCadence: false,
      simulatedData: false,
      comparisonRow: null,
    };
  }
  selectedSkuHandler = (val) => {
    this.callAPISimulate(val.value);
    this.setState({ selectedSkuId: val.value });
  };
  compariosnCheckbox = (comparisonRowVal) => {
    this.setState({ comparisonRow: comparisonRowVal });
  };
  onUpdateMarkdownPercentage = (data, index) => {
    if (this.state.cadenceConstraintsVal === 'overall') {
      let data1 = cloneDeep(this.state.overallCadenceData);
      data1[index] = data;
      this.setState({
        overallCadenceData: data1,
      });
    } else {
      let data1 = cloneDeep(this.state.skuLabelCadenceData);
      data1[index] = data;
      this.setState({
        skuLabelCadenceData: data1,
      });
    }
  };
  getComparsionHeader = (header) => {
    if (this.state.cadenceConstraintsVal === 'sku' && header === 'Comparison') {
      return (
        <div className="row skuLabelStyle">
          <label className="skuLabel">SKU/SKU Group</label>
          <div className="col-md-2">
            <Select name="category" value={this.state.selectedSkuGroup} />
          </div>
        </div>
      );
    }
    return (
      <div className="row justify-content-center skuLabelStyle">
        <label className="font-size-14">Markdown Percentages</label>
      </div>
    );
  };
  getTabTag = (tabTitle, index) => {
    console.log(
      'this.state.overallCadenceData[index]:',
      this.state.overallCadenceData,
      index,
      this.state.overallCadenceData[index]
    );
    if (tabTitle === 'Comparison') {
      if (this.state.cadenceConstraintsVal === 'sku') {
        return (
          <div className="cadencetable">
            <ComparisonMetricTable showCheckBoxComparsionTable={true} />
          </div>
        );
      } else {
        return (
          <ComparisonTable
            compariosnCheckbox={this.compariosnCheckbox}
            data={this.state.overallCadenceData[index]}
          />
        );
      }
    } else {
      return (
        <div className="cadencetable">
          <CreateCadence
            overallCadenceData={
              this.state.cadenceConstraintsVal === 'overall'
                ? this.state.overallCadenceData[index]
                : this.state.skuLabelCadenceData[index]
            }
            onUpdateMarkdownPercentage={this.onUpdateMarkdownPercentage}
            addScenario={this.addScenario}
            selectedSku={this.state.selectedSku}
            key={tabTitle}
            index={index}
            activeTab={this.state.defaultActiveKey}
          />
        </div>
      );
    }
  };
  getTab() {
    return this.state.TabTitles.map((tabTitle, index) => {
      return (
        <Tab eventKey={tabTitle} title={tabTitle} key={tabTitle}>
          {this.getComparsionHeader(tabTitle)}
          {this.getTabTag(tabTitle, index)}
        </Tab>
      );
    });
  }
  onSelectTabs = (key) => {
    this.setState({
      defaultActiveKey: key,
    });
  };
  addTitleInTabs = () => {
    //checl comparison is there or not if yes -1
    let TabTitles = this.state.TabTitles;
    let comparisonTitleIndex = TabTitles.indexOf('Comparison');
    let newSceniosIndex = TabTitles.length;
    let defaultSceniosTitle = `Scenario${TabTitles.length}`;
    if (comparisonTitleIndex !== -1) {
      //comparsion present
      newSceniosIndex = comparisonTitleIndex;
      defaultSceniosTitle = `Scenario${newSceniosIndex}`;
      TabTitles.splice(newSceniosIndex, 0, defaultSceniosTitle);
    } else {
      TabTitles.splice(newSceniosIndex, 0, defaultSceniosTitle);
    }
    this.setState({
      TabTitles,
      defaultActiveKey: defaultSceniosTitle,
    });
    this.addContentsInTabs(newSceniosIndex);
  };
  addContentsInTabs = (newSceniosIndex) => {
    let data =
      this.state.cadenceConstraintsVal === 'overall'
        ? cloneDeep(this.state.overallCadenceData)
        : cloneDeep(this.state.skuLabelCadenceData);
    data.splice(newSceniosIndex, 0, data[0]);
    let obj = {
      skuLabelCadenceData: data,
    };
    if (this.state.cadenceConstraintsVal === 'overall') {
      obj = {
        overallCadenceData: data,
      };
    }
    this.setState(obj);
  };
  addScenario = () => {
    this.addTitleInTabs();
  };

  generateCadence = () => {
    this.setState({
      showCadence: true,
      simulatedData: true,
    });
    let startWeek = moment('2-25-1995', 'MM-DD-YYYY').week();
    let endWeek = moment('12-25-1995', 'MM-DD-YYYY').week();
    let weeks = this.getWeekRange(startWeek, endWeek);
    let payload = {
      filters: {
        hierarchy1: this.props.selectedHierarchy1,
        hierarchy2: this.props.selectedHierarchy2,
        hierarchy3: this.props.selectedHierarchy3,
        hierarchy4: this.props.selectedHierarchy4,
        hierarchy5: this.props.selectedHierarchy5,
        hierarchy6: this.props.selectedHierarchy6,
      },
      startDate: '2-25-1995',
      endDate: '12-25-1995',
      weeks: weeks,
      startWeek: moment('2-25-1995', 'MM-DD-YYYY').week(),
      endWeek: moment('12-25-1995', 'MM-DD-YYYY').week(),
      inventorySelected: '',
      storeSelected: '',
      excludeStore: '',
      sellThrough: this.state.targetSellThroughVal || '',
      margin: this.state.marginVal || '',
      markdownType: this.state.markdownTypeVal || '',
      cadenceType: this.state.cadenceConstraintsVal || '',
    };

    console.log(payload, 'payyloaaddd');
    let obj = {
      filters: {
        hierarchy1: [],
        hierarchy2: [],
        hierarchy3: [],
        hierarchy4: [],
        hierarchy5: [],
        hierarchy6: ['SKU1', 'SKU2', 'SKU3'],
      },
      startDate: '',
      endDate: '',
      startWeek: 1,
      endWeek: 3,
      inventorySelected: '',
      storeSelected: '',
      excludeStore: '',
      sellThrough: '2',
      margin: '2',
      markdownType: '',
      cadenceType: this.state.cadenceConstraintsVal || 'overall',
    };
    let abc = {
      filters: {
        hierarchy1: ['ARCHERY'],
        hierarchy2: ['BOWS'],
        hierarchy3: ['BOW ACCESSORIES'],
        hierarchy4: ['QUIVERS'],
        hierarchy5: ['AXT ARCHER XTREME'],
        hierarchy6: ['CARBON HELIX'],
      },
      startDate: '2-25-1995',
      endDate: '12-25-1995',
      startWeek: 8,
      endWeek: 52,
      inventorySelected: '',
      storeSelected: '',
      excludeStore: '',
      sellThrough: '20',
      margin: '30',
      markdownType: false,
      cadenceType: 'overall',
    };
    // {
    //   csv_names: [],
    //   skus: 'all',
    //   scenario: 'false',
    //   scenario_data: [],
    // }
    this.props.fetchCadence(obj);
  };
  updateComparsionDataInTab = () => {
    let comparsionData = [];
    for (let i = 0; i < this.state.TabTitles.length; i++) {
      if (this.state.cadenceConstraintsVal === 'overall') {
        comparsionData.push({
          scenarios: this.state.TabTitles[i],
          ...this.state.overallCadenceData[i][0],
        });
      } else {
        let rows = cloneDeep(this.state.skuLabelCadenceData[i][0]);
        delete rows['SKU ID'];
        delete rows['MD Status'];
        comparsionData.push({
          scenarios: this.state.TabTitles[i],
          ...rows,
        });
      }
    }
    let data = [];
    if (this.state.cadenceConstraintsVal === 'overall') {
      data = cloneDeep(this.state.overallCadenceData);
      data.splice(data.length, 1, comparsionData);
      this.setState({
        overallCadenceData: data,
        simulatedData: true,
      });
    } else {
      data = cloneDeep(this.state.skuLabelCadenceData);
      data.splice(data.length, 1, comparsionData);
      this.setState({
        skuLabelCadenceData: data,
        simulatedData: true,
      });
    }
  };
  onSimulate = () => {
    //adding comparsion title

    if (this.state.TabTitles.indexOf('Comparison') === -1) {
      let array = [...this.state.TabTitles, 'Comparison'];
      this.setState(
        {
          TabTitles: array,
        },
        () => {}
      );
    }
    this.updateComparsionDataInTab();
    this.compariosnCheckbox();
    this.callAPISimulate();
  };
  callAPISimulate = (skus = 'all') => {
    let index = this.state.TabTitles.indexOf(this.state.defaultActiveKey);
    let array = this.state.overallCadenceData[index];
    this.props.simulate({
      csv_names: this.props.csv_names,
      skus,
      scenario: 'true',
      scenario_data: [{ W1: '10', W2: '10', W3: '10' }],
    });
  };
  changedView = (view) => {
    this.setState({ selectedView: view });
  };
  // selectedSkuHandler = (val) => {
  //   this.setState({ selectedSkuId: val });
  // };
  onExcludeStores = () => {
    this.setState({ showExcludeStorePopup: true });
  };
  showPlanSavePopup = () => {
    this.setState({ showPlanSavePopup: true });
  };
  excludeStoreValHandler = (selectedRowId) => {
    console.log(selectedRowId, 'selectedRowId');
  };
  showTablePopup = () => {
    this.setState({ showExcludeStorePopup: false });
  };
  getWeekRange = (startWeek, endWeek) => {
    let arr = [];
    for (let i = startWeek; i <= endWeek; i++) {
      arr.push(i);
    }
    return arr;
  };

  onFilter = () => {
    let startWeek = moment('2-25-1995', 'MM-DD-YYYY').week();
    let endWeek = moment('12-25-1995', 'MM-DD-YYYY').week();
    let weeks = this.getWeekRange(startWeek, endWeek);
    let tempObj = {
      filters: {
        hierarchy1: ['AAF'],
        hierarchy2: ['ARIZONA HOTSHOTS'],
        hierarchy3: ['AAF-HARDGOODS'],
        hierarchy4: ['AAF-HARDGOODS'],
        hierarchy5: ['LOGO CHAIR_ INC.'],
        hierarchy6: ['421-1-5-5-1052-93FAD-1'],
      },
      startDate: '2-25-1995',
      endDate: '12-25-1997',
      startWeek: 1,
      endWeek: 90,
    };
    let params = {
      filters: {
        hierarchy1: this.props.selectedHierarchy1,
        hierarchy2: this.props.selectedHierarchy2,
        hierarchy3: this.props.selectedHierarchy3,
        hierarchy4: this.props.selectedHierarchy4,
        hierarchy5: this.props.selectedHierarchy5,
        hierarchy6: this.props.selectedHierarchy6,
        // hierarchy7: this.props.selectedHierarchy7,
      },
      weeks: weeks,
      startDate: '2-25-1995',
      endDate: '12-25-1995',
      startWeek: moment('2-25-1995', 'MM-DD-YYYY').week(),
      endWeek: moment('12-25-1995', 'MM-DD-YYYY').week(),
    };
    this.props.fetchInventoryInfo(tempObj);
  };

  constraintsCadenceHandler = (value) => {
    this.setState({
      cadenceConstraintsVal: value,
      showCadence: false,
      TabTitles: ['Optimal'],
      defaultActiveKey: 'Optimal',
    });
  };
  markdownTypeHandler = (val) => {
    this.setState({
      markdownTypeVal: val,
    });
  };
  marginValueHandler = (e) => {
    this.setState({
      marginVal: e.target.value,
    });
  };
  targetSellThrough = (e) => {
    this.setState({
      targetSellThroughVal: e.target.value,
    });
  };
  componentDidUpdate(prevProps, prevState) {
    if (this.props.cadenceData !== prevProps.cadenceData) {
      this.state.cadenceConstraintsVal === 'overall' &&
        this.setState({
          overallCadenceData: cloneDeep(this.props.cadenceData),
        });
      this.state.cadenceConstraintsVal === 'sku' &&
        this.setState({
          skuLabelCadenceData: cloneDeep(this.props.cadenceData),
        });
    }
    if (!_.isEqual(prevProps.inventoryInfoData, this.props.inventoryInfoData)) {
      let totalInv = this.props.inventoryInfoData.totalInv;
      let totalStores = this.props.inventoryInfoData.storeData.length;
      this.setState({
        totalInventory: totalInv,
        currentInventory: totalInv,
        selectedStores: totalStores,
        totalStores: totalStores,
      });
    }
    console.log(prevState.overallCadenceData, this.state.overallCadenceData);
    console.log(this.state.overallCadenceData === prevState.overallCadenceData);
  }
  onSend = () => {
    this.setState({ showPlanSavePopup: true });
  };
  onSave = (planName, sceniosNames) => {
    debugger;
    console.log('this.props.savedData:', this.props.savedData, planName, sceniosNames);
    this.props.sendApproval(planName);
  };
  render() {
    console.log('this.props.analysisTabledata:', this.props.analysisTable.analysisTabledata);
    console.log('skuData', this.props.anu);
    return (
      <LoadingOverlay
        loader={this.props.getOverlayLoaderState}
        spinner
        // text={this.props.getOverlayLoaderText}
      >
        <div className="createMd pl-2 pr-2">
          <div className="card mb-4">
            <div>
              <h4 className="font-size-14 markdown-heading">
                Markdown Optimization /
                <span className="font-bold color-blue">Manually Create MD</span>
              </h4>
            </div>
            <hr
              className="mb-2"
              style={{
                marginTop: '-4px',
              }}
            />
            <Filters hierarchy={this.state.filters} onFilter={() => this.onFilter()} drop />
            {/* <Filters filters={this.state.filters} onFilter={() => this.onFilter()} /> */}
            <hr className="dotted-line"></hr>
            <InventoryInfo
              onExcludeStores={this.onExcludeStores}
              totalInventory={this.state.totalInventory}
              currentInventory={this.state.currentInventory}
              totalStores={this.state.totalStores}
              selectedStores={this.state.selectedStores}
            />
            <hr className="dotted-line"></hr>
            <Constraints
              markdownTypeHandler={this.markdownTypeHandler}
              marginValueHandler={this.marginValueHandler}
              markdownTypeVal={this.state.markdownTypeVal}
              targetSellThrough={this.targetSellThrough}
              constraintsCadenceHandler={this.constraintsCadenceHandler}
              cadenceConstraintsVal={this.state.cadenceConstraintsVal}
            />
            <hr className="dotted-line"></hr>
            <div className="cadencebtn">
              <Button onClick={this.generateCadence}>Generate Cadence</Button>
            </div>
          </div>

          {this.state.showCadence &&
            (this.state.overallCadenceData.length || this.state.skuLabelCadenceData.length) && (
              <div className="card mb-4">
                <Tabs
                  activeKey={this.state.defaultActiveKey}
                  transition={false}
                  id="noanim-tab-example"
                  onSelect={this.onSelectTabs}>
                  {this.getTab()}
                </Tabs>
                <div className="addScenarioBtn">
                  <Button className="btn btn-secondary pull-right mt-2" onClick={this.addScenario}>
                    + Add Scenario
                  </Button>
                </div>
                <div className="simulatebtn">
                  {this.state.defaultActiveKey !== 'Optimal' && (
                    <button
                      className="btn btn-primary"
                      onClick={this.onSimulate}
                      disabled={this.state.defaultActiveKey !== 'Comparison' ? false : true}>
                      Simulate
                    </button>
                  )}
                  <button className="btn btn-primary ml-3 mr-3" onClick={this.onSend}>
                    Send for approval
                  </button>
                  <button className="btn btn-primary resetButton">Send to drafts</button>
                </div>
              </div>
            )}
          {this.state.cadenceConstraintsVal === 'overall' &&
            this.state.defaultActiveKey === 'Comparison' && (
              <div className="card mb-4">
                <div className="optimalheading pl-3 pt-2">{this.state.defaultActiveKey}</div>
                <hr />
                <div className="cadencetable">
                  <ComparisonMetricTable />
                </div>
              </div>
            )}

          {this.state.simulatedData &&
            this.props.markdownAnalysisGraphData &&
            this.props.markdownAnalysisGraphData.overview &&
            this.state.defaultActiveKey !== 'Comparison' && (
              <div className="card mb-4">
                <div className="optimalheading pl-3 pt-2">{this.state.defaultActiveKey}</div>
                <hr />
                {this.state.defaultActiveKey !== 'Comparison' && (
                  <div className="overviewtoggle">
                    <button
                      type="button"
                      className={classNames({
                        active: this.state.selectedView == 'Overview',
                        ' btn': true,
                        'btn-outline-primary cal-btn1': true,
                        btnCalendr: true,
                        calPromo: true,
                      })}
                      onClick={() => this.changedView('Overview')}
                      style={{
                        width: '90px',
                      }}>
                      Overview
                    </button>
                    <button
                      type="button"
                      className={classNames({
                        active: this.state.selectedView == 'Detailed',
                        ' btn': true,
                        'btn-outline-primary cal-btn1': true,
                        btnCalendr: true,
                        calPromo: true,
                      })}
                      onClick={() => this.changedView('Detailed')}
                      style={{
                        width: '90px',
                        borderColor: '#507a91',
                      }}>
                      Detailed
                    </button>
                  </div>
                )}
                {this.state.simulatedData &&
                  this.props.markdownAnalysisGraphData &&
                  this.props.markdownAnalysisGraphData.overview &&
                  this.state.selectedView === 'Overview' &&
                  this.state.defaultActiveKey !== 'Comparison' && (
                    <div>
                      <MarkdownAnalysis
                        analysisTable={this.props.analysisTable.analysisTabledata}
                        markdownAnalysisGraphData={this.props.markdownAnalysisGraphData.overview}
                        selectedSku={this.state.selectedSkuId}
                        optionSku={this.props.skuData}
                        value={this.props.selectedSkuId}
                        selectedSkuHandler={(val) => this.selectedSkuHandler(val)}
                      />
                    </div>
                  )}
                {this.state.selectedView === 'Detailed' &&
                  this.state.defaultActiveKey !== 'Comparison' && (
                    <div>
                      <ColumnSelection attributes={this.state.attributes} />
                      <div className="cadencetable" style={{ paddingBottom: '2rem' }}>
                        <MarkdownDetailedTable
                          data={this.props.markdownAnalysisGraphData.detailed}
                        />
                      </div>
                    </div>
                  )}{' '}
              </div>
            )}

          <Modal
            visible={this.state.showPlanSavePopup}
            effect="fadeInDown"
            onClickAway={() => this.showPlanSavePopup()}>
            <div className="card" style={{ width: '130%' }}>
              <div className="card-body">
                <h6 className="card-title">
                  Enter MD Plan Details
                  <span
                    onClick={() => this.showPlanSavePopup()}
                    className="pull-right select-store-span">
                    <i
                      className="fa fa-times"
                      onClick={() => this.showPlanSavePopup()}
                      aria-hidden="true"
                    />
                  </span>
                </h6>
                <hr />
              </div>

              <div className="ai-pre-grid-table" style={{ textAlign: 'right' }}>
                <PlanSavePopup
                  labels={this.state.TabTitles}
                  onSave={(planName, sceniosNames) => this.onSave(planName, sceniosNames)}
                />
              </div>
            </div>
          </Modal>
          <Modal
            visible={this.state.showExcludeStorePopup}
            effect="fadeInDown"
            onClickAway={() => this.showTablePopup()}>
            <div className="card">
              <div className="card-body">
                <h6 className="card-title">
                  Select stores
                  <span
                    onClick={() => this.showTablePopup()}
                    className="pull-right select-store-span">
                    <i
                      className="fa fa-times"
                      onClick={() => this.showTablePopup()}
                      aria-hidden="true"
                    />
                  </span>
                </h6>
                <hr />
              </div>
              {this.props.inventoryInfoData && this.props.inventoryInfoData.length && (
                <div className="ai-pre-grid-table">
                  <ExcludeStoreTable
                    columnHeaders={this.props.inventoryInfoData.columnHeaders || []}
                    excludeStoreValHandler={this.excludeStoreValHandler}
                    storeData={this.props.inventoryInfoData.storeData || []}
                  />
                </div>
              )}
            </div>
          </Modal>
        </div>
      </LoadingOverlay>
    );
  }
}
