import React, { Component } from 'react';
import { connect } from 'react-redux';
import _ from 'underscore';
import DataTable from '../../services/reactTable';
import './index.css';
import SkuColDef from '../../../src/components/SKUCockpit/skuColdef.js';
import styled from 'styled-components';
import ColumnSelection from '../../components/columnSelection';

export class SKUCocktpit extends Component {
  constructor(props) {
    super(props);
    this.state = {
      checkedSku: [],
      tableData: this.props.cockpitData.length ? this.props.cockpitData : [],
    };
  }

  updateMyData = (rowIndex, columnId, value) => {
    // We also turn on the flag to not reset the page
    this.setState((old) => {
      let val = old.tableData.map((row, index) => {
        if (index === rowIndex) {
          return {
            ...row,
            [columnId]: value,
          };
        }
        return row;
      });
      return { tableData: val };
    });
  };

  onRowClick = (rowInfo) => {
    let selectedRow = [];
    if (Object.keys(rowInfo).length) {
      Object.keys(rowInfo).forEach((row) => {
        selectedRow.push(this.props.cockpitData[row].style_number);
      });
      this.setState({
        checkedSku: selectedRow,
      });
    } else {
      this.setState({
        checkedSku: [],
      });
    }
  };
  render() {
    return (
      <div className="promo">
        <div className="card" id="">
          <div className="" style={{ padding: '10px 9px' }}>
            <div className="promolist-header pull-left">
              <label className="pipelineLabel">Style-Color level details</label>
            </div>
            <button
              disabled={
                this.state.checkedSku.length === 0 || this.props.dataLoadStatus ? true : false
              }
              type="button"
              className="btn btn-primary pull-right"
              onClick={() => this.props.onCreateMarkdown(this.state.checkedSku)}>
              Create Markdown
            </button>
          </div>

          <div style={{ padding: '1rem' }}>
            <ColSelBtn>
              {this.props.columnHeaders && Object.keys(this.props.columnHeaders).length && (
                <ColumnSelection
                  onHandleCheckBox={this.props.onHandleCheckBox}
                  columnHeaders={this.props.columnHeaders}
                  onHandleSelect={this.props.onHandleSelect}
                  columnOptions={this.props.columnOptions}
                  selectedItems={this.props.selectedItems}
                  selLabels={this.props.selLabels}
                />
              )}
            </ColSelBtn>
            {this.props.cockpitData && this.props.cockpitData.length > 0 && (
              <DataTable
                columns={SkuColDef()}
                showExport={true}
                data={this.props.cockpitData}
                updateMyData={this.updateMyData}
                visibleColumns="onlyRowSelection"
                isPagination={true}
                selectRow={this.onRowClick}
              />
            )}
          </div>
        </div>
      </div>
    );
  }
}

const mapStateToProps = (state) => ({
  dataLoadStatus: state.dataLoadReducer.dataLoadStatus,
});

export default connect(mapStateToProps, {})(SKUCocktpit);
const ColSelBtn = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: space-between;
`;
