import React, { Component } from 'react';
import { withRouter } from 'react-router-dom';
import { connect } from 'react-redux';
import StyleConfigFilter from '../StyleConfigFilter/StyleConfigFilter';
import CreateStyleGroup from './CreateStyleGroup';
import CreateStyle from './CreateStyle';
import DataTable from '../../../services/reactTable';
import _ from 'lodash';
import filterActions from '../../../actions/filtersAction';
import NoData from '../../noTableData';
import withClearCache from '../../../ClearCache';

import './StyleConfig.css';

const Tabledata1 = [
  {
    id: '205-1-2-1-1C300WMBHGN3',
    name: 'BH GOLD 300 WIN 180GR ACCUBOND',
  },
  {
    id: '205-1-2-1-1C308BHGN6',
    name: 'BH GOLD 308 WIN 150 GR SST 20R',
  },
];
export class Config extends Component {
  state = {
    styleTable: 'styleGroup',
    showEDLP: true,
    styleEdit: false,
    header: [
      {
        // Build our expander column
        id: 'expander', // Make sure it has an ID
        // Header: ({ getToggleAllRowsExpandedProps, isAllRowsExpanded }) => (
        //   <span {...getToggleAllRowsExpandedProps()}>{isAllRowsExpanded ? 'ðŸ‘‡' : 'ðŸ‘‰'}</span>
        // ),
        Header: 'Vendor-Style Color Group',
        Cell: ({ row }) => {
          // Use the row.canExpand and row.getToggleRowExpandedProps prop getter
          // to build the toggle for expanding a row
          return row.canExpand ? (
            <span
              {...row.getToggleRowExpandedProps({
                style: {
                  // We can even use the row.depth property
                  // and paddingLeft to indicate the depth
                  // of the row
                  paddingLeft: `${row.depth * 2}rem`,
                },
              })}>
              {row.original.style_group}&nbsp;&nbsp;&nbsp;&nbsp;
              {row.isExpanded ? (
                <i class="fa fa-angle-down expanded-icon-size color-blue" aria-hidden="true"></i>
              ) : (
                <i class="fa fa-angle-right expanded-icon-size color-blue" aria-hidden="true"></i>
              )}
            </span>
          ) : (
            <span>{row.original.style_group}</span>
          );
        },
      },
      // {
      //   Header: 'Style Color Group',
      //   accessor: 'style_group',
      // },
      {
        Header: 'Style Color',
        accessor: 'id',
        Aggregated: ({ row }) => {
          return <span>Style Color({row.subRows.length})</span>;
        },
      },
      // {
      //   Header: 'Style Description',
      //   accessor: 'name',
      // },
      {
        Header: 'Edit Style',
        Cell: ({ row }) => {
          return row.original && row.original.style_group ? (
            <i
              className="fa fa-pencil color-blue"
              onClick={async () => {
                await this.props.setRetainFilters(true);
                this.setState({ styleEdit: true, tableData: [], editedStyleGrp: [row.original] });
              }}></i>
          ) : null;
        },
      },
    ],
    styleHeader: [
      {
        Header: 'Style ID',
        accessor: 'id',
      },
      {
        Header: 'Style Description',
        accessor: 'name',
      },
      {
        Header: 'Edit Style',
        Cell: ({ row }) => {
          return (
            <i
              className="fa fa-pencil color-blue"
              onClick={() => {
                this.setState({ styleEdit: true });
              }}></i>
          );
        },
      },
    ],
    tableData: [],
  };

  styleEditHandler = () => this.setState({ styleEdit: false });

  renderTable = (styleName, data) => {
    if (styleName === 'style') {
      return <DataTable columns={this.state.styleHeader} data={Tabledata1} />;
    }
    if (styleName === 'styleGroup') {
      return <DataTable columns={this.state.header} data={this.state.tableData} />;
    }
    return null;
  };
  onFilterHandler = () => this.setState({ tableData: this.props.styleConfigTableData });
  onStyleTypeChangeHandler = (value, type) => {
    this.setState({
      styleTable: type,
    });
  };
  render() {
    let { styleTable, styleEdit, tableData, showEDLP } = this.state;
    return (
      <>
        {!styleEdit ? (
          <div className="card p-2">
            <div className="mt-1" style={{ display: 'flex', justifyContent: 'space-between' }}>
              <h4 className="font-size-14  pad-10 markdown-heading">Vendor-style color Configuration</h4>
              <div>
                <button
                  className="btn btn-primary"
                  onClick={() => {
                    this.props.history.push(`/vis/Markdown/configuration/style/group`);
                  }}>
                  Create Vendor-Style Color Group
                </button>
                {/* <button
                  className="btn btn-primary ml-1"
                  onClick={() => {
                    this.props.history.push(`/vis/Markdown/configuration/style/create`);
                  }}>
                  Map Style
                </button> */}
              </div>
            </div>
            <hr
              className="mb-2 mt-1"
              style={{
                marginTop: '-4px',
              }}
            />
            <StyleConfigFilter
              from="styleConfig"
              styleType={styleTable}
              onStyleTypeChangeHandler={this.onStyleTypeChangeHandler}
              showEDLP={showEDLP}
              onFilterHandler={this.onFilterHandler}
            />
            <hr />
            <div style={{ width: '64%', margin: '0 auto' }}>
              {tableData.length ? (
                this.renderTable(styleTable)
              ) : (
                <NoData description="Please select filters to show Vendor-Style Color Group Table." />
              )}
            </div>
          </div>
        ) : styleEdit && styleTable === 'styleGroup' ? (
          <CreateStyleGroup
            styleGroupEdit={true}
            editedStyleGrp={this.state.editedStyleGrp}
            styleGroupEditHandler={this.styleEditHandler}
          />
        ) : (
          <CreateStyle styleEdit={true} styleEditHandler={this.styleEditHandler} />
        )}
      </>
    );
  }
}
const mapStateToProps = (store) => {
  return {
    styleConfigTableData: store.styleReducer.styleConfigTableData,
  };
};
const mapDispatchToProps = {
  setRetainFilters: filterActions.setRetainFilters,
};
export default connect(mapStateToProps, mapDispatchToProps)(withRouter(withClearCache(Config)));
