import React, { Component } from 'react';
import { connect } from 'react-redux';
import DraftAction from '../../../actions/draftStausFinalizeAction';
import Filters from '../../../services/filters';
import DataTable from '../../../services/reactTable';
import ColumnSelection from '../../columnSelection';
import Coldef from './coldef';
import styled from 'styled-components';
import _ from 'lodash';
import cloneDeep from 'clone-deep';
import RowData from './data';
import moment from 'moment';
import { toast } from 'react-toastify';
import '../draftStatus.css';
import NoData from '../../noTableData';
import IndeterminateCheckbox from '../../../components/cellRenders/inderterminateCheckbox';
import { disabled } from 'glamor';

class Draft extends Component {
  constructor(props) {
    super(props);
    this.state = {
      // tableData: RowData,
      columnOptions: [],
      selLabels: [],
      columns: [],
      selectedRows: [],
    };
  }

  findStatusColor = (status, color) => {
    switch (status) {
      case 'high':
        return color.high;
      case 'medium':
        return color.medium;
      case 'low':
        return color.low;
      default:
        break;
    }
  };

  bgmCol = (cellInfo) => {
    if (cellInfo.column.id === 'md_status') var status = cellInfo.row.original.md_status;
    let bgCol = {
      high: '#F8E8E8',
      medium: '#FCF3D1',
      low: '#E8F8F5',
    };
    return this.findStatusColor(status, bgCol);
  };
  txtCol = (cellInfo) => {
    if (cellInfo.column.id === 'md_status') var status = cellInfo.row.original.md_status;
    let txtCol = {
      high: '#D38485',
      medium: '#F0C53A',
      low: '#66CFBA',
    };
    return this.findStatusColor(status, txtCol);
  };
  sendForApproval = () => {
    let msg = [];
    let payload = {
      markdown_data: this.state.selectedRows.map((item, i) => {
        let arrLength = this.state.selectedRows.length;
        let msgStr = `Scenario '${item.scenario}' of Plan '${item.md_plan}'${
          i === arrLength - 2 ? ' and ' : i === arrLength - 1 ? '' : ', '
        }`;
        msg.push(msgStr);
        return {
          id: item.markdown_id,
          scenario: item.scenario,
        };
      }),
      update_status: 'pending',
    };
    this.props
      .sendForApproval(payload)
      .then((resp) => {
        if (resp && resp.data.status == true) {
          let successMsg = `${msg.join('')} has been sent for Approval successfully`;
          toast.success(successMsg, {
            autoClose: 15000,
            hideProgressBar: true,
          });
          let payload = {
            filters: {
              hierarchy1: this.props.selectedHierarchy1,
              hierarchy2: this.props.selectedHierarchy2,
              hierarchy3: this.props.selectedHierarchy3,
              hierarchy4: this.props.selectedHierarchy4,
              hierarchy5: this.props.selectedHierarchy5,
              season: this.props.selectedSeason,
              clearance: this.props.selectedClearance,
              matrix: this.props.selectedMatrix,
              exclusionList: this.props.selectedExclusionList,
              styleColor: this.props.selectedStyleColor,
              country: this.props.selectedCountry,
              storeCluster: this.props.selectedStoreCluster,
              store: this.props.selectedStore,
              channel: this.props.selectedChannel,
              customStyleGroup: this.props.selectedCustomStyleGroup,
              customStoreGroup: this.props.selectedCustomStoreGroup,
              tiers: this.props.selectedStores,
            },
            startDate: this.props.startDate
              ? moment(this.props.startDate).format('YYYY-MM-DD')
              : null,
            endDate: this.props.endDate ? moment(this.props.endDate).format('YYYY-MM-DD') : null,
            status: 'draft',
          };
          this.props.getMarkdowns(payload);
          this.setState({
            selectedRows: [],
          });
        }
      })
      .catch((err) => {
        toast.error(err.message, {
          autoClose: 15000,
          hideProgressBar: true,
        });
      });
  };
  updateMyData = (rowIndex, columnId, value) => {
    // We also turn on the flag to not reset the page
    this.setState((old) => {
      let val = old.tableData.map((row, index) => {
        if (index === rowIndex) {
          return {
            ...row,
            [columnId]: value,
          };
        }
        return row;
      });
      return { tableData: val };
    });
  };
  ////////// handling reset issues rising bcs of tabs start///////////
  mandatoryFiltersVal = ({ filters, startDate, endDate }) => {
    return !_.isEmpty(filters.hierarchy1) && !_.isEmpty(filters.hierarchy2) && startDate && endDate;
  };
  onFilter = ({ filters, startDate, endDate }) => {
    if (!this.mandatoryFiltersVal({ filters, startDate, endDate })) {
      toast.error(`Please select mandatory filters`, {
        autoClose: 15000,
        hideProgressBar: true,
      });
      return;
    }
    let payload = {
      filters: {
        hierarchy1: filters.hierarchy1,
        hierarchy2: filters.hierarchy2,
        hierarchy3: filters.hierarchy3,
        hierarchy4: filters.hierarchy4,
        hierarchy5: filters.hierarchy5,
        hierarchy6: filters.hierarchy6,
        hierarchy7: filters.hierarchy7,
        hierarchy8: filters.hierarchy8,
        customStyleGroup: this.props.selectedCustomStyleGroup,
        customStoreGroup: this.props.selectedCustomStoreGroup,
        tiers: filters.stores,
      },
      startDate: startDate ? moment(startDate).format('YYYY-MM-DD') : null,
      endDate: endDate ? moment(endDate).format('YYYY-MM-DD') : null,
      status: 'draft',
    };
    this.props.getMarkdowns(payload);
  };
  ////////// handling reset issues rising bcs of tabs end///////////

  componentDidUpdate() {
    if (this.props.scenarioSelected.value) {
      this.props.scenarioSelectedHandler(false, []);
      this.onScenarioSelection(this.props.scenarioSelected.row);
    }
  }
  onScenarioSelection = (rowData) => {
    let { selectedRows } = this.state;
    let foundEle = _.find(selectedRows, {
      markdown_id: rowData.original.markdown_id,
    });
    if (foundEle) {
      _.remove(selectedRows, (item) => item.markdown_id === rowData.original.markdown_id);
    } else {
      selectedRows.push(rowData.original);
    }
    this.setState({
      selectedRows,
    });
  };

  render() {
    return (
      <div className="createMd pl-2 pr-2">
        <div className="card">
          <div>
            <h4 className="font-size-14  pad-10 markdown-heading">
              MarkSmart /<span className="font-bold color-blue">Draft</span>
            </h4>
          </div>
          <hr
            className="mb-2"
            style={{
              marginTop: '-4px',
            }}
          />
          <div className="filterCol mb-4">
            <Filters
              customStyleGroups
              customStoreGroup
              onFilter={this.onFilter}
              onReset={(e) => {
                this.props.textStrHandler(true);
                this.props.onResetDraftTable(e);
              }}
              stores
              dateRange
              hierarchy={[
                {
                  type: 'hierarchy1',
                  isMandatory: true,
                  isMultiple: false,
                },
                {
                  type: 'hierarchy2',
                  isMultiple: true,
                  isMandatory: true,
                },
                {
                  type: 'hierarchy3',
                  isMultiple: true,
                },
                {
                  type: 'hierarchy4',
                  isMultiple: true,
                },
                {
                  type: 'hierarchy5',
                  isMultiple: true,
                },
                {
                  type: 'hierarchy6',
                  isMultiple: true,
                },
                {
                  type: 'hierarchy7',
                  isMultiple: true,
                },
                {
                  type: 'hierarchy8',
                  isMultiple: true,
                },
              ]}
              storeIsNotMandatory={true}
              from="draft/status" ////////// handling reset issues rising bcs of tabs
            />
          </div>
        </div>
        {this.props.draftMarkdowns && this.props.draftMarkdowns.length ? (
          <div className="card pl-3 pr-3 mt-3">
            <ColSelBtn>
              {this.props.draftColumnHeaders &&
                Object.keys(this.props.draftColumnHeaders).length && (
                  <ColumnSelection
                    onHandleCheckBox={this.props.onHandleCheckBox}
                    columnHeaders={this.props.draftColumnHeaders}
                    onHandleSelect={this.props.onHandleSelect}
                    columnOptions={this.props.columnOptions}
                    selectedItems={this.props.selectedItems}
                    selLabels={this.props.selLabels}
                  />
                )}
              <div style={{ padding: '1rem' }}>
                <button
                  onClick={this.sendForApproval}
                  disabled={!this.state.selectedRows.length}
                  className="btn btn-primary">
                  Send for Approval
                </button>
                &nbsp;
              </div>
            </ColSelBtn>
            <div className="tablel">
              <DataTable
                columns={this.props.columns}
                data={this.props.draftMarkdowns}
                isSort={true}
                isExpanded={true}
                updateMyData={this.updateMyData}
                isPagination={true}
                NoDataComponent="This is the dynamic title"
                getCellProps={(cellInfo) => ({
                  // onClick: () => this.onCellClick(cellInfo),
                  style: {
                    backgroundColor: this.bgmCol(cellInfo),
                    color: this.txtCol(cellInfo),
                    margin: '4px 4px',
                    borderRadius: '2px',
                  },
                })}
              />
            </div>
          </div>
        ) : (
          <NoData description={this.props.draftTextStr} />
        )}
      </div>
    );
  }
}
