import React, { Component } from 'react';
import '../StoreConfigContainers/StoreConfig';
import { connect } from 'react-redux';
import DataTable from '../../../services/reactTable';
import '../../Configuration/StoreConfigContainers/StoreConfig.css';
import { cloneDeep, differenceBy } from 'lodash';
import excelIcon from '../../../images/leftPanel/status.svg';
import StoreConfigActions from '../../../actions/storeConfigActions';
import _ from 'lodash';

export class CreateStoreGroup extends Component {
  state = {
    storeGroupName: '',
    allStores: [],
    currentStores: [],
    selectedAllStores: [],
    selectedCurrentStores: [],
    fromEdit: false,
    storeGroupId: null,
  };
  header = [
    {
      Header: 'Store Number',
      accessor: 'store_code',
    },
    {
      Header: 'Store Name',
      accessor: 'store_name',
    },
    {
      Header: 'Chain Description',
      accessor: 'chain_description',
    },
    {
      Header: 'Region Description',
      accessor: 'region_description',
    },
  ];

  async componentDidMount() {
    this.props.fetchStoreGroups();
    let response = await this.props.getStoreGroupTableData();
    let { match } = this.props;
    if (match && match.params && match.params.id) {
      let { id } = match.params;
      let res = await this.props.getStoreGroupTableData({ Stores_groups_Id: [id] });
      this.setState({ fromEdit: true, storeGroupId: id, currentStores: res });
    }
    this.setState({ allStores: response });
  }
  componentDidUpdate(prevProps, prevState) {
    if (this.state.fromEdit && !this.state.storeGroupName && this.props.storeGroups) {
      let groupName = _.find(this.props.storeGroups, { value: parseInt(this.state.storeGroupId) });
      console.log(this.props.storeGroups, this.state.storeGroupId, 'Verlotppppppp', groupName);
      if (groupName) {
        this.setState({ storeGroupName: groupName.label });
      }
    }
  }

  onStoreGroupNameChangeHandler = (e) => {
    this.setState({
      storeGroupName: e.target.value,
    });
  };

  onCurrentStoresSelectionChangeHandler = (rowInfo) => {
    let { selectedCurrentStores } = this.state;
    let foundEle = _.find(selectedCurrentStores, {
      store_code: rowInfo.original.store_code,
    });
    console.log(rowInfo, 'rowinfooo');
    if (foundEle) {
      _.remove(selectedCurrentStores, (item) => item.store_code === rowInfo.original.store_code);
    } else {
      selectedCurrentStores.push(rowInfo.original);
    }
    this.setState({
      selectedCurrentStores,
    });
  };

  onAddClickHandler = () => {
    const allStores = cloneDeep(this.state.allStores);
    const currentStores = cloneDeep(this.state.currentStores);
    let newcurrentStores = _.unionWith(currentStores, this.state.selectedAllStores, _.isEqual);

    let mainArray = allStores,
      childArray = this.state.selectedAllStores;

    if (allStores.length < this.state.selectedAllStores.length) {
      mainArray = this.state.selectedAllStores;
      childArray = allStores;
    }
    let newAllStores = _.differenceBy(mainArray, childArray, 'store_code');
    this.setState({
      allStores: newAllStores,
      currentStores: newcurrentStores,
      selectedAllStores: [],
    });
  };
  onAllStoresSelectionChangeHandler = (rowInfo) => {
    let { selectedAllStores } = this.state;
    let foundEle = _.find(selectedAllStores, { store_code: rowInfo.original.store_code });
    if (foundEle) {
      _.remove(selectedAllStores, (item) => item.store_code === rowInfo.original.store_code);
    } else {
      selectedAllStores.push(rowInfo.original);
    }

    this.setState({
      selectedAllStores,
    });
  };
  onCreateStoreGroup = () => {
    if (this.state.fromEdit) {
      let { storeGroupId, storeGroupName, currentStores } = this.state;
      let storeCodes = [];
      currentStores.map((ele) => storeCodes.push(ele.store_code));
      this.props.editStoreGroup({
        Stores_groups_NAME: storeGroupName,
        Stores_Codes: storeCodes,
        Stores_groups_Id: storeGroupId,
      });
    } else {
      let storeCodes = this.state.currentStores.map((ele) => ele.store_code);
      let { storeGroupName } = this.state;
      this.props.createStoreGroup({
        Stores_groups_NAME: storeGroupName,
        Stores_Codes: storeCodes,
      });
    }
  };
  onCancelButtonHandler = () => {
    this.setState({ allStores: [] });
    this.props.history.goBack();
  };
  onRemoveClickHandler = () => {
    const currentStores = cloneDeep(this.state.currentStores);
    const allStores = cloneDeep(this.state.allStores);
    let mainArray = currentStores,
      childArray = this.state.selectedCurrentStores;

    if (currentStores.length < this.state.selectedCurrentStores.length) {
      mainArray = this.state.selectedCurrentStores;
      childArray = currentStores;
    }
    const newCurrentStStores = differenceBy(mainArray, childArray, 'store_code');
    let newallStores = _.unionWith(allStores, this.state.selectedCurrentStores, _.isEqual);

    this.setState({
      allStores: newallStores,
      currentStores: newCurrentStStores,
      selectedCurrentStores: [],
    });
  };

  render() {
    let { storeGroupName, currentStores, allStores } = this.state;
    return (
      <div className="card p-2">
        <div style={{ display: 'flex', justifyContent: 'space-between' }}>
          <h4 className="font-size-14  pad-10 markdown-heading">
            Store Configuration/
            <span className="font-bold color-blue">
              {this.state.fromEdit ? 'Edit Store Group' : 'Create Store Group'}
            </span>
          </h4>
          <div>
            <button className="btn btn-primary" onClick={this.onCancelButtonHandler}>
              Back
            </button>
          </div>
        </div>
        <hr
          className="mb-3"
          style={{
            marginTop: '-4px',
          }}
        />
        <div className="row justify-content-between">
          <div className="col-md-6">
            <label className="mr-1">
              <span style={{ color: 'red' }}>*</span>Store group name:
            </label>
            {this.state.fromEdit ? (
              <input
                style={{ background: '#dddddd', border: '1px solid #CCC' }}
                className="inputText"
                disabled
                type="text"
                value={storeGroupName}
              />
            ) : (
              // <span className="store-name">{storeGroupName}</span>
              <input
                className="inputText"
                type="text"
                onChange={this.onStoreGroupNameChangeHandler}
              />
            )}
          </div>
          <div className="col-md-2">
            {/* <button className="col-md-5 btn btn-primary" onClick={() => {}}>
              Upload &nbsp;&nbsp;
              <span>
                <img className="img-icons-create-promo" src={excelIcon} alt="" />
              </span>
            </button> */}
          </div>
        </div>
        <hr />
        <div className="row mx-2 create-store-grp-table">
          <div className="col-md-6">
            <div>All Stores</div>
            <div style={{ height: '439px' }}>
              <DataTable
                visibleColumns="onlyRowSelection"
                getRowProps={(rowInfo) => ({
                  onClick: () => this.onAllStoresSelectionChangeHandler(rowInfo),
                })}
                columns={this.header}
                data={allStores}
                // isPagination={true}
              />
            </div>
            <div>
              <button className="btnStyle btn btn-primary" onClick={this.onAddClickHandler}>
                Add
              </button>
            </div>
          </div>
          <div className="col-md-6">
            <div>
              <span className="mr-2">Current Stores : {currentStores && currentStores.length}</span>
            </div>
            <div style={{ height: '414px' }}>
              <DataTable
                visibleColumns="onlyRowSelection"
                getRowProps={(rowInfo) => ({
                  onClick: () => this.onCurrentStoresSelectionChangeHandler(rowInfo),
                })}
                columns={this.header}
                data={currentStores}
                isPagination={true}
              />
            </div>
            <div className="mt-4">
              <button className="btnStyle btn btn-primary" onClick={this.onRemoveClickHandler}>
                Remove
              </button>
            </div>
          </div>
        </div>
        <div className="mb-3 mt-4" style={{ textAlign: 'center' }}>
          <button
            style={{ width: '7%' }}
            className=" btn btn-primary"
            onClick={this.onCreateStoreGroup}>
            {this.state.fromEdit ? 'Update' : 'Create'}
          </button>
          <button
            className=" btn ml-1 cancel-button"
            style={{ width: '7%' }}
            onClick={this.onCancelButtonHandler}>
            Cancel
          </button>
        </div>
