import React from 'react';
import Modal from 'react-awesome-modal';
import Select from 'react-select';
import ManuallyCreateMD from '../ManuallyCreateMD';

const EditMDModal = (props) => {
  return (
    <div className="constraints-modal">
      <Modal
        visible={true}
        effect="fadeInDown"
        width="75%"
        height="100%"
        onClickAway={() => props.showEditModalHandler(false)}>
        <div className="card-body pb-0 px-0">
          <div className="card-title mx-1 font-weight-bold row" style={{ fontSize: '1rem' }}>
            <div className="col-4">Edit Markdown</div>
            <div className="col-4 text-center">
              Plan ID:{' '}
              {props.from === 'actualVsPlanned'
                ? props.selectedPlanDetails.plan
                : props.selectedPlanDetails.md_plan}
            </div>
            <div className="col-4 text-right">
              <span onClick={() => props.showEditModalHandler(false)}>
                <i
                  style={{ cursor: 'pointer' }}
                  className="fa fa-times"
                  onClick={() => props.showEditModalHandler(false)}
                  aria-hidden="true"
                />
              </span>
            </div>
          </div>
          <hr />

          <div className="edit-md-modal">
            <ManuallyCreateMD
              from={props.from}
              editModal={true}
              planId={
                props.from === 'actualVsPlanned'
                  ? props.selectedPlanDetails.promo_id
                  : props.selectedPlanDetails.markdown_id
              }
            />
          </div>

          <div className="constraints-card-footer">
            <hr />
            <div className="text-center mt-4">
              <button
                className="btn btn-primary mr-3"
                onClick={() => props.showEditModalHandler(false)}>
                Cancel
              </button>
              <button className="btn btn-primary" onClick={() => props.showEditModalHandler(false)}>
                Save Changes
              </button>
            </div>
          </div>
        </div>
      </Modal>
    </div>
  );
};

export default EditMDModal;
