import axios from '../utils/axios';
import { config } from '../utils/config';
import ReactGA from 'react-ga';

const reportingActions = {
  onReset: () => {
    return (dispatch) => {
      dispatch({
        type: 'RESET_REPORTING_DATA',
      });
    };
  },
  getReportingData: (req) => {
    return (dispatch) => {
      dispatch({
        type: 'LOADER_STATE',
        payload: {
          status: true,
        },
      });
      axios({
        url: `${config.createMarkdownURL}/getReporting/`,
        method: 'POST',
        data: req,
      })
        .then((resp) => {
          if (req.reportingType === 'week_level_aggregated_data') {
            dispatch({
              type: 'SET_REPORTING_DATA',
              payload:
                resp.data.data &&
                (Object.keys(resp.data.data.sell_through).length ||
                  Object.keys(resp.data.data.margin).length ||
                  Object.keys(resp.data.data.inventory).length ||
                  resp.data.data.sales.ty ||
                  resp.data.data.sales.ly)
                  ? resp.data.data
                  : { message: 'No data found. Please try with different filters.' },
            });
            dispatch({
              type: 'GET_SKU_COCKPIT',
              payload: [],
            });
            ReactGA.event({
              category: 'Reporting',
              action: 'Generated report'
            });
          } else {
            dispatch({
              type: 'GET_SKU_COCKPIT',
              payload: resp.data.data.length
                ? resp.data.data
                : { message: 'No data found. Please try with different filters.' },
            });
            dispatch({
              type: 'GET_COLUMNHEADERS',
            });
            ReactGA.event({
              category: 'Reporting',
              action: 'Viewed Style-Color level data'
            });
          }
          dispatch({
            type: 'LOADER_STATE',
            payload: {
              status: false,
            },
          });
        })
        .catch((err) => {
          dispatch({
            type: 'LOADER_STATE',
            payload: {
              status: false,
            },
          });
        });
    };
  },
};

export default reportingActions;
