      boxWidth: 12,
    },
    fontFamily: 'Helvetica',
    fontSize: '12px',
  },
  maintainAspectRatio: false,
  scales: {
    yAxes: [
      {
        gridLines: {
          display: true,
        },
        ticks: {
          fontSize: 13,
        },
        scaleLabel: {
          show: true,
          display: true,
          fontSize: 14,
          fontStyle: 'bold',
          fontColor: '#000',
          labelString: 'Margin %',
        },
      },
    ],
    xAxes: [
      {
        id: 'x-axis-0',
        gridLines: {
          // color: '#aeaeae5e',
          display: false,
        },
        ticks: {
          autoSkip: true,
          autoSkipPadding: 40,
          maxRotation: 0,
        },
        scaleLabel: {
          display: true,
          // labelString: "Time (week)",
          fontWeight: 900,
          fontStyle: 'bold',
        },
      },
    ],
  },
};
const options = {
  plugins: {
    datalabels: {
      display: false,
    },
  },
  legend: {
    display: false,
