          fontSize: 14,
          fontStyle: 'bold',
          fontColor: '#000',
          labelString: 'Sell Through %',
        },
      },
    ],
    xAxes: [
      {
        id: 'x-axis-0',
        gridLines: {
          // color: '#aeaeae5e',
          display: false,
        },
        ticks: {
          autoSkip: true,
          autoSkipPadding: 40,
          maxRotation: 0,
        },
        scaleLabel: {
          display: true,
          // labelString: "Time (week)",
          fontWeight: 900,
          fontStyle: 'bold',
        },
      },
    ],
  },
};
class MarkdownAnalysis extends Component {
  constructor(props) {
    super(props);
    this.state = {};
  }
  render() {
    const borderColors = [
      '#44677B',
      '#6F9FC3',
      '#83BE8C',
      '#88ad5f',
      '#b06d58',
      '#86588a',
      '#17a69c',
      '#b0a819',
      '#d97c30',
      '#5d5b85',
    ];
    const borderColors1 = [
      '#44677B',
      '#6F9FC3',
      '#83BE8C',
      '#88ad5f',
      '#b06d58',
      '#86588a',
      '#17a69c',
      '#b0a819',
      '#d97c30',
      '#5d5b85',
    ];
    const gradient = [
      'repeating-linear-gradient(-44deg, #44677B 5%,white 9px)',
